/**
 * ç¤ºä¾‹APIè·¯ç”± - å±•ç¤ºæ ‡å‡†åŒ–é”™è¯¯å¤„ç†
 */

import { NextRequest } from 'next/server';
import {
  createSuccessResponse,
  createValidationErrorResponse,
  createNotFoundErrorResponse,
  createBusinessErrorResponse,
  withErrorHandling,
  validateRequestBody,
  parsePaginationParams
} from '@/lib/api/responses';
import { CustomError } from '@/lib/errors/CustomError';
import { ErrorCode } from '@/lib/errors/types';
import { logger } from '@/lib/logging/logger';

/**
 * ç¤ºä¾‹æ•°æ®æ¥å£
 */
interface ExampleData {
  id: string;
  name: string;
  email: string;
  createdAt: string;
}

/**
 * åˆ›å»ºæ•°æ®è¯·æ±‚æ¥å£
 */
interface CreateExampleRequest {
  name: string;
  email: string;
}

/**
 * æ›´æ–°æ•°æ®è¯·æ±‚æ¥å£
 */
interface UpdateExampleRequest {
  name?: string;
  email?: string;
}

// æ¨¡æ‹Ÿæ•°æ®å­˜å‚¨
const mockData: ExampleData[] = [
  {
    id: '1',
    name: 'John Doe',
    email: 'john@example.com',
    createdAt: '2024-01-01T00:00:00Z'
  },
  {
    id: '2',
    name: 'Jane Smith',
    email: 'jane@example.com',
    createdAt: '2024-01-02T00:00:00Z'
  }
];

/**
 * éªŒè¯åˆ›å»ºè¯·æ±‚
 */
function validateCreateRequest(data: any): CreateExampleRequest {
  if (!data.name || typeof data.name !== 'string') {
    throw new CustomError('å§“åæ˜¯å¿…å¡«é¡¹ä¸”å¿…é¡»æ˜¯å­—ç¬¦ä¸²', ErrorCode.VALIDATION_ERROR);
  }

  if (!data.email || typeof data.email !== 'string') {
    throw new CustomError('é‚®ç®±æ˜¯å¿…å¡«é¡¹ä¸”å¿…é¡»æ˜¯å­—ç¬¦ä¸²', ErrorCode.VALIDATION_ERROR);
  }

  if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email)) {
    throw new CustomError('é‚®ç®±æ ¼å¼ä¸æ­£ç¡®', ErrorCode.VALIDATION_ERROR);
  }

  // æ£€æŸ¥é‚®ç®±æ˜¯å¦å·²å­˜åœ¨
  if (mockData.some(item => item.email === data.email)) {
    throw new CustomError('é‚®ç®±å·²å­˜åœ¨', ErrorCode.BUSINESS_LOGIC_ERROR);
  }

  return {
    name: data.name.trim(),
    email: data.email.toLowerCase().trim()
  };
}

/**
 * éªŒè¯æ›´æ–°è¯·æ±‚
 */
function validateUpdateRequest(data: any): UpdateExampleRequest {
  const result: UpdateExampleRequest = {};

  if (data.name !== undefined) {
    if (typeof data.name !== 'string') {
      throw new CustomError('å§“åå¿…é¡»æ˜¯å­—ç¬¦ä¸²', ErrorCode.VALIDATION_ERROR);
    }
    result.name = data.name.trim();
  }

  if (data.email !== undefined) {
    if (typeof data.email !== 'string') {
      throw new CustomError('é‚®ç®±å¿…é¡»æ˜¯å­—ç¬¦ä¸²', ErrorCode.VALIDATION_ERROR);
    }

    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(data.email)) {
      throw new CustomError('é‚®ç®±æ ¼å¼ä¸æ­£ç¡®', ErrorCode.VALIDATION_ERROR);
    }

    result.email = data.email.toLowerCase().trim();
  }

  return result;
}

/**
 * GET /api/example - è·å–æ•°æ®åˆ—è¡¨
 */
export const GET = withErrorHandling(async (request: NextRequest) => {
  const { searchParams } = new URL(request.url);
  const { page, limit, offset } = parsePaginationParams(searchParams);

  // æ¨¡æ‹ŸæŸ¥è¯¢å‚æ•°
  const search = searchParams.get('search');
  const sortBy = searchParams.get('sortBy') || 'createdAt';
  const sortOrder = searchParams.get('sortOrder') || 'desc';

  logger.info('GET /api/example', {
    metadata: {
      page,
      limit,
      search,
      sortBy,
      sortOrder
    }
  });

  // æ¨¡æ‹Ÿæ•°æ®è¿‡æ»¤å’Œæ’åº
  let filteredData = [...mockData];

  if (search) {
    filteredData = filteredData.filter(item =>
      item.name.toLowerCase().includes(search.toLowerCase()) ||
      item.email.toLowerCase().includes(search.toLowerCase())
    );
  }

  // æ¨¡æ‹Ÿæ’åº
  filteredData.sort((a, b) => {
    const aValue = a[sortBy as keyof ExampleData];
    const bValue = b[sortBy as keyof ExampleData];

    if (sortOrder === 'desc') {
      return bValue.localeCompare(aValue);
    } else {
      return aValue.localeCompare(bValue);
    }
  });

  // åˆ†é¡µ
  const total = filteredData.length;
  const paginatedData = filteredData.slice(offset, offset + limit);

  return createSuccessResponse(paginatedData, {
    pagination: {
      page,
      limit,
      total,
      totalPages: Math.ceil(total / limit)
    },
    performance: {
      duration: 50 // æ¨¡æ‹ŸæŸ¥è¯¢æ—¶é—´
    }
  });
});

/**
 * POST /api/example - åˆ›å»ºæ•°æ®
 */
export const POST = withErrorHandling(async (request: NextRequest) => {
  const validatedData = await validateRequestBody(request, validateCreateRequest);

  logger.info('POST /api/example', {
    metadata: {
      name: validatedData.name,
      email: validatedData.email
    }
  });

  // æ¨¡æ‹Ÿåˆ›å»ºæ•°æ®
  const newData: ExampleData = {
    id: (mockData.length + 1).toString(),
    name: validatedData.name,
    email: validatedData.email,
    createdAt: new Date().toISOString()
  };

  mockData.push(newData);

  return createSuccessResponse(newData, {
    performance: {
      duration: 100 // æ¨¡æ‹Ÿåˆ›å»ºæ—¶é—´
    }
  }, 201);
});

/**
 * PUT /api/example/[id] - æ›´æ–°æ•°æ®
 */
export const PUT = withErrorHandling(async (request: NextRequest) => {
  const url = new URL(request.url);
  const pathSegments = url.pathname.split('/');
  const id = pathSegments[pathSegments.length - 1];

  if (!id) {
    return createValidationErrorResponse('ç¼ºå°‘IDå‚æ•°');
  }

  const validatedData = await validateRequestBody(request, validateUpdateRequest);

  logger.info('PUT /api/example/:id', {
    metadata: {
      id,
      updateData: validatedData
    }
  });

  // æŸ¥æ‰¾æ•°æ®
  const dataIndex = mockData.findIndex(item => item.id === id);
  if (dataIndex === -1) {
    return createNotFoundErrorResponse('æ•°æ®');
  }

  // æ£€æŸ¥é‚®ç®±å†²çª
  if (validatedData.email && 
      mockData.some(item => item.id !== id && item.email === validatedData.email)) {
    return createBusinessErrorResponse('é‚®ç®±å·²è¢«å…¶ä»–ç”¨æˆ·ä½¿ç”¨');
  }

  // æ›´æ–°æ•°æ®
  const updatedData = {
    ...mockData[dataIndex],
    ...validatedData
  };

  mockData[dataIndex] = updatedData;

  return createSuccessResponse(updatedData, {
    performance: {
      duration: 80 // æ¨¡æ‹Ÿæ›´æ–°æ—¶é—´
    }
  });
});

/**
 * DELETE /api/example/[id] - åˆ é™¤æ•°æ®
 */
export const DELETE = withErrorHandling(async (request: NextRequest) => {
  const url = new URL(request.url);
  const pathSegments = url.pathname.split('/');
  const id = pathSegments[pathSegments.length - 1];

  if (!id) {
    return createValidationErrorResponse('ç¼ºå°‘IDå‚æ•°');
  }

  logger.info('DELETE /api/example/:id', {
    metadata: { id }
  });

  // æŸ¥æ‰¾æ•°æ®
  const dataIndex = mockData.findIndex(item => item.id === id);
  if (dataIndex === -1) {
    return createNotFoundErrorResponse('æ•°æ®');
  }

  // åˆ é™¤æ•°æ®
  const deletedData = mockData.splice(dataIndex, 1)[0];

  return createSuccessResponse(deletedData, {
    performance: {
      duration: 30 // æ¨¡æ‹Ÿåˆ é™¤æ—¶é—´
    }
  });
});

