  return {
    title: isNetworkError ? 'ç½‘ç»œè¿æ¥é”™è¯¯' : 'ç³»ç»Ÿé”™è¯¯',
    message: isNetworkError ? 'ç½‘ç»œè¿æ¥å¤±è´¥ï¼Œè¯·æ£€æŸ¥ç½‘ç»œåé‡è¯•' : 'ç³»ç»Ÿé‡åˆ°äº†ä¸€ä¸ªé”™è¯¯ï¼Œè¯·ç¨åé‡è¯•',
    canRetry: true,
    isClientError: false,
