'use client';

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { logger } from '@/lib/logging/logger';

/**
 * æ— é™æ»šåŠ¨é…ç½®
 */
export interface InfiniteScrollConfig {
  // è§¦å‘åŠ è½½æ›´å¤šçš„é˜ˆå€¼ï¼ˆè·ç¦»åº•éƒ¨çš„åƒç´ ï¼‰
  threshold?: number;
  // æ˜¯å¦å¯ç”¨
  enabled?: boolean;
  // æ˜¯å¦æœ‰æ›´å¤šæ•°æ®
  hasMore?: boolean;
  // åŠ è½½å»¶è¿Ÿï¼ˆé˜²æŠ–ï¼‰
  loadDelay?: number;
  // åˆå§‹åŠ è½½
  initialLoad?: boolean;
  // åå‘æ»šåŠ¨ï¼ˆå‘ä¸ŠåŠ è½½æ›´å¤šï¼‰
  reverse?: boolean;
  // ä½¿ç”¨çª—å£æ»šåŠ¨è€Œä¸æ˜¯å®¹å™¨æ»šåŠ¨
  useWindow?: boolean;
  // è‡ªå®šä¹‰æ ¹å…ƒç´ 
  root?: Element | null;
  // æ ¹è¾¹è·
  rootMargin?: string;
}

/**
 * æ— é™æ»šåŠ¨çŠ¶æ€
 */
export interface InfiniteScrollState {
  loading: boolean;
  error: Error | null;
  hasMore: boolean;
  page: number;
  totalLoaded: number;
}

/**
 * æ— é™æ»šåŠ¨å±æ€§
 */
interface InfiniteScrollProps {
  // å­ç»„ä»¶
  children: React.ReactNode;
  // åŠ è½½æ›´å¤šå›è°ƒ
  onLoadMore: (page: number) => Promise<boolean>;
  // é…ç½®
  config?: InfiniteScrollConfig;
  // åŠ è½½ä¸­ç»„ä»¶
  loadingComponent?: React.ComponentType;
  // é”™è¯¯ç»„ä»¶
  errorComponent?: React.ComponentType<{ error: Error; retry: () => void }>;
  // æ²¡æœ‰æ›´å¤šæ•°æ®ç»„ä»¶
  endComponent?: React.ComponentType;
  // å®¹å™¨æ ·å¼
  containerStyle?: React.CSSProperties;
  // å®¹å™¨ç±»å
  containerClassName?: string;
  // åŠ è½½æ›´å¤šå›è°ƒï¼ˆç®€åŒ–ç‰ˆï¼‰
  onLoad?: () => void;
  // é”™è¯¯å›è°ƒ
  onError?: (error: Error) => void;
  // çŠ¶æ€å˜åŒ–å›è°ƒ
  onStateChange?: (state: InfiniteScrollState) => void;
}

/**
 * æ— é™æ»šåŠ¨ç»„ä»¶
 */
const InfiniteScroll: React.FC<InfiniteScrollProps> = ({
  children,
  onLoadMore,
  config = {},
  loadingComponent: LoadingComponent = DefaultLoadingComponent,
  errorComponent: ErrorComponent = DefaultErrorComponent,
  endComponent: EndComponent = DefaultEndComponent,
  containerStyle,
  containerClassName,
  onLoad,
  onError,
  onStateChange
}) => {
  const {
    threshold = 100,
    enabled = true,
    hasMore: configHasMore = true,
    loadDelay = 200,
    initialLoad = false,
    reverse = false,
    useWindow = false,
    root = null,
    rootMargin = '0px'
  } = config;

  // çŠ¶æ€
  const [state, setState] = useState<InfiniteScrollState>({
    loading: false,
    error: null,
    hasMore: configHasMore,
    page: 0,
    totalLoaded: 0
  });

  // å¼•ç”¨
  const containerRef = useRef<HTMLDivElement>(null);
  const sentinelRef = useRef<HTMLDivElement>(null);
  const observerRef = useRef<IntersectionObserver | null>(null);
  const loadTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const isLoadingRef = useRef(false);

  // æ›´æ–°çŠ¶æ€å¹¶è§¦å‘å›è°ƒ
  const updateState = useCallback((newState: Partial<InfiniteScrollState>) => {
    setState(prevState => {
      const updatedState = { ...prevState, ...newState };
      onStateChange?.(updatedState);
      return updatedState;
    });
  }, [onStateChange]);

  // åŠ è½½æ›´å¤šæ•°æ®
  const loadMore = useCallback(async () => {
    if (!enabled || state.loading || !state.hasMore || isLoadingRef.current) {
      return;
    }

    isLoadingRef.current = true;
    updateState({ loading: true, error: null });

    try {
      const nextPage = state.page + 1;
      logger.debug('Loading more data', { page: nextPage });

      let hasMoreData = true;

      if (onLoadMore) {
        hasMoreData = await onLoadMore(nextPage);
      } else if (onLoad) {
        onLoad();
      }

      updateState({
        loading: false,
        page: nextPage,
        hasMore: hasMoreData,
        totalLoaded: state.totalLoaded + 1
      });

      logger.debug('Data loaded successfully', { 
        page: nextPage, 
        hasMore: hasMoreData 
      });

    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));

      updateState({
        loading: false,
        error: err
      });

      onError?.(err);

      logger.error('Failed to load more data', err, { page: state.page + 1 });
    } finally {
      isLoadingRef.current = false;
    }
  }, [enabled, state.loading, state.hasMore, state.page, state.totalLoaded, onLoadMore, onLoad, onError, updateState]);

  // å»¶è¿ŸåŠ è½½ï¼ˆé˜²æŠ–ï¼‰
  const debouncedLoadMore = useCallback(() => {
    if (loadTimeoutRef.current) {
      clearTimeout(loadTimeoutRef.current);
    }

    loadTimeoutRef.current = setTimeout(() => {
      loadMore();
    }, loadDelay);
  }, [loadMore, loadDelay]);

  // é‡è¯•åŠ è½½
  const retry = useCallback(() => {
    updateState({ error: null });
    loadMore();
  }, [loadMore, updateState]);

  // æ»šåŠ¨äº‹ä»¶å¤„ç†ï¼ˆç”¨äºéIntersection Observerçš„æƒ…å†µï¼‰
  const handleScroll = useCallback(() => {
    if (!enabled || state.loading || !state.hasMore) {
      return;
    }

    const container = useWindow ? window : containerRef.current;
    if (!container) return;

    let scrollTop: number;
    let scrollHeight: number;
    let clientHeight: number;

    if (useWindow) {
      scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      scrollHeight = document.documentElement.scrollHeight;
      clientHeight = window.innerHeight;
    } else {
      const element = container as HTMLElement;
      scrollTop = element.scrollTop;
      scrollHeight = element.scrollHeight;
      clientHeight = element.clientHeight;
    }

    const distanceFromBottom = scrollHeight - scrollTop - clientHeight;
    const shouldLoad = reverse 
      ? scrollTop <= threshold
      : distanceFromBottom <= threshold;

    if (shouldLoad) {
      debouncedLoadMore();
    }
  }, [enabled, state.loading, state.hasMore, useWindow, threshold, reverse, debouncedLoadMore]);

  // è®¾ç½®Intersection Observer
  useEffect(() => {
    if (!sentinelRef.current || !enabled) {
      return;
    }

    const sentinel = sentinelRef.current;

    const observer = new IntersectionObserver(
      (entries) => {
        const entry = entries[0];
        if (entry.isIntersecting && state.hasMore && !state.loading) {
          debouncedLoadMore();
        }
      },
      {
        root: useWindow ? null : (root || containerRef.current),
        rootMargin,
        threshold: 0.1
      }
    );

    observer.observe(sentinel);
    observerRef.current = observer;

    return () => {
      observer.disconnect();
    };
  }, [enabled, state.hasMore, state.loading, useWindow, root, rootMargin, debouncedLoadMore]);

  // è®¾ç½®æ»šåŠ¨ç›‘å¬ï¼ˆå¤‡ç”¨æ–¹æ¡ˆï¼‰
  useEffect(() => {
    if (!enabled || observerRef.current) {
      return; // å¦‚æœIntersection Observerå¯ç”¨ï¼Œåˆ™ä¸ä½¿ç”¨æ»šåŠ¨ç›‘å¬
    }

    const container = useWindow ? window : containerRef.current;
    if (!container) return;

    container.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      container.removeEventListener('scroll', handleScroll);
    };
  }, [enabled, useWindow, handleScroll]);

  // åˆå§‹åŠ è½½
  useEffect(() => {
    if (initialLoad && enabled && state.page === 0 && !state.loading) {
      loadMore();
    }
  }, [initialLoad, enabled, state.page, state.loading, loadMore]);

  // æ¸…ç†å®šæ—¶å™¨
  useEffect(() => {
    return () => {
      if (loadTimeoutRef.current) {
        clearTimeout(loadTimeoutRef.current);
      }
    };
  }, []);

  // æ¸²æŸ“åŠ è½½çŠ¶æ€
  const renderLoadingState = () => {
    if (!state.loading) return null;
    return <LoadingComponent />;
  };

  // æ¸²æŸ“é”™è¯¯çŠ¶æ€
  const renderErrorState = () => {
    if (!state.error) return null;
    return <ErrorComponent error={state.error} retry={retry} />;
  };

  // æ¸²æŸ“ç»“æŸçŠ¶æ€
  const renderEndState = () => {
    if (state.hasMore || state.loading || state.error) return null;
    return <EndComponent />;
  };

  // æ¸²æŸ“å“¨å…µå…ƒç´ 
  const renderSentinel = () => {
    if (!enabled || !state.hasMore || state.error) return null;

    return (
      <div
        ref={sentinelRef}
        style={{
          height: '1px',
          width: '100%',
          visibility: 'hidden'
        }}
        data-testid="infinite-scroll-sentinel"
      />
    );
  };

  const containerProps = {
    ref: containerRef,
    style: {
      overflow: useWindow ? 'visible' : 'auto',
      ...containerStyle
    },
    className: containerClassName
  };

  return (
    <div {...containerProps}>
      {reverse && renderSentinel()}
      {reverse && renderLoadingState()}
      {reverse && renderErrorState()}

      {children}

      {!reverse && renderSentinel()}
      {!reverse && renderLoadingState()}
      {!reverse && renderErrorState()}
      {!reverse && renderEndState()}
    </div>
  );
};

/**
 * æ— é™æ»šåŠ¨Hook
 */
export function useInfiniteScroll<T>(
  fetchData: (page: number) => Promise<{ data: T[]; hasMore: boolean }>,
  config: InfiniteScrollConfig = {}
) {
  const [data, setData] = useState<T[]>([]);
  const [state, setState] = useState<InfiniteScrollState>({
    loading: false,
    error: null,
    hasMore: true,
    page: 0,
    totalLoaded: 0
  });

  const loadMore = useCallback(async (page: number): Promise<boolean> => {
    try {
      setState(prev => ({ ...prev, loading: true, error: null }));

      const result = await fetchData(page);

      setData(prev => page === 1 ? result.data : [...prev, ...result.data]);
      setState(prev => ({
        ...prev,
        loading: false,
        page,
        hasMore: result.hasMore,
        totalLoaded: prev.totalLoaded + result.data.length
      }));

      return result.hasMore;
    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));
      setState(prev => ({ ...prev, loading: false, error: err }));
      throw err;
    }
  }, [fetchData]);

  const reset = useCallback(() => {
    setData([]);
    setState({
      loading: false,
      error: null,
      hasMore: true,
      page: 0,
      totalLoaded: 0
    });
  }, []);

  const retry = useCallback(() => {
    if (state.error) {
      loadMore(state.page || 1);
    }
  }, [state.error, state.page, loadMore]);

  return {
    data,
    state,
    loadMore,
    reset,
    retry
  };
}

