'use client';

import React, { useState, useRef, useEffect, useCallback } from 'react';
import { logger } from '@/lib/logging/logger';

/**
 * æ‡’åŠ è½½å›¾ç‰‡é…ç½®
 */
export interface LazyImageConfig {
  // å ä½ç¬¦å›¾ç‰‡
  placeholder?: string;
  // é”™è¯¯æ—¶æ˜¾ç¤ºçš„å›¾ç‰‡
  fallback?: string;
  // äº¤å‰è§‚å¯Ÿå™¨é€‰é¡¹
  intersectionOptions?: IntersectionObserverInit;
  // æ˜¯å¦å¯ç”¨æ¸å…¥åŠ¨ç”»
  fadeIn?: boolean;
  // æ¸å…¥åŠ¨ç”»æŒç»­æ—¶é—´
  fadeInDuration?: number;
  // æ˜¯å¦å¯ç”¨æ¨¡ç³Šåˆ°æ¸…æ™°æ•ˆæœ
  blurToSharp?: boolean;
  // é‡è¯•æ¬¡æ•°
  retries?: number;
  // é‡è¯•å»¶è¿Ÿ
  retryDelay?: number;
  // é¢„åŠ è½½ç­–ç•¥
  preload?: 'none' | 'metadata' | 'auto';
  // è·¨åŸŸè®¾ç½®
  crossOrigin?: 'anonymous' | 'use-credentials';
  // å›¾ç‰‡ä¼˜å…ˆçº§
  fetchPriority?: 'high' | 'low' | 'auto';
  // å“åº”å¼å›¾ç‰‡å°ºå¯¸
  sizes?: string;
  // å“åº”å¼å›¾ç‰‡æºé›†
  srcSet?: string;
}

/**
 * æ‡’åŠ è½½å›¾ç‰‡çŠ¶æ€
 */
export interface LazyImageState {
  loading: boolean;
  loaded: boolean;
  error: boolean;
  retryCount: number;
  currentSrc: string;
}

/**
 * æ‡’åŠ è½½å›¾ç‰‡å±æ€§
 */
interface LazyImageProps extends Omit<React.ImgHTMLAttributes<HTMLImageElement>, 'src' | 'onLoad' | 'onError'> {
  // å›¾ç‰‡æº
  src: string;
  // æ›¿ä»£æ–‡æœ¬
  alt: string;
  // é…ç½®
  config?: LazyImageConfig;
  // å®¹å™¨æ ·å¼
  containerStyle?: React.CSSProperties;
  // å®¹å™¨ç±»å
  containerClassName?: string;
  // åŠ è½½æˆåŠŸå›è°ƒ
  onLoad?: (event: React.SyntheticEvent<HTMLImageElement>) => void;
  // åŠ è½½å¤±è´¥å›è°ƒ
  onError?: (error: Error) => void;
  // å¼€å§‹åŠ è½½å›è°ƒ
  onLoadStart?: () => void;
  // çŠ¶æ€å˜åŒ–å›è°ƒ
  onStateChange?: (state: LazyImageState) => void;
}

/**
 * æ‡’åŠ è½½å›¾ç‰‡ç»„ä»¶
 */
const LazyImage: React.FC<LazyImageProps> = ({
  src,
  alt,
  config = {},
  containerStyle,
  containerClassName,
  onLoad,
  onError,
  onLoadStart,
  onStateChange,
  style,
  className,
  ...imgProps
}) => {
  const {
    placeholder,
    fallback,
    intersectionOptions = { threshold: 0.1, rootMargin: '50px' },
    fadeIn = true,
    fadeInDuration = 300,
    blurToSharp = false,
    retries = 3,
    retryDelay = 1000,
    preload = 'none',
    crossOrigin,
    fetchPriority,
    sizes,
    srcSet
  } = config;

  // çŠ¶æ€
  const [state, setState] = useState<LazyImageState>({
    loading: false,
    loaded: false,
    error: false,
    retryCount: 0,
    currentSrc: placeholder || ''
  });

  // å¼•ç”¨
  const containerRef = useRef<HTMLDivElement>(null);
  const imgRef = useRef<HTMLImageElement>(null);
  const observerRef = useRef<IntersectionObserver | null>(null);
  const retryTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // æ›´æ–°çŠ¶æ€
  const updateState = useCallback((newState: Partial<LazyImageState>) => {
    setState(prevState => {
      const updatedState = { ...prevState, ...newState };
      onStateChange?.(updatedState);
      return updatedState;
    });
  }, [onStateChange]);

  // åŠ è½½å›¾ç‰‡
  const loadImage = useCallback(async (imageSrc: string): Promise<void> => {
    return new Promise((resolve, reject) => {
      const img = new Image();

      if (crossOrigin) {
        img.crossOrigin = crossOrigin;
      }

      img.onload = () => {
        resolve();
      };

      img.onerror = () => {
        reject(new Error(`Failed to load image: ${imageSrc}`));
      };

      img.src = imageSrc;
    });
  }, [crossOrigin]);

  // å¼€å§‹åŠ è½½å›¾ç‰‡
  const startLoading = useCallback(async () => {
    if (state.loading || state.loaded) {
      return;
    }

    updateState({ loading: true, error: false });
    onLoadStart?.();

    try {
      await loadImage(src);

      updateState({
        loading: false,
        loaded: true,
        currentSrc: src
      });

      // è§¦å‘åŠ è½½æˆåŠŸå›è°ƒ
      if (onLoad && imgRef.current) {
        const event = new Event('load') as any;
        onLoad(event);
      }

      logger.debug('Lazy image loaded successfully', { src });

    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));

      // é‡è¯•é€»è¾‘
      if (state.retryCount < retries) {
        const nextRetryCount = state.retryCount + 1;
        updateState({ retryCount: nextRetryCount });

        logger.warn('Lazy image load failed, retrying', {
          src,
          attempt: nextRetryCount,
          maxRetries: retries
        });

        retryTimeoutRef.current = setTimeout(() => {
          startLoading();
        }, retryDelay * nextRetryCount);

      } else {
        // æ‰€æœ‰é‡è¯•éƒ½å¤±è´¥äº†
        const finalSrc = fallback || placeholder || '';

        updateState({
          loading: false,
          error: true,
          currentSrc: finalSrc
        });

        onError?.(err);

        logger.error('Lazy image load failed after all retries', err, {
          src,
          retries
        });
      }
    }
  }, [src, state.loading, state.loaded, state.retryCount, retries, retryDelay, placeholder, fallback, loadImage, updateState, onLoadStart, onLoad, onError]);

  // è®¾ç½®äº¤å‰è§‚å¯Ÿå™¨
  useEffect(() => {
    if (!containerRef.current) {
      return;
    }

    const container = containerRef.current;

    const observer = new IntersectionObserver(
      (entries) => {
        const entry = entries[0];
        if (entry.isIntersecting && !state.loaded && !state.loading) {
          startLoading();
          observer.disconnect(); // åªåŠ è½½ä¸€æ¬¡
        }
      },
      intersectionOptions
    );

    observer.observe(container);
    observerRef.current = observer;

    return () => {
      observer.disconnect();
    };
  }, [intersectionOptions, state.loaded, state.loading, startLoading]);

  // æ¸…ç†å®šæ—¶å™¨
  useEffect(() => {
    return () => {
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
      }
    };
  }, []);

  // è®¡ç®—å›¾ç‰‡æ ·å¼
  const getImageStyle = (): React.CSSProperties => {
    const baseStyle: React.CSSProperties = {
      width: '100%',
      height: 'auto',
      display: 'block',
      ...style
    };

    // æ¸å…¥åŠ¨ç”»
    if (fadeIn) {
      baseStyle.transition = `opacity ${fadeInDuration}ms ease-in-out`;
      baseStyle.opacity = state.loaded ? 1 : 0;
    }

    // æ¨¡ç³Šåˆ°æ¸…æ™°æ•ˆæœ
    if (blurToSharp && !state.loaded && state.currentSrc === placeholder) {
      baseStyle.filter = 'blur(5px)';
      baseStyle.transition = `${baseStyle.transition || ''}, filter ${fadeInDuration}ms ease-in-out`;
    }

    return baseStyle;
  };

  // è®¡ç®—å®¹å™¨æ ·å¼
  const getContainerStyle = (): React.CSSProperties => {
    return {
      position: 'relative',
      overflow: 'hidden',
      ...containerStyle
    };
  };

  // æ¸²æŸ“åŠ è½½æŒ‡ç¤ºå™¨
  const renderLoadingIndicator = () => {
    if (!state.loading) return null;

    return (
      <div
        style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          zIndex: 1
        }}
      >
        <div
          style={{
            width: '24px',
            height: '24px',
            border: '2px solid #f3f4f6',
            borderTop: '2px solid #3b82f6',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite'
          }}
        />
        <style jsx>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  };

  // æ¸²æŸ“é”™è¯¯æŒ‡ç¤ºå™¨
  const renderErrorIndicator = () => {
    if (!state.error) return null;

    return (
      <div
        style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          zIndex: 1,
          color: '#ef4444',
          fontSize: '12px',
          textAlign: 'center'
        }}
      >
        <div style={{ marginBottom: '4px' }}>âš ï¸</div>
        <div>åŠ è½½å¤±è´¥</div>
      </div>
    );
  };

  const imgAttributes = {
    ref: imgRef,
    src: state.currentSrc,
    alt,
    style: getImageStyle(),
    className,
    loading: preload as any,
    crossOrigin,
    fetchPriority: fetchPriority as any,
    sizes,
    srcSet,
    ...imgProps
  };

  return (
    <div
      ref={containerRef}
      style={getContainerStyle()}
      className={containerClassName}
    >
      <img {...imgAttributes} />
      {renderLoadingIndicator()}
      {renderErrorIndicator()}
    </div>
  );
};

/**
 * å“åº”å¼æ‡’åŠ è½½å›¾ç‰‡ç»„ä»¶
 */
interface ResponsiveLazyImageProps extends Omit<LazyImageProps, 'src' | 'srcSet' | 'sizes'> {
