/**
 * åŠ è½½è¾¹ç•Œç»„ä»¶ - å¤„ç†æ‡’åŠ è½½è¿‡ç¨‹ä¸­çš„é”™è¯¯å’ŒåŠ è½½çŠ¶æ€
 */
'use client';

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { logger } from '@/lib/logging/logger';

/**
 * åŠ è½½è¾¹ç•Œå±æ€§
 */
interface LoadingBoundaryProps {
  children: ReactNode;
  fallback?: React.ComponentType<{ error?: Error; retry?: () => void }>;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
  retryable?: boolean;
  maxRetries?: number;
  retryDelay?: number;
}

/**
 * åŠ è½½è¾¹ç•ŒçŠ¶æ€
 */
interface LoadingBoundaryState {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
  retryCount: number;
  isRetrying: boolean;
}

/**
 * åŠ è½½è¾¹ç•Œç»„ä»¶
 */
class LoadingBoundary extends Component<LoadingBoundaryProps, LoadingBoundaryState> {
  private retryTimeoutId: NodeJS.Timeout | null = null;

  constructor(props: LoadingBoundaryProps) {
    super(props);

    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
      retryCount: 0,
      isRetrying: false
    };
  }

  static getDerivedStateFromError(error: Error): Partial<LoadingBoundaryState> {
    return {
      hasError: true,
      error
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    this.setState({
      errorInfo
    });

    // è®°å½•é”™è¯¯æ—¥å¿—
    logger.error('Loading boundary caught error', error, {
      componentStack: errorInfo.componentStack,
      errorBoundary: 'LoadingBoundary'
    });

    // è°ƒç”¨é”™è¯¯å›è°ƒ
    if (this.props.onError) {
      this.props.onError(error, errorInfo);
    }
  }

  componentWillUnmount() {
    if (this.retryTimeoutId) {
      clearTimeout(this.retryTimeoutId);
    }
  }

  /**
   * é‡è¯•åŠ è½½
   */
  retry = () => {
    const { maxRetries = 3, retryDelay = 1000 } = this.props;

    if (this.state.retryCount >= maxRetries) {
      logger.warn('Max retries reached for loading boundary');
      return;
    }

    this.setState({
      isRetrying: true
    });

    // å»¶è¿Ÿé‡è¯•
    this.retryTimeoutId = setTimeout(() => {
      this.setState({
        hasError: false,
        error: null,
        errorInfo: null,
        retryCount: this.state.retryCount + 1,
        isRetrying: false
      });

      logger.info('Loading boundary retry attempt', {
        retryCount: this.state.retryCount + 1,
        maxRetries
      });
    }, retryDelay);
  };

  render() {
    const { hasError, error, isRetrying, retryCount } = this.state;
    const { children, fallback: FallbackComponent, retryable = true, maxRetries = 3 } = this.props;

    if (hasError && error) {
      // å¦‚æœæä¾›äº†è‡ªå®šä¹‰é”™è¯¯ç»„ä»¶
      if (FallbackComponent) {
        return (
          <FallbackComponent 
            error={error} 
            retry={retryable && retryCount < maxRetries ? this.retry : undefined}
          />
        );
      }

      // é»˜è®¤é”™è¯¯UI
      return (
        <DefaultErrorFallback
          error={error}
          retry={retryable && retryCount < maxRetries ? this.retry : undefined}
          isRetrying={isRetrying}
          retryCount={retryCount}
          maxRetries={maxRetries}
        />
      );
    }

    return children;
  }
}

