/**
 * æ€§èƒ½ä¼˜åŒ–ç»„åˆç»„ä»¶
 */
'use client';

import React, { useEffect, useRef, useState, useCallback } from 'react';
import { logger } from '@/lib/logging/logger';
import { globalMemoryMonitor, MemoryInfo } from '@/lib/performance/memory';
import { globalCodeSplittingManager } from '@/lib/performance/code-splitting';
import FirstPaintOptimizer from '@/lib/performance/first-paint';
import PerformanceMonitor from '@/lib/performance/metrics';

/**
 * æ€§èƒ½ä¼˜åŒ–é…ç½®
 */
export interface PerformanceOptimizerConfig {
  // å†…å­˜ç›‘æ§
  memoryMonitoring?: {
    enabled: boolean;
    warningThreshold?: number;
    dangerThreshold?: number;
    interval?: number;
  };

  // é¦–å±ä¼˜åŒ–
  firstPaintOptimization?: {
    enabled: boolean;
    inlineCSSThreshold?: number;
    preloadFonts?: boolean;
    deferNonCritical?: boolean;
  };

  // ä»£ç åˆ†å‰²
  codeSplitting?: {
    enabled: boolean;
    preloadCritical?: boolean;
    prefetchRoutes?: string[];
  };

  // æ€§èƒ½ç›‘æ§
  performanceMonitoring?: {
    enabled: boolean;
    reportInterval?: number;
    reportEndpoint?: string;
  };

  // å¼€å‘å·¥å…·
  devTools?: {
    enabled: boolean;
    showMemoryInfo: boolean;
    showPerformanceInfo: boolean;
    position?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
  };
}

/**
 * æ€§èƒ½ä¼˜åŒ–å™¨å±æ€§
 */
interface PerformanceOptimizerProps {
  children: React.ReactNode;
  config?: PerformanceOptimizerConfig;
}

/**
 * æ€§èƒ½ç»Ÿè®¡ä¿¡æ¯
 */
interface PerformanceStats {
  memory: MemoryInfo | null;
  loadTime: number;
  renderTime: number;
  interactionTime: number;
  cacheHitRate: number;
}

/**
 * æ€§èƒ½ä¼˜åŒ–å™¨ç»„ä»¶
 */
const PerformanceOptimizer: React.FC<PerformanceOptimizerProps> = ({
  children,
  config = {}
}) => {
  const {
    memoryMonitoring = { enabled: true },
    firstPaintOptimization = { enabled: true },
    codeSplitting = { enabled: true },
    performanceMonitoring = { enabled: true },
    devTools = { enabled: process.env.NODE_ENV === 'development', showMemoryInfo: true, showPerformanceInfo: true }
  } = config;

  const [stats, setStats] = useState<PerformanceStats>({
    memory: null,
    loadTime: 0,
    renderTime: 0,
    interactionTime: 0,
    cacheHitRate: 0
  });

  const [isOptimizing, setIsOptimizing] = useState(false);
  const performanceMonitorRef = useRef<PerformanceMonitor | null>(null);
  const firstPaintOptimizerRef = useRef<FirstPaintOptimizer | null>(null);
  const startTimeRef = useRef<number>(Date.now());

  // åˆå§‹åŒ–æ€§èƒ½ç›‘æ§
  useEffect(() => {
    if (performanceMonitoring.enabled) {
      performanceMonitorRef.current = new PerformanceMonitor({
        reportInterval: performanceMonitoring.reportInterval,
        reportEndpoint: performanceMonitoring.reportEndpoint
      });
      performanceMonitorRef.current.start();
    }

    return () => {
      if (performanceMonitorRef.current) {
        performanceMonitorRef.current.stop();
      }
    };
  }, [performanceMonitoring]);

  // åˆå§‹åŒ–é¦–å±ä¼˜åŒ–
  useEffect(() => {
    if (firstPaintOptimization.enabled) {
      firstPaintOptimizerRef.current = new FirstPaintOptimizer({
        inlineCSSThreshold: firstPaintOptimization.inlineCSSThreshold,
        preloadFonts: firstPaintOptimization.preloadFonts,
        deferNonCritical: firstPaintOptimization.deferNonCritical
      });
      firstPaintOptimizerRef.current.optimize();
    }
  }, [firstPaintOptimization]);

  // åˆå§‹åŒ–å†…å­˜ç›‘æ§
  useEffect(() => {
    if (memoryMonitoring.enabled) {
      globalMemoryMonitor.start();

      const memoryConfig = {
        interval: memoryMonitoring.interval,
        warningThreshold: memoryMonitoring.warningThreshold,
        dangerThreshold: memoryMonitoring.dangerThreshold,
        onWarning: (info: MemoryInfo) => {
          logger.warn('Memory usage warning', { usagePercentage: info.usagePercentage });
          setStats(prev => ({ ...prev, memory: info }));
        },
        onDanger: (info: MemoryInfo) => {
          logger.error('Memory usage critical', { usagePercentage: info.usagePercentage });
          setStats(prev => ({ ...prev, memory: info }));
          // è§¦å‘è‡ªåŠ¨ä¼˜åŒ–
          optimizePerformance();
        }
      };
    }

    return () => {
      if (memoryMonitoring.enabled) {
        globalMemoryMonitor.stop();
      }
    };
  }, [memoryMonitoring]);

  // åˆå§‹åŒ–ä»£ç åˆ†å‰²
  useEffect(() => {
    if (codeSplitting.enabled) {
      if (codeSplitting.preloadCritical) {
        globalCodeSplittingManager.preloadCriticalChunks();
      }

      if (codeSplitting.prefetchRoutes) {
        codeSplitting.prefetchRoutes.forEach(route => {
          globalCodeSplittingManager.prefetchRoute(route);
        });
      }
    }
  }, [codeSplitting]);

  // æ€§èƒ½ç»Ÿè®¡æ›´æ–°
  const updateStats = useCallback(() => {
    const currentTime = Date.now();
    const loadTime = currentTime - startTimeRef.current;
    const memory = globalMemoryMonitor.getCurrentMemoryInfo();

    // è·å–æ€§èƒ½æŒ‡æ ‡
    if (typeof window !== 'undefined' && 'performance' in window) {
      const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming;
      const renderTime = navigation.loadEventEnd - navigation.loadEventStart;
      const interactionTime = navigation.domInteractive - navigation.navigationStart;

      setStats(prev => ({
        ...prev,
        memory,
        loadTime,
        renderTime,
        interactionTime
      }));
    }
  }, []);

  // å®šæœŸæ›´æ–°ç»Ÿè®¡ä¿¡æ¯
  useEffect(() => {
    const interval = setInterval(updateStats, 5000); // æ¯5ç§’æ›´æ–°ä¸€æ¬¡
    return () => clearInterval(interval);
  }, [updateStats]);

  // è‡ªåŠ¨æ€§èƒ½ä¼˜åŒ–
  const optimizePerformance = useCallback(async () => {
    if (isOptimizing) return;

    setIsOptimizing(true);
    logger.info('Starting automatic performance optimization');

    try {
      // æ¸…ç†å†…å­˜
      if (typeof window !== 'undefined' && (window as any).gc) {
        (window as any).gc();
      }

      // æ¸…ç†ç¼“å­˜
      if ('caches' in window) {
        const cacheNames = await caches.keys();
        const oldCaches = cacheNames.filter(name => 
          name.includes('old') || name.includes('expired')
        );

        await Promise.all(
          oldCaches.map(cacheName => caches.delete(cacheName))
        );
      }

      // é¢„åŠ è½½å…³é”®èµ„æº
      if (firstPaintOptimizerRef.current) {
        firstPaintOptimizerRef.current.preloadCriticalResources();
      }

      // ä¼˜åŒ–ä»£ç åˆ†å‰²
      globalCodeSplittingManager.optimizeChunkLoading();

      logger.info('Performance optimization completed');
    } catch (error) {
      logger.error('Performance optimization failed', error instanceof Error ? error : new Error(String(error)));
    } finally {
      setIsOptimizing(false);
    }
  }, [isOptimizing]);

  // æ‰‹åŠ¨è§¦å‘ä¼˜åŒ–
  const handleOptimize = useCallback(() => {
    optimizePerformance();
  }, [optimizePerformance]);

  // å¼€å‘å·¥å…·ç»„ä»¶
  const DevTools = () => {
    if (!devTools.enabled || process.env.NODE_ENV !== 'development') {
      return null;
    }

    const position = devTools.position || 'bottom-right';
    const positionStyles = {
      'top-left': { top: '10px', left: '10px' },
      'top-right': { top: '10px', right: '10px' },
      'bottom-left': { bottom: '10px', left: '10px' },
      'bottom-right': { bottom: '10px', right: '10px' }
    };

    return (
      <div
        style={{
          position: 'fixed',
          ...positionStyles[position],
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          color: 'white',
          padding: '10px',
          borderRadius: '5px',
          fontSize: '12px',
          fontFamily: 'monospace',
          zIndex: 9999,
          minWidth: '200px'
        }}
      >
        <div style={{ marginBottom: '5px', fontWeight: 'bold' }}>
          Performance Monitor
        </div>

        {devTools.showMemoryInfo && stats.memory && (
          <div style={{ marginBottom: '5px' }}>
            <div>Memory: {Math.round(stats.memory.usagePercentage)}%</div>
            <div>Used: {Math.round(stats.memory.usedJSHeapSize / 1024 / 1024)}MB</div>
          </div>
        )}

        {devTools.showPerformanceInfo && (
          <div style={{ marginBottom: '5px' }}>
            <div>Load: {stats.loadTime}ms</div>
            <div>Render: {stats.renderTime}ms</div>
            <div>Interactive: {stats.interactionTime}ms</div>
          </div>
        )}

        <button
          onClick={handleOptimize}
          disabled={isOptimizing}
          style={{
            backgroundColor: isOptimizing ? '#666' : '#007bff',
            color: 'white',
            border: 'none',
            padding: '5px 10px',
            borderRadius: '3px',
            cursor: isOptimizing ? 'not-allowed' : 'pointer',
            fontSize: '11px'
          }}
        >
          {isOptimizing ? 'Optimizing...' : 'Optimize'}
        </button>
      </div>
    );
  };

  return (
    <>
      {children}
      <DevTools />
    </>
  );
};

/**
 * æ€§èƒ½ä¼˜åŒ–Hook
 */
export function usePerformanceOptimizer(config: PerformanceOptimizerConfig = {}) {
  const [isOptimizing, setIsOptimizing] = useState(false);
  const [stats, setStats] = useState<PerformanceStats>({
    memory: null,
    loadTime: 0,
    renderTime: 0,
    interactionTime: 0,
    cacheHitRate: 0
  });

  const optimize = useCallback(async () => {
    if (isOptimizing) return;

    setIsOptimizing(true);

    try {
      // æ‰§è¡Œä¼˜åŒ–é€»è¾‘
      if (typeof window !== 'undefined' && (window as any).gc) {
        (window as any).gc();
      }

      // æ›´æ–°ç»Ÿè®¡ä¿¡æ¯
      const memory = globalMemoryMonitor.getCurrentMemoryInfo();
      setStats(prev => ({ ...prev, memory }));

    } catch (error) {
      logger.error('Performance optimization failed', error instanceof Error ? error : new Error(String(error)));
    } finally {
      setIsOptimizing(false);
    }
  }, [isOptimizing]);

  const getRecommendations = useCallback(() => {
    const recommendations: string[] = [];

    if (stats.memory && stats.memory.usagePercentage > 80) {
      recommendations.push('Consider reducing memory usage');
    }

    if (stats.loadTime > 3000) {
      recommendations.push('Optimize loading performance');
    }

    if (stats.renderTime > 1000) {
      recommendations.push('Optimize rendering performance');
    }

    return recommendations;
  }, [stats]);

  return {
    stats,
    isOptimizing,
    optimize,
    getRecommendations
  };
}

/**
 * æ€§èƒ½ç›‘æ§Provider
 */
export const PerformanceProvider: React.FC<{
  children: React.ReactNode;
  config?: PerformanceOptimizerConfig;
}> = ({ children, config }) => {
  return (
    <PerformanceOptimizer config={config}>
      {children}
    </PerformanceOptimizer>
  );
};

export default PerformanceOptimizer;

/**
 * èµ„æºä¼˜åŒ–ç»„ä»¶
 */
'use client';

import React, { useEffect, useRef, useState } from 'react';
import { logger } from '@/lib/logging/logger';
import ResourcePreloader, { PreloadConfig, ResourceType } from '@/lib/performance/preload';
import PerformanceMonitor from '@/lib/performance/metrics';

/**
 * èµ„æºä¼˜åŒ–å™¨å±æ€§
 */
interface ResourceOptimizerProps {
  // é¢„åŠ è½½é…ç½®
  criticalResources?: PreloadConfig[];
  importantResources?: PreloadConfig[];

  // å¤–éƒ¨åŸŸåé¢„è¿æ¥
  externalDomains?: string[];

  // å­—ä½“é¢„åŠ è½½
  fonts?: Array<{
    href: string;
    format?: string;
    display?: 'auto' | 'block' | 'swap' | 'fallback' | 'optional';
  }>;

  // å›¾ç‰‡é¢„åŠ è½½
  images?: Array<{
    href: string;
    sizes?: string;
    srcset?: string;
    media?: string;
  }>;

  // æ™ºèƒ½é¢„åŠ è½½é…ç½®
  smartPreload?: {
    enabled: boolean;
    mouseoverDelay?: number;
    touchDelay?: number;
    visibilityThreshold?: number;
  };

  // æ€§èƒ½ç›‘æ§é…ç½®
  performanceMonitoring?: {
    enabled: boolean;
    reportEndpoint?: string;
    reportInterval?: number;
  };

  // å­ç»„ä»¶
  children?: React.ReactNode;
}

/**
 * èµ„æºä¼˜åŒ–å™¨ç»„ä»¶
 */
const ResourceOptimizer: React.FC<ResourceOptimizerProps> = ({
  criticalResources = [],
  importantResources = [],
  externalDomains = [],
  fonts = [],
  images = [],
  smartPreload = { enabled: true },
  performanceMonitoring = { enabled: true },
  children
}) => {
  const preloaderRef = useRef<ResourcePreloader | null>(null);
  const performanceMonitorRef = useRef<PerformanceMonitor | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);
  const [performanceScore, setPerformanceScore] = useState<string>('unknown');

  useEffect(() => {
    // åˆå§‹åŒ–èµ„æºé¢„åŠ è½½å™¨
    if (!preloaderRef.current) {
      preloaderRef.current = new ResourcePreloader();
    }

    // åˆå§‹åŒ–æ€§èƒ½ç›‘æ§å™¨
    if (performanceMonitoring.enabled && !performanceMonitorRef.current) {
      performanceMonitorRef.current = new PerformanceMonitor();
    }

    const initializeOptimizations = async () => {
      try {
        const preloader = preloaderRef.current!;

        // 1. é¢„è¿æ¥å¤–éƒ¨åŸŸå
        if (externalDomains.length > 0) {
          preloader.preconnectDomains(externalDomains);
          preloader.dnsPrefetch(externalDomains);
        }

        // 2. é¢„åŠ è½½å…³é”®èµ„æº
        if (criticalResources.length > 0) {
          await preloader.preloadCritical(criticalResources);
        }

        // 3. é¢„åŠ è½½å­—ä½“
        if (fonts.length > 0) {
          await preloader.preloadFonts(fonts);
        }

        // 4. é¢„åŠ è½½å›¾ç‰‡
        if (images.length > 0) {
          await preloader.preloadImages(images);
        }

        // 5. é¢„è·å–é‡è¦èµ„æº
        if (importantResources.length > 0) {
          await preloader.prefetchImportant(importantResources);
        }

        // 6. å¯ç”¨æ™ºèƒ½é¢„åŠ è½½
        if (smartPreload.enabled) {
          preloader.smartPreload({
            mouseoverDelay: smartPreload.mouseoverDelay,
            touchDelay: smartPreload.touchDelay,
            visibilityThreshold: smartPreload.visibilityThreshold
          });
        }

        setIsInitialized(true);
        logger.info('Resource optimization initialized');

      } catch (error) {
        logger.error('Resource optimization initialization failed', error instanceof Error ? error : new Error(String(error)));
      }
    };

    initializeOptimizations();

    // è®¾ç½®æ€§èƒ½ç›‘æ§æŠ¥å‘Š
    if (performanceMonitoring.enabled && performanceMonitoring.reportEndpoint) {
      const reportInterval = performanceMonitoring.reportInterval || 30000; // 30ç§’

      const intervalId = setInterval(async () => {
        if (performanceMonitorRef.current) {
          try {
            await performanceMonitorRef.current.sendReport(performanceMonitoring.reportEndpoint!);

            // æ›´æ–°æ€§èƒ½è¯„åˆ†
            const evaluation = performanceMonitorRef.current.evaluatePerformance();
            setPerformanceScore(evaluation.overall);

          } catch (error) {
            logger.error('Performance report failed', error instanceof Error ? error : new Error(String(error)));
          }
        }
      }, reportInterval);

      return () => {
        clearInterval(intervalId);
      };
    }

    // æ¸…ç†å‡½æ•°
    return () => {
      if (preloaderRef.current) {
        preloaderRef.current.cleanup();
      }
      if (performanceMonitorRef.current) {
        performanceMonitorRef.current.cleanup();
      }
    };
  }, []);

  // å¼€å‘ç¯å¢ƒä¸‹æ˜¾ç¤ºæ€§èƒ½ä¿¡æ¯
  const showPerformanceInfo = process.env.NODE_ENV === 'development' && performanceMonitoring.enabled;

  return (
    <>
      {children}

      {/* å¼€å‘ç¯å¢ƒæ€§èƒ½ä¿¡æ¯æ˜¾ç¤º */}
      {showPerformanceInfo && (
        <PerformanceInfo
          isInitialized={isInitialized}
          performanceScore={performanceScore}
          preloader={preloaderRef.current}
          monitor={performanceMonitorRef.current}
        />
      )}
    </>
  );
};

