import React, { useState, useEffect } from 'react';

/**
 * é‡è¯•æŒ‰é’®å±æ€§
 */
interface RetryButtonProps {
  onRetry: () => Promise<void> | void;
  maxRetries?: number;
  retryDelay?: number;
  autoRetry?: boolean;
  disabled?: boolean;
  className?: string;
  size?: 'sm' | 'md' | 'lg';
  variant?: 'primary' | 'secondary' | 'outline';
  showCount?: boolean;
  resetOnSuccess?: boolean;
  children?: React.ReactNode;
}

/**
 * é‡è¯•æŒ‰é’®ç»„ä»¶
 */
export const RetryButton: React.FC<RetryButtonProps> = ({
  onRetry,
  maxRetries = 3,
  retryDelay = 3000,
  autoRetry = false,
  disabled = false,
  className = '',
  size = 'md',
  variant = 'primary',
  showCount = true,
  resetOnSuccess = true,
  children
}) => {
  const [retryCount, setRetryCount] = useState(0);
  const [isRetrying, setIsRetrying] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const [lastError, setLastError] = useState<Error | null>(null);

  // è‡ªåŠ¨é‡è¯•å€’è®¡æ—¶
  useEffect(() => {
    if (countdown > 0) {
      const timer = setTimeout(() => {
        setCountdown(countdown - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (countdown === 0 && autoRetry && retryCount > 0 && retryCount < maxRetries) {
      handleRetry();
    }
  }, [countdown, autoRetry, retryCount, maxRetries]);

  const handleRetry = async () => {
    if (isRetrying || retryCount >= maxRetries) {
      return;
    }

    setIsRetrying(true);
    setLastError(null);

    try {
      await onRetry();

      // æˆåŠŸåé‡ç½®è®¡æ•°
      if (resetOnSuccess) {
        setRetryCount(0);
      }
    } catch (error) {
      const newRetryCount = retryCount + 1;
      setRetryCount(newRetryCount);
      setLastError(error instanceof Error ? error : new Error('Retry failed'));

      // å¦‚æœè¿˜æœ‰é‡è¯•æ¬¡æ•°ä¸”å¯ç”¨è‡ªåŠ¨é‡è¯•ï¼Œå¼€å§‹å€’è®¡æ—¶
      if (autoRetry && newRetryCount < maxRetries) {
        setCountdown(Math.floor(retryDelay / 1000));
      }
    } finally {
      setIsRetrying(false);
    }
  };

  const resetRetries = () => {
    setRetryCount(0);
    setCountdown(0);
    setLastError(null);
  };

  // æ ·å¼é…ç½®
  const sizeClasses = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-4 py-2 text-base',
    lg: 'px-6 py-3 text-lg'
  };

  const variantClasses = {
    primary: 'bg-blue-600 text-white hover:bg-blue-700 disabled:bg-blue-400',
    secondary: 'bg-gray-600 text-white hover:bg-gray-700 disabled:bg-gray-400',
    outline: 'border-2 border-blue-600 text-blue-600 hover:bg-blue-50 disabled:border-gray-300 disabled:text-gray-400'
  };

  const baseClasses = 'font-medium rounded-md transition-colors duration-200 disabled:cursor-not-allowed focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500';

  const buttonClasses = `${baseClasses} ${sizeClasses[size]} ${variantClasses[variant]} ${className}`;

  const canRetry = retryCount < maxRetries;
  const isDisabled = disabled || isRetrying || !canRetry;

  return (
    <div className="inline-flex flex-col items-center space-y-2">
      {/* ä¸»é‡è¯•æŒ‰é’® */}
      <button
        onClick={handleRetry}
        disabled={isDisabled}
        className={buttonClasses}
      >
        {isRetrying ? (
          <span className="flex items-center justify-center">
            <svg className="animate-spin -ml-1 mr-2 h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
            é‡è¯•ä¸­...
          </span>
        ) : countdown > 0 ? (
          `${countdown}ç§’åé‡è¯•`
        ) : (
          children || (
            <span>
              é‡è¯•
              {showCount && retryCount > 0 && ` (${retryCount}/${maxRetries})`}
            </span>
          )
        )}
      </button>

      {/* é‡è¯•çŠ¶æ€ä¿¡æ¯ */}
      {retryCount > 0 && (
        <div className="text-center">
          {canRetry ? (
            <p className="text-sm text-gray-600">
              å·²é‡è¯• {retryCount}/{maxRetries} æ¬¡
              {countdown > 0 && autoRetry && (
                <span className="block text-xs text-gray-500 mt-1">
                  {countdown} ç§’åè‡ªåŠ¨é‡è¯•
                </span>
              )}
            </p>
          ) : (
            <div className="text-center">
              <p className="text-sm text-red-600 mb-2">
                å·²è¾¾åˆ°æœ€å¤§é‡è¯•æ¬¡æ•° ({maxRetries})
              </p>
              <button
                onClick={resetRetries}
                className="text-xs text-blue-600 hover:text-blue-800 underline"
              >
                é‡ç½®é‡è¯•
              </button>
            </div>
          )}
        </div>
      )}

      {/* é”™è¯¯ä¿¡æ¯ */}
      {lastError && (
        <div className="text-center max-w-xs">
          <p className="text-xs text-red-600">
            {lastError.message}
          </p>
        </div>
      )}
    </div>
  );
};

/**
 * æ™ºèƒ½é‡è¯•æŒ‰é’®ï¼ˆå¸¦æŒ‡æ•°é€€é¿ï¼‰
 */
export const SmartRetryButton: React.FC<Omit<RetryButtonProps, 'retryDelay'> & {
  baseDelay?: number;
  maxDelay?: number;
  backoffFactor?: number;
}> = ({
  baseDelay = 1000,
  maxDelay = 30000,
  backoffFactor = 2,
  ...props
}) => {
  const [currentDelay, setCurrentDelay] = useState(baseDelay);

  // è®¡ç®—ä¸‹æ¬¡é‡è¯•å»¶è¿Ÿï¼ˆæŒ‡æ•°é€€é¿ï¼‰
  const calculateDelay = (retryCount: number): number => {
    const delay = baseDelay * Math.pow(backoffFactor, retryCount);
    return Math.min(delay, maxDelay);
  };

  const handleRetry = async () => {
    try {
      await props.onRetry();
      // æˆåŠŸåé‡ç½®å»¶è¿Ÿ
      setCurrentDelay(baseDelay);
    } catch (error) {
      // å¤±è´¥åå¢åŠ å»¶è¿Ÿ
      setCurrentDelay(prev => calculateDelay(Math.log(prev / baseDelay) / Math.log(backoffFactor) + 1));
      throw error;
    }
  };

  return (
    <RetryButton
      {...props}
      onRetry={handleRetry}
      retryDelay={currentDelay}
    />
  );
};

