        <div className="text-red-600 mb-4">
          <svg className="w-12 h-12 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
          </svg>
          <p className="font-medium">åŠ è½½å¤±è´¥</p>
          <p className="text-sm text-gray-600 mt-1">{error}</p>
        </div>
        <button
          onClick={() => fetchLeaderboard()}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          é‡è¯•
        </button>
      </div>
    );
  }

  if (!leaderboard || leaderboard.entries.length === 0) {
    return (
      <div className={`text-center py-12 ${className}`}>
        <div className="text-gray-500">
          <svg className="w-12 h-12 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
          </svg>
          <p className="font-medium">æš‚æ— æ’è¡Œæ¦œæ•°æ®</p>
          <p className="text-sm mt-1">è¿˜æ²¡æœ‰ç”¨æˆ·è·å¾—è´¡çŒ®åº¦ç§¯åˆ†</p>
        </div>
      </div>
    );
  }

  return (
    <div className={className}>
      {/* æ ‡é¢˜å’Œç»Ÿè®¡ä¿¡æ¯ */}
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">
          {getTypeTitle(type)}
        </h2>
        <div className="flex items-center space-x-6 text-sm text-gray-600">
          <span>å…± {leaderboard.entries.length} ä½ç”¨æˆ·</span>
          <span>æ›´æ–°æ—¶é—´: {new Date(leaderboard.lastUpdated).toLocaleString()}</span>
        </div>
      </div>

      {/* å½“å‰ç”¨æˆ·æ’åï¼ˆå¦‚æœä¸åœ¨å‰åˆ—è¡¨ä¸­ï¼‰ */}
      {leaderboard.userRank && leaderboard.userRank.rank > limit && (
        <div className="mb-6">
