                <button
                  onClick={() => onCardRegenerate?.(currentCard.id, currentCard.type)}
                  className="touch-target p-2 text-gray-400 hover:text-green-600 transition-colors"
                  aria-label="é‡æ–°ç”Ÿæˆå¡ç‰‡"
                >
