import React, { useState, useRef, useEffect } from 'react';

interface KnowledgeNode {
  id: string;
  name: string;
  subject: string;
  level: number;
  worksCount: number;
  x: number;
  y: number;
  connections: string[];
}

interface MobileKnowledgeGraphProps {
  nodes: KnowledgeNode[];
  onNodeClick?: (nodeId: string) => void;
}

const MobileKnowledgeGraph: React.FC<MobileKnowledgeGraphProps> = ({ 
  nodes, 
  onNodeClick 
}) => {
  const [viewMode, setViewMode] = useState<'overview' | 'detail'>('overview');
  const [selectedNode, setSelectedNode] = useState<KnowledgeNode | null>(null);
  const [scale, setScale] = useState(1);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [lastTouch, setLastTouch] = useState({ x: 0, y: 0 });

  const containerRef = useRef<HTMLDivElement>(null);
  const svgRef = useRef<SVGSVGElement>(null);

  // è·å–èŠ‚ç‚¹é¢œè‰²
  const getNodeColor = (level: number, worksCount: number) => {
    if (worksCount === 0) return '#E5E7EB'; // ç°è‰² - æ— ä½œå“

    switch (level) {
      case 1: return '#3B82F6'; // è“è‰² - ä¸€çº§èŠ‚ç‚¹
      case 2: return '#10B981'; // ç»¿è‰² - äºŒçº§èŠ‚ç‚¹
      case 3: return '#F59E0B'; // é»„è‰² - ä¸‰çº§èŠ‚ç‚¹
      default: return '#6B7280'; // ç°è‰² - å…¶ä»–
    }
  };

  // è·å–èŠ‚ç‚¹å¤§å°
  const getNodeSize = (level: number, worksCount: number) => {
    const baseSize = 20;
    const levelMultiplier = level === 1 ? 1.5 : level === 2 ? 1.2 : 1;
    const worksMultiplier = Math.min(1 + worksCount * 0.1, 2);
    return baseSize * levelMultiplier * worksMultiplier;
  };

  // å¤„ç†èŠ‚ç‚¹ç‚¹å‡»
  const handleNodeClick = (node: KnowledgeNode) => {
    setSelectedNode(node);
    setViewMode('detail');
    onNodeClick?.(node.id);
  };

  // å¤„ç†è§¦æ‘¸å¼€å§‹
  const handleTouchStart = (e: React.TouchEvent) => {
    if (e.touches.length === 1) {
      setIsDragging(true);
      setLastTouch({
        x: e.touches[0].clientX,
        y: e.touches[0].clientY
      });
