export default function OfflineIndicator() {
  const [isOnline, setIsOnline] = useState(true);
  const [showOfflineMessage, setShowOfflineMessage] = useState(false);
  const [hasBeenOffline, setHasBeenOffline] = useState(false);
  const [showUpdatePrompt, setShowUpdatePrompt] = useState(false);
  const [swStatus, setSWStatus] = useState<'installing' | 'waiting' | 'active' | 'none'>('none');
  const { isMobile } = useResponsive();

  // é”®ç›˜å¿«æ·é”®æ”¯æŒ
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Escape' || (e.ctrlKey && e.key === 'x')) {
        setShowUpdatePrompt(false);
        setShowOfflineMessage(false);
        setSWStatus('none');
        localStorage.setItem('sw-update-dismissed', 'permanent');
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  useEffect(() => {
    // åˆå§‹åŒ–ç½‘ç»œçŠ¶æ€
    setIsOnline(navigator.onLine);

    const handleOnline = () => {
      setIsOnline(true);
      if (hasBeenOffline) {
        // æ˜¾ç¤ºé‡æ–°è¿æ¥æ¶ˆæ¯
        setShowOfflineMessage(true);
        setTimeout(() => setShowOfflineMessage(false), 3000);
      }
    };

    const handleOffline = () => {
      setIsOnline(false);
      setHasBeenOffline(true);
      setShowOfflineMessage(true);
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, [hasBeenOffline]);

  // æ£€æŸ¥Service WorkerçŠ¶æ€

  useEffect(() => {
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.ready.then((registration) => {
        if (registration.installing) {
          setSWStatus('installing');
        } else if (registration.waiting) {
          setSWStatus('waiting');
          // æ£€æŸ¥æ˜¯å¦åº”è¯¥æ˜¾ç¤ºæ›´æ–°æç¤º
          const lastDismissed = localStorage.getItem('sw-update-dismissed');
          if (lastDismissed === 'permanent') {
            setShowUpdatePrompt(false);
          } else if (!lastDismissed || lastDismissed === 'false') {
            setShowUpdatePrompt(true);
          } else {
            const dismissedTime = parseInt(lastDismissed);
            if (!isNaN(dismissedTime)) {
              const now = Date.now();
              const hoursSinceDismissed = (now - dismissedTime) / (1000 * 60 * 60);
              if (hoursSinceDismissed > 24) {
                setShowUpdatePrompt(true);
              }
            } else {
              setShowUpdatePrompt(true);
            }
          }
        } else if (registration.active) {
          setSWStatus('active');
        }
      });

      // ç›‘å¬Service WorkerçŠ¶æ€å˜åŒ–
      navigator.serviceWorker.addEventListener('controllerchange', () => {
        setSWStatus('active');
        setShowUpdatePrompt(false);
      });
    }
  }, []);

  const handleDismiss = () => {
    setShowOfflineMessage(false);
  };

  const handleRefresh = () => {
    window.location.reload();
  };

  const handleUpdateDismiss = () => {
    setShowUpdatePrompt(false);
    setSWStatus('none');
    localStorage.setItem('sw-update-dismissed', Date.now().toString());
  };

  const handleUpdateLater = () => {
    setShowUpdatePrompt(false);
    localStorage.setItem('sw-update-dismissed', Date.now().toString());
  };

  const handleNeverShow = () => {
    setShowUpdatePrompt(false);
    setSWStatus('none');
    localStorage.setItem('sw-update-dismissed', 'permanent');
  };

  return (
    <>
      {/* ç½‘ç»œçŠ¶æ€æŒ‡ç¤ºå™¨ */}
      <AnimatePresence>
        {showOfflineMessage && (
          <motion.div
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            transition={{ type: 'spring', stiffness: 300, damping: 30 }}
            className={`fixed top-4 left-4 right-4 z-50 ${isMobile ? 'top-16' : 'top-4'}`}
          >
            <div className={`
              mobile-card border-l-4 shadow-lg
              ${isOnline 
                ? 'bg-green-50 border-green-500' 
                : 'bg-orange-50 border-orange-500'
              }
            `}>
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className={`
                    w-8 h-8 rounded-full flex items-center justify-center
                    ${isOnline ? 'bg-green-100' : 'bg-orange-100'}
                  `}>
                    {isOnline ? (
                      <svg className="w-4 h-4 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.111 16.404a5.5 5.5 0 017.778 0M12 20h.01m-7.08-7.071c3.904-3.905 10.236-3.905 14.141 0M1.394 9.393c5.857-5.857 15.355-5.857 21.213 0" />
                      </svg>
                    ) : (
                      <svg className="w-4 h-4 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18.364 5.636l-12.728 12.728m0 0L12 12m-6.364 6.364L12 12m6.364-6.364L12 12" />
                      </svg>
                    )}
                  </div>
                  <div>
                    <h3 className={`text-sm font-medium ${
                      isOnline ? 'text-green-800' : 'text-orange-800'
                    }`}>
                      {isOnline ? 'ç½‘ç»œå·²è¿æ¥' : 'ç½‘ç»œè¿æ¥ä¸­æ–­'}
                    </h3>
                    <p className={`text-xs ${
                      isOnline ? 'text-green-600' : 'text-orange-600'
                    }`}>
                      {isOnline 
                        ? 'æ‚¨ç°åœ¨å¯ä»¥æ­£å¸¸ä½¿ç”¨æ‰€æœ‰åŠŸèƒ½' 
                        : 'éƒ¨åˆ†åŠŸèƒ½å¯èƒ½æ— æ³•ä½¿ç”¨ï¼Œå·²ä¿å­˜çš„å†…å®¹ä»å¯è®¿é—®'
                      }
                    </p>
                  </div>
                </div>
                <button
                  onClick={handleDismiss}
                  className={`text-xs px-2 py-1 rounded transition-colors ${
                    isOnline 
                      ? 'text-green-600 hover:text-green-800' 
                      : 'text-orange-600 hover:text-orange-800'
                  }`}
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Service Workeræ›´æ–°æç¤º */}
      <AnimatePresence>
        {swStatus === 'waiting' && showUpdatePrompt && (
          <motion.div
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 100 }}
            className="fixed bottom-20 left-4 right-4 z-50"
          >
            <div className="mobile-card bg-blue-50 border border-blue-200">
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center space-x-3">
                  <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center flex-shrink-0">
                    <svg className="w-4 h-4 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                    </svg>
                  </div>
                  <div className="flex-1">
                    <h3 className="text-sm font-medium text-blue-800 mb-1">
                      åº”ç”¨æ›´æ–°å¯ç”¨
                    </h3>
                    <p className="text-xs text-blue-600">
                      å‘ç°æ–°ç‰ˆæœ¬ï¼Œåˆ·æ–°é¡µé¢ä»¥è·å¾—æœ€æ–°åŠŸèƒ½
                    </p>
                  </div>
                </div>
                <button
                  onClick={handleUpdateDismiss}
                  className="text-blue-600 hover:text-blue-800 transition-colors p-1"
