          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
        </svg>
      );
    default:
      return null;
  }
};

/**
 * å•ä¸ªToastç»„ä»¶
 */
const Toast: React.FC<{ 
  toast: ToastItem; 
  onRemove: (id: string) => void;
}> = ({ toast: toastItem, onRemove }) => {
  const [isVisible, setIsVisible] = useState(false);
  const [isLeaving, setIsLeaving] = useState(false);

  useEffect(() => {
    // è¿›å…¥åŠ¨ç”»
    const timer = setTimeout(() => setIsVisible(true), 10);
    return () => clearTimeout(timer);
  }, []);

  const handleClose = () => {
    setIsLeaving(true);
    setTimeout(() => {
      onRemove(toastItem.id);
    }, 300);
  };

  const getToastStyles = () => {
    const baseStyles = "max-w-sm w-full bg-white shadow-lg rounded-lg pointer-events-auto ring-1 ring-black ring-opacity-5 overflow-hidden transition-all duration-300 transform";

    if (isLeaving) {
      return `${baseStyles} translate-x-full opacity-0`;
    }

    if (isVisible) {
      return `${baseStyles} translate-x-0 opacity-100`;
    }

    return `${baseStyles} translate-x-full opacity-0`;
  };

  const getBorderColor = () => {
    switch (toastItem.type) {
      case 'error': return 'border-l-red-500';
      case 'warning': return 'border-l-yellow-500';
      case 'success': return 'border-l-green-500';
      case 'info': return 'border-l-blue-500';
      default: return 'border-l-gray-500';
    }
  };

  return (
    <div className={`${getToastStyles()} border-l-4 ${getBorderColor()}`}>
      <div className="p-4">
        <div className="flex items-start">
          <div className="flex-shrink-0">
            <ToastIcon type={toastItem.type} />
          </div>
          <div className="ml-3 w-0 flex-1">
            {toastItem.title && (
              <p className="text-sm font-medium text-gray-900 mb-1">
                {toastItem.title}
              </p>
            )}
            <p className="text-sm text-gray-700">
              {toastItem.message}
            </p>
            {toastItem.action && (
              <div className="mt-3">
                <button
                  onClick={toastItem.action.onClick}
                  className="text-sm font-medium text-blue-600 hover:text-blue-500"
                >
                  {toastItem.action.label}
                </button>
              </div>
            )}
          </div>
          <div className="ml-4 flex-shrink-0 flex">
            <button
              onClick={handleClose}
              className="bg-white rounded-md inline-flex text-gray-400 hover:text-gray-500 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              <span className="sr-only">å…³é—­</span>
              <svg className="h-5 w-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

/**
 * Toastå®¹å™¨ç»„ä»¶
 */
export const ToastContainer: React.FC = () => {
  const [toasts, setToasts] = useState<ToastItem[]>([]);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
    const unsubscribe = toast.subscribe(setToasts);
    return unsubscribe;
  }, []);

  if (!mounted) {
    return null;
  }

  return createPortal(
    <div className="fixed inset-0 flex items-end justify-center px-4 py-6 pointer-events-none sm:p-6 sm:items-start sm:justify-end z-50">
      <div className="w-full flex flex-col items-center space-y-4 sm:items-end">
        {toasts.map((toastItem) => (
          <Toast
            key={toastItem.id}
            toast={toastItem}
            onRemove={toast.remove.bind(toast)}
          />
        ))}
      </div>
    </div>,
    document.body
  );
};

/**
 * Toast Hook
 */
export function useToast() {
  return {
    show: toast.show.bind(toast),
    error: toast.error.bind(toast),
    warning: toast.warning.bind(toast),
    success: toast.success.bind(toast),
    info: toast.info.bind(toast),
    remove: toast.remove.bind(toast),
    clear: toast.clear.bind(toast)
  };
}

/**
 * é”™è¯¯Toast Hook
 */
export function useErrorToast() {
  const { error, warning } = useToast();

  const showError = (errorObj: Error | string, options?: {
    title?: string;
    duration?: number;
    action?: ToastItem['action'];
  }) => {
    const message = typeof errorObj === 'string' ? errorObj : errorObj.message;
    return error(message, {
      title: options?.title || 'é”™è¯¯',
      duration: options?.duration,
      action: options?.action
    });
  };

  const showNetworkError = (retryFn?: () => void) => {
    return error('ç½‘ç»œè¿æ¥å¤±è´¥ï¼Œè¯·æ£€æŸ¥ç½‘ç»œåé‡è¯•', {
      title: 'ç½‘ç»œé”™è¯¯',
      duration: 8000,
      action: retryFn ? {
        label: 'é‡è¯•',
        onClick: retryFn
      } : undefined
    });
  };

  const showValidationError = (message: string) => {
    return warning(message, {
      title: 'è¾“å…¥é”™è¯¯',
      duration: 6000
    });
  };

  return {
    showError,
    showNetworkError,
    showValidationError
  };
}

