/**
 * APIé”™è¯¯å¤„ç†Hook
 */

import { useState, useCallback, useEffect } from 'react';
import { ApiError, ApiClient } from '@/lib/api/client';
import { RetryManager, DEFAULT_RETRY_STRATEGY, RetryStrategy } from '@/lib/api/retry';
import { useErrorToast } from '@/components/ui/ErrorToast';
import { logger } from '@/lib/logging/logger';

/**
 * APIé”™è¯¯å¤„ç†Hooké…ç½®
 */
interface UseApiErrorOptions {
  showToast?: boolean;
  retryStrategy?: Partial<RetryStrategy>;
  onError?: (error: ApiError) => void;
  onRetry?: (attempt: number, error: ApiError) => void;
  onSuccess?: () => void;
}

/**
 * APIé”™è¯¯çŠ¶æ€
 */
interface ApiErrorState {
  error: ApiError | null;
  isError: boolean;
  isLoading: boolean;
  retryCount: number;
  canRetry: boolean;
}

/**
 * APIé”™è¯¯å¤„ç†Hook
 */
export function useApiError(options: UseApiErrorOptions = {}) {
  const {
    showToast = true,
    retryStrategy,
    onError,
    onRetry,
    onSuccess
  } = options;

  const [state, setState] = useState<ApiErrorState>({
    error: null,
    isError: false,
    isLoading: false,
    retryCount: 0,
    canRetry: false
  });

  const errorToast = useErrorToast();
  const retryManager = new RetryManager({ ...DEFAULT_RETRY_STRATEGY, ...retryStrategy });

  /**
   * å¤„ç†APIé”™è¯¯
   */
  const handleError = useCallback((error: ApiError) => {
    setState(prev => ({
      ...prev,
      error,
      isError: true,
      isLoading: false,
      canRetry: error.isRetryable()
    }));

    // æ˜¾ç¤ºToasté€šçŸ¥
    if (showToast) {
      if (error.isNetworkError()) {
        errorToast.showNetworkError();
      } else if (error.isClientError()) {
        errorToast.showValidationError(error.message);
      } else {
        errorToast.showError(error);
      }
    }

    // è®°å½•é”™è¯¯æ—¥å¿—
    logger.error('API Error handled by useApiError', error, {
      metadata: {
        status: error.status,
        code: error.code,
        traceId: error.traceId,
        retryable: error.isRetryable()
      }
    });

    // è°ƒç”¨é”™è¯¯å›è°ƒ
    if (onError) {
      onError(error);
    }
  }, [showToast, errorToast, onError]);

  /**
   * æ¸…é™¤é”™è¯¯
   */
  const clearError = useCallback(() => {
    setState({
      error: null,
      isError: false,
      isLoading: false,
      retryCount: 0,
      canRetry: false
    });
  }, []);

  /**
   * æ‰§è¡ŒAPIè¯·æ±‚ï¼ˆå¸¦é”™è¯¯å¤„ç†ï¼‰
   */
  const executeRequest = useCallback(async <T>(
    requestFn: () => Promise<T>
  ): Promise<T | null> => {
    setState(prev => ({
      ...prev,
      isLoading: true,
      error: null,
      isError: false
    }));

    try {
      const result = await retryManager.execute(requestFn, {
        retryCondition: (error: ApiError, attempt: number) => {
          setState(prev => ({
            ...prev,
            retryCount: attempt
          }));

          if (onRetry) {
            onRetry(attempt, error);
          }

          return (retryStrategy?.retryCondition || DEFAULT_RETRY_STRATEGY.retryCondition)(error, attempt);
        }
      });

      if (result.success) {
        setState(prev => ({
          ...prev,
          isLoading: false,
          retryCount: result.attempts - 1
        }));

        if (onSuccess) {
          onSuccess();
        }

        return result.data!;
      } else {
        handleError(result.error!);
        return null;
      }
    } catch (error) {
      const apiError = error instanceof ApiError ? error : new ApiError(
        error instanceof Error ? error.message : 'Unknown error',
        0
      );

      handleError(apiError);
      return null;
    }
  }, [retryManager, handleError, onRetry, onSuccess, retryStrategy]);

  /**
   * é‡è¯•æœ€åçš„è¯·æ±‚
   */
  const retry = useCallback(async <T>(
    requestFn: () => Promise<T>
  ): Promise<T | null> => {
    if (!state.canRetry) {
      return null;
    }

    return executeRequest(requestFn);
  }, [state.canRetry, executeRequest]);

  return {
    // çŠ¶æ€
    error: state.error,
    isError: state.isError,
    isLoading: state.isLoading,
    retryCount: state.retryCount,
    canRetry: state.canRetry,

    // æ–¹æ³•
    handleError,
    clearError,
    executeRequest,
    retry
  };
}

/**
 * APIè¯·æ±‚Hook
 */
export function useApiRequest<T = any>(options: UseApiErrorOptions = {}) {
  const apiError = useApiError(options);
  const [data, setData] = useState<T | null>(null);

  /**
   * æ‰§è¡Œè¯·æ±‚
   */
  const execute = useCallback(async (
    requestFn: () => Promise<T>
  ): Promise<void> => {
    const result = await apiError.executeRequest(requestFn);
    if (result !== null) {
      setData(result);
    }
  }, [apiError]);

  /**
   * é‡è¯•è¯·æ±‚
   */
  const retry = useCallback(async (
    requestFn: () => Promise<T>
  ): Promise<void> => {
    const result = await apiError.retry(requestFn);
    if (result !== null) {
      setData(result);
    }
  }, [apiError]);

  /**
   * é‡ç½®çŠ¶æ€
   */
  const reset = useCallback(() => {
    setData(null);
    apiError.clearError();
  }, [apiError]);

  return {
    // æ•°æ®
    data,

    // çŠ¶æ€
    error: apiError.error,
    isError: apiError.isError,
    isLoading: apiError.isLoading,
    retryCount: apiError.retryCount,
    canRetry: apiError.canRetry,

    // æ–¹æ³•
    execute,
    retry,
    reset,
    clearError: apiError.clearError
  };
}

/**
 * ç‰¹å®šAPIç«¯ç‚¹Hook
 */
export function useApiEndpoint<T = any>(
  endpoint: string,
  apiClient: ApiClient,
  options: UseApiErrorOptions = {}
) {
  const apiRequest = useApiRequest<T>(options);

  /**
   * GETè¯·æ±‚
   */
  const get = useCallback(async (params?: Record<string, any>) => {
    const url = params ? `${endpoint}?${new URLSearchParams(params).toString()}` : endpoint;
    await apiRequest.execute(() => apiClient.get<T>(url).then(res => res.data!));
  }, [endpoint, apiClient, apiRequest]);

  /**
   * POSTè¯·æ±‚
   */
  const post = useCallback(async (data?: any) => {
    await apiRequest.execute(() => apiClient.post<T>(endpoint, data).then(res => res.data!));
  }, [endpoint, apiClient, apiRequest]);

  /**
   * PUTè¯·æ±‚
   */
  const put = useCallback(async (data?: any) => {
    await apiRequest.execute(() => apiClient.put<T>(endpoint, data).then(res => res.data!));
  }, [endpoint, apiClient, apiRequest]);

  /**
   * DELETEè¯·æ±‚
   */
  const del = useCallback(async () => {
    await apiRequest.execute(() => apiClient.delete<T>(endpoint).then(res => res.data!));
  }, [endpoint, apiClient, apiRequest]);

  /**
   * PATCHè¯·æ±‚
   */
  const patch = useCallback(async (data?: any) => {
