'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { logger } from '@/lib/logging/logger';

/**
 * æ•°æ®æ‡’åŠ è½½é…ç½®
 */
export interface DataLazyLoadConfig {
  // ç¼“å­˜é”®
  cacheKey?: string;
  // ç¼“å­˜æ—¶é—´ï¼ˆæ¯«ç§’ï¼‰
  cacheTTL?: number;
  // æ˜¯å¦å¯ç”¨ç¼“å­˜
  enableCache?: boolean;
  // é‡è¯•æ¬¡æ•°
  retries?: number;
  // é‡è¯•å»¶è¿Ÿï¼ˆæ¯«ç§’ï¼‰
  retryDelay?: number;
  // è¶…æ—¶æ—¶é—´ï¼ˆæ¯«ç§’ï¼‰
  timeout?: number;
  // æ˜¯å¦åœ¨ç»„ä»¶æŒ‚è½½æ—¶è‡ªåŠ¨åŠ è½½
  autoLoad?: boolean;
  // ä¾èµ–é¡¹ï¼ˆå½“ä¾èµ–é¡¹å˜åŒ–æ—¶é‡æ–°åŠ è½½ï¼‰
  dependencies?: any[];
  // é”™è¯¯å¤„ç†
  onError?: (error: Error) => void;
  // æˆåŠŸå›è°ƒ
  onSuccess?: (data: any) => void;
  // åŠ è½½å¼€å§‹å›è°ƒ
  onLoadStart?: () => void;
}

/**
 * æ•°æ®æ‡’åŠ è½½çŠ¶æ€
 */
export interface DataLazyLoadState<T> {
  data: T | null;
  isLoading: boolean;
  error: Error | null;
  isLoaded: boolean;
  lastUpdated: Date | null;
  retryCount: number;
}

/**
 * ç¼“å­˜é¡¹
 */
interface CacheItem<T> {
  data: T;
  timestamp: number;
  ttl: number;
}

/**
 * å…¨å±€ç¼“å­˜
 */
const globalCache = new Map<string, CacheItem<any>>();

/**
 * æ•°æ®æ‡’åŠ è½½Hook
 */
export function useDataLazyLoad<T>(
  fetchFn: () => Promise<T>,
  config: DataLazyLoadConfig = {}
): [DataLazyLoadState<T>, () => void, () => void] {
  const {
    cacheKey,
    cacheTTL = 5 * 60 * 1000, // 5åˆ†é’Ÿ
    enableCache = true,
    retries = 3,
    retryDelay = 1000,
    timeout = 10000,
    autoLoad = false,
    dependencies = [],
    onError,
    onSuccess,
    onLoadStart
  } = config;

  const [state, setState] = useState<DataLazyLoadState<T>>({
    data: null,
    isLoading: false,
    error: null,
    isLoaded: false,
    lastUpdated: null,
    retryCount: 0
  });

  const abortControllerRef = useRef<AbortController | null>(null);
  const mountedRef = useRef(true);

  // ä»ç¼“å­˜è·å–æ•°æ®
  const getFromCache = useCallback((): T | null => {
    if (!enableCache || !cacheKey) {
      return null;
    }

    const cached = globalCache.get(cacheKey);
    if (!cached) {
      return null;
    }

    const now = Date.now();
    if (now - cached.timestamp > cached.ttl) {
      globalCache.delete(cacheKey);
      return null;
    }

    return cached.data;
  }, [enableCache, cacheKey]);

  // è®¾ç½®ç¼“å­˜
  const setCache = useCallback((data: T) => {
    if (!enableCache || !cacheKey) {
      return;
    }

    globalCache.set(cacheKey, {
      data,
      timestamp: Date.now(),
      ttl: cacheTTL
    });
  }, [enableCache, cacheKey, cacheTTL]);

  // æ‰§è¡ŒåŠ è½½
  const load = useCallback(async (retryCount = 0) => {
    if (!mountedRef.current) {
      return;
    }

    // æ£€æŸ¥ç¼“å­˜
    const cachedData = getFromCache();
    if (cachedData && retryCount === 0) {
      setState({
        data: cachedData,
        isLoading: false,
        error: null,
        isLoaded: true,
        lastUpdated: new Date(),
        retryCount: 0
      });
      onSuccess?.(cachedData);
      return;
    }

    // å–æ¶ˆä¹‹å‰çš„è¯·æ±‚
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }

    abortControllerRef.current = new AbortController();
    const { signal } = abortControllerRef.current;

    setState(prev => ({
      ...prev,
      isLoading: true,
      error: null,
      retryCount
    }));

    onLoadStart?.();

    try {
      // è®¾ç½®è¶…æ—¶
      const timeoutPromise = new Promise<never>((_, reject) => {
        setTimeout(() => reject(new Error('Request timeout')), timeout);
      });

      const fetchPromise = fetchFn();
      const data = await Promise.race([fetchPromise, timeoutPromise]);

      if (!mountedRef.current || signal.aborted) {
        return;
      }

      // è®¾ç½®ç¼“å­˜
      setCache(data);

      setState({
        data,
        isLoading: false,
        error: null,
        isLoaded: true,
        lastUpdated: new Date(),
        retryCount: 0
      });

      onSuccess?.(data);

      logger.debug('Data lazy load completed', {
        cacheKey,
        retryCount,
        dataSize: JSON.stringify(data).length
      });

    } catch (error) {
      if (!mountedRef.current || signal.aborted) {
        return;
      }

      const err = error instanceof Error ? error : new Error(String(error));

      logger.error('Data lazy load failed', err, {
        cacheKey,
        retryCount,
        maxRetries: retries
      });

      // é‡è¯•é€»è¾‘
      if (retryCount < retries) {
        setTimeout(() => {
          load(retryCount + 1);
        }, retryDelay * (retryCount + 1));
        return;
      }

      setState(prev => ({
        ...prev,
        isLoading: false,
        error: err,
        retryCount
      }));

      onError?.(err);
    }
  }, [fetchFn, getFromCache, setCache, timeout, retries, retryDelay, onLoadStart, onSuccess, onError, cacheKey]);

  // æ‰‹åŠ¨é‡æ–°åŠ è½½
  const reload = useCallback(() => {
    // æ¸…é™¤ç¼“å­˜
    if (cacheKey) {
      globalCache.delete(cacheKey);
    }
    load();
  }, [load, cacheKey]);

  // è‡ªåŠ¨åŠ è½½
  useEffect(() => {
    if (autoLoad) {
      load();
    }
  }, [autoLoad, load]);

  // ä¾èµ–é¡¹å˜åŒ–æ—¶é‡æ–°åŠ è½½
  useEffect(() => {
    if (dependencies.length > 0 && state.isLoaded) {
      reload();
    }
  }, dependencies); // eslint-disable-line react-hooks/exhaustive-deps

  // æ¸…ç†
  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, []);

  return [state, load, reload];
}

/**
 * åˆ†é¡µæ•°æ®æ‡’åŠ è½½Hook
 */
export function usePaginatedDataLazyLoad<T>(
  fetchFn: (page: number, pageSize: number) => Promise<{ data: T[]; total: number; hasMore: boolean }>,
  pageSize: number = 20,
  config: Omit<DataLazyLoadConfig, 'autoLoad'> = {}
) {
  const [allData, setAllData] = useState<T[]>([]);
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [hasMore, setHasMore] = useState(true);

  const paginatedFetchFn = useCallback(async () => {
    const result = await fetchFn(page, pageSize);
    return result;
  }, [fetchFn, page, pageSize]);

  const [state, load, reload] = useDataLazyLoad(paginatedFetchFn, {
    ...config,
    cacheKey: config.cacheKey ? `${config.cacheKey}-page-${page}` : undefined,
    onSuccess: (result) => {
      if (page === 1) {
        setAllData(result.data);
      } else {
        setAllData(prev => [...prev, ...result.data]);
      }
      setTotal(result.total);
      setHasMore(result.hasMore);
      config.onSuccess?.(result);
    }
  });

  const loadMore = useCallback(() => {
    if (hasMore && !state.isLoading) {
      setPage(prev => prev + 1);
    }
  }, [hasMore, state.isLoading]);

  const reset = useCallback(() => {
    setAllData([]);
    setPage(1);
    setTotal(0);
    setHasMore(true);
  }, []);

  // å½“é¡µç å˜åŒ–æ—¶åŠ è½½æ•°æ®
  useEffect(() => {
    if (page > 1) {
      load();
    }
  }, [page, load]);

  return {
    data: allData,
    total,
    hasMore,
    page,
    state,
    loadMore,
    reset,
    reload: () => {
      reset();
      reload();
    }
  };
}

/**
 * æœç´¢æ•°æ®æ‡’åŠ è½½Hook
 */
export function useSearchDataLazyLoad<T>(
  searchFn: (query: string) => Promise<T[]>,
  config: DataLazyLoadConfig & {
    debounceDelay?: number;
    minQueryLength?: number;
  } = {}
) {
  const {
    debounceDelay = 300,
    minQueryLength = 1,
    ...lazyLoadConfig
  } = config;

  const [query, setQuery] = useState('');
  const [debouncedQuery, setDebouncedQuery] = useState('');
  const debounceTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const searchFetchFn = useCallback(async () => {
    if (debouncedQuery.length < minQueryLength) {
      return [];
    }
    return await searchFn(debouncedQuery);
  }, [searchFn, debouncedQuery, minQueryLength]);

  const [state, load, reload] = useDataLazyLoad(searchFetchFn, {
    ...lazyLoadConfig,
    cacheKey: lazyLoadConfig.cacheKey ? `${lazyLoadConfig.cacheKey}-search-${debouncedQuery}` : undefined
  });

  // é˜²æŠ–å¤„ç†
  useEffect(() => {
    if (debounceTimeoutRef.current) {
      clearTimeout(debounceTimeoutRef.current);
    }

    debounceTimeoutRef.current = setTimeout(() => {
      setDebouncedQuery(query);
    }, debounceDelay);

    return () => {
      if (debounceTimeoutRef.current) {
        clearTimeout(debounceTimeoutRef.current);
      }
    };
  }, [query, debounceDelay]);

  // å½“æœç´¢è¯å˜åŒ–æ—¶åŠ è½½æ•°æ®
  useEffect(() => {
    if (debouncedQuery.length >= minQueryLength) {
      load();
    }
  }, [debouncedQuery, minQueryLength, load]);

  const search = useCallback((newQuery: string) => {
    setQuery(newQuery);
  }, []);

  const clear = useCallback(() => {
    setQuery('');
    setDebouncedQuery('');
  }, []);

  return {
    query,
    debouncedQuery,
    data: state.data || [],
    state,
    search,
    clear,
    reload
  };
}

/**
 * æ¡ä»¶æ•°æ®æ‡’åŠ è½½Hook
 */
export function useConditionalDataLazyLoad<T>(
  fetchFn: () => Promise<T>,
  condition: boolean | (() => boolean),
  config: DataLazyLoadConfig = {}
) {
  const shouldLoad = typeof condition === 'function' ? condition() : condition;

  const conditionalFetchFn = useCallback(async () => {
    if (!shouldLoad) {
      throw new Error('Condition not met');
    }
    return await fetchFn();
  }, [fetchFn, shouldLoad]);

  const [state, load, reload] = useDataLazyLoad(conditionalFetchFn, {
    ...config,
    autoLoad: config.autoLoad && shouldLoad
  });

  // å½“æ¡ä»¶å˜åŒ–æ—¶é‡æ–°è¯„ä¼°
  useEffect(() => {
    if (shouldLoad && !state.isLoaded && !state.isLoading) {
      load();
    }
  }, [shouldLoad, state.isLoaded, state.isLoading, load]);

  return [state, load, reload] as const;
}

/**
 * ç¼“å­˜å·¥å…·å‡½æ•°
 */
export const cacheUtils = {
  /**
   * æ¸…é™¤æ‰€æœ‰ç¼“å­˜
   */
  clearAll: () => {
    globalCache.clear();
    logger.debug('All data lazy load cache cleared');
  },

  /**
   * æ¸…é™¤æŒ‡å®šé”®çš„ç¼“å­˜
   */
  clear: (key: string) => {
    globalCache.delete(key);
    logger.debug('Data lazy load cache cleared', { key });
  },

  /**
   * è·å–ç¼“å­˜ç»Ÿè®¡
   */
  getStats: () => {
    const now = Date.now();
    let validCount = 0;
    let expiredCount = 0;
    let totalSize = 0;

    for (const [key, item] of globalCache.entries()) {
      const isExpired = now - item.timestamp > item.ttl;
      if (isExpired) {
        expiredCount++;
      } else {
        validCount++;
      }
      totalSize += JSON.stringify(item.data).length;
    }

    return {
      totalItems: globalCache.size,
      validItems: validCount,
      expiredItems: expiredCount,
      totalSize
    };
  },

  /**
   * æ¸…ç†è¿‡æœŸç¼“å­˜
   */
  cleanup: () => {
    const now = Date.now();
    let cleanedCount = 0;

    for (const [key, item] of globalCache.entries()) {
      if (now - item.timestamp > item.ttl) {
        globalCache.delete(key);
        cleanedCount++;
      }
    }

    logger.debug('Expired data lazy load cache cleaned', { cleanedCount });
    return cleanedCount;
  }
};

