import { useState, useEffect } from 'react';

/**
 * é˜²æŠ–Hook - å»¶è¿Ÿæ›´æ–°å€¼ç›´åˆ°æŒ‡å®šæ—¶é—´å†…æ²¡æœ‰æ–°çš„æ›´æ–°
 * @param value - éœ€è¦é˜²æŠ–çš„å€¼
 * @param delay - å»¶è¿Ÿæ—¶é—´ï¼ˆæ¯«ç§’ï¼‰
 * @returns é˜²æŠ–åçš„å€¼
 */
export function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

/**
 * é˜²æŠ–å›è°ƒHook - å»¶è¿Ÿæ‰§è¡Œå›è°ƒå‡½æ•°
 * @param callback - éœ€è¦é˜²æŠ–çš„å›è°ƒå‡½æ•°
 * @param delay - å»¶è¿Ÿæ—¶é—´ï¼ˆæ¯«ç§’ï¼‰
 * @param deps - ä¾èµ–æ•°ç»„
 * @returns é˜²æŠ–åçš„å›è°ƒå‡½æ•°
 */
export function useDebouncedCallback<T extends (...args: any[]) => any>(
  callback: T,
  delay: number,
  deps: React.DependencyList = []
): T {
  const [debouncedCallback, setDebouncedCallback] = useState<T>(() => callback);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedCallback(() => callback);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [callback, delay, ...deps]);

  return debouncedCallback;
}

'use client'

import { useCallback, useState } from 'react'

// é”™è¯¯ç±»å‹å®šä¹‰
export interface AppError {
  message: string
  code?: string
  status?: number
  details?: any
  timestamp?: Date
}

// APIé”™è¯¯ç±»å‹
export interface ApiError extends AppError {
  endpoint?: string
  method?: string
  requestId?: string
}

// ç½‘ç»œé”™è¯¯ç±»å‹
export interface NetworkError extends AppError {
  isNetworkError: true
  isOffline?: boolean
}

// é”™è¯¯å¤„ç†å™¨é…ç½®
export interface ErrorHandlerConfig {
  showToast?: boolean
  logError?: boolean
  retryable?: boolean
  fallbackMessage?: string
}

// åŸºç¡€é”™è¯¯å¤„ç†Hook
export function useErrorHandler(config: ErrorHandlerConfig = {}) {
  const [error, setError] = useState<AppError | null>(null)
  const [isLoading, setIsLoading] = useState(false)

  const handleError = useCallback((error: Error | AppError | string, context?: string) => {
    const appError: AppError = typeof error === 'string' 
      ? { message: error, timestamp: new Date() }
      : error instanceof Error 
        ? { message: error.message, details: error.stack, timestamp: new Date() }
        : { ...error, timestamp: error.timestamp || new Date() }

    // è®¾ç½®é”™è¯¯çŠ¶æ€
    setError(appError)
    setIsLoading(false)

    // æ—¥å¿—è®°å½•
    if (config.logError !== false) {
      console.error(`[ErrorHandler${context ? ` - ${context}` : ''}]:`, appError)
    }

    // è¿™é‡Œå¯ä»¥æ·»åŠ é”™è¯¯ä¸ŠæŠ¥é€»è¾‘
    // reportError(appError, context)

    return appError
  }, [config.logError])

  const clearError = useCallback(() => {
    setError(null)
  }, [])

  const retry = useCallback(async (retryFn: () => Promise<any>) => {
    if (!config.retryable) {
      console.warn('Retry attempted on non-retryable error handler')
      return
    }

    setIsLoading(true)
    setError(null)

    try {
      const result = await retryFn()
      setIsLoading(false)
      return result
    } catch (error) {
      handleError(error as Error, 'retry')
      return null
    }
  }, [config.retryable, handleError])

  return {
    error,
    isLoading,
    handleError,
    clearError,
    retry,
    hasError: !!error
  }
}

// å…¨å±€é”™è¯¯å¤„ç†Hook
export function useGlobalErrorHandler() {
  const [globalErrors, setGlobalErrors] = useState<AppError[]>([])

  const addGlobalError = useCallback((error: AppError) => {
    setGlobalErrors(prev => [...prev, error])

    // è‡ªåŠ¨æ¸…é™¤é”™è¯¯ï¼ˆ5ç§’åï¼‰
    setTimeout(() => {
      setGlobalErrors(prev => prev.filter(e => e !== error))
    }, 5000)
  }, [])

  const removeGlobalError = useCallback((error: AppError) => {
    setGlobalErrors(prev => prev.filter(e => e !== error))
  }, [])

  const clearAllErrors = useCallback(() => {
    setGlobalErrors([])
  }, [])

  return {
    globalErrors,
    addGlobalError,
    removeGlobalError,
    clearAllErrors,
    hasGlobalErrors: globalErrors.length > 0
  }
}

// APIé”™è¯¯å¤„ç†Hook
export function useApiErrorHandler() {
  const { handleError, ...rest } = useErrorHandler({ 
    showToast: true, 
    logError: true, 
    retryable: true 
  })

  const handleApiError = useCallback((error: any, endpoint?: string, method?: string) => {
    let apiError: ApiError

    if (error.response) {
      // HTTPé”™è¯¯å“åº”
      apiError = {
        message: error.response.data?.message || error.message || 'APIè¯·æ±‚å¤±è´¥',
        code: error.response.data?.code || 'API_ERROR',
        status: error.response.status,
        endpoint,
        method,
        details: error.response.data,
        timestamp: new Date()
      }
    } else if (error.request) {
      // ç½‘ç»œé”™è¯¯
      const networkError: NetworkError = {
        message: 'ç½‘ç»œè¿æ¥å¤±è´¥ï¼Œè¯·æ£€æŸ¥ç½‘ç»œè®¾ç½®',
        code: 'NETWORK_ERROR',
        isNetworkError: true,
        isOffline: !navigator.onLine,
        endpoint,
        method,
        timestamp: new Date()
      }
      apiError = networkError
    } else {
      // å…¶ä»–é”™è¯¯
      apiError = {
        message: error.message || 'æœªçŸ¥é”™è¯¯',
        code: 'UNKNOWN_ERROR',
        endpoint,
        method,
        details: error,
        timestamp: new Date()
      }
    }

    return handleError(apiError, `API ${method} ${endpoint}`)
  }, [handleError])

  const handleNetworkError = useCallback(() => {
    const networkError: NetworkError = {
      message: 'ç½‘ç»œè¿æ¥å·²æ–­å¼€ï¼Œè¯·æ£€æŸ¥ç½‘ç»œè®¾ç½®',
      code: 'NETWORK_OFFLINE',
      isNetworkError: true,
      isOffline: true,
      timestamp: new Date()
    }
    return handleError(networkError, 'Network')
  }, [handleError])

  return {
    ...rest,
    handleApiError,
    handleNetworkError
  }
}

// é”™è¯¯é‡è¯•Hook
export function useRetryHandler(maxRetries: number = 3, retryDelay: number = 1000) {
  const [retryCount, setRetryCount] = useState(0)
  const [isRetrying, setIsRetrying] = useState(false)

  const executeWithRetry = useCallback(async <T>(
    operation: () => Promise<T>,
    onError?: (error: Error, attempt: number) => void
  ): Promise<T | null> => {
    setIsRetrying(true)

    for (let attempt = 0; attempt <= maxRetries; attempt++) {
      try {
        const result = await operation()
        setRetryCount(0)
        setIsRetrying(false)
        return result
      } catch (error) {
        setRetryCount(attempt + 1)

        if (onError) {
          onError(error as Error, attempt + 1)
        }

        if (attempt === maxRetries) {
          setIsRetrying(false)
          throw error
        }

        // ç­‰å¾…é‡è¯•å»¶è¿Ÿ
        await new Promise(resolve => setTimeout(resolve, retryDelay * Math.pow(2, attempt)))
      }
    }

    setIsRetrying(false)
    return null
  }, [maxRetries, retryDelay])

  const resetRetry = useCallback(() => {
    setRetryCount(0)
    setIsRetrying(false)
  }, [])

  return {
    retryCount,
    isRetrying,
    executeWithRetry,
    resetRetry,
    canRetry: retryCount < maxRetries
  }
}

