'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { logger } from '@/lib/logging/logger';

/**
 * æ‡’åŠ è½½é€‰é¡¹
 */
export interface UseLazyLoadOptions {
  // è§¦å‘æ¡ä»¶
  trigger?: 'manual' | 'intersection' | 'hover' | 'focus' | 'idle';
  // äº¤å‰è§‚å¯Ÿå™¨é€‰é¡¹
  intersectionOptions?: IntersectionObserverInit;
  // ç©ºé—²å›è°ƒé€‰é¡¹
  idleOptions?: IdleRequestOptions;
  // å»¶è¿Ÿæ—¶é—´ï¼ˆæ¯«ç§’ï¼‰
  delay?: number;
  // æ˜¯å¦åªè§¦å‘ä¸€æ¬¡
  once?: boolean;
  // æ˜¯å¦å¯ç”¨
  enabled?: boolean;
  // é”™è¯¯å¤„ç†
  onError?: (error: Error) => void;
  // åŠ è½½æˆåŠŸå›è°ƒ
  onLoad?: () => void;
  // åŠ è½½å¼€å§‹å›è°ƒ
  onLoadStart?: () => void;
}

/**
 * æ‡’åŠ è½½çŠ¶æ€
 */
export interface LazyLoadState {
  isLoading: boolean;
  isLoaded: boolean;
  error: Error | null;
  hasTriggered: boolean;
}

/**
 * æ‡’åŠ è½½Hook
 */
export function useLazyLoad<T>(
  loadFn: () => Promise<T>,
  options: UseLazyLoadOptions = {}
): [LazyLoadState, () => void, T | null] {
  const {
    trigger = 'manual',
    intersectionOptions,
    idleOptions,
    delay = 0,
    once = true,
    enabled = true,
    onError,
    onLoad,
    onLoadStart
  } = options;

  const [state, setState] = useState<LazyLoadState>({
    isLoading: false,
    isLoaded: false,
    error: null,
    hasTriggered: false
  });

  const [data, setData] = useState<T | null>(null);
  const elementRef = useRef<HTMLElement | null>(null);
  const observerRef = useRef<IntersectionObserver | null>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  // æ‰§è¡ŒåŠ è½½
  const executeLoad = useCallback(async () => {
    if (!enabled || (once && state.hasTriggered) || state.isLoading) {
      return;
    }

    setState(prev => ({
      ...prev,
      isLoading: true,
      error: null,
      hasTriggered: true
    }));

    onLoadStart?.();

    try {
      // æ·»åŠ å»¶è¿Ÿ
      if (delay > 0) {
        await new Promise(resolve => setTimeout(resolve, delay));
      }

      const result = await loadFn();

      setData(result);
      setState(prev => ({
        ...prev,
        isLoading: false,
        isLoaded: true
      }));

      onLoad?.();

      logger.debug('Lazy load completed successfully');

    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));

      setState(prev => ({
        ...prev,
        isLoading: false,
        error: err
      }));

      onError?.(err);

      logger.error('Lazy load failed', err);
    }
  }, [loadFn, enabled, once, state.hasTriggered, state.isLoading, delay, onLoadStart, onLoad, onError]);

  // æ‰‹åŠ¨è§¦å‘åŠ è½½
  const load = useCallback(() => {
    executeLoad();
  }, [executeLoad]);

  // è®¾ç½®äº¤å‰è§‚å¯Ÿå™¨
  useEffect(() => {
    if (trigger === 'intersection' && enabled && !state.hasTriggered) {
      const observer = new IntersectionObserver(
        (entries) => {
          entries.forEach((entry) => {
            if (entry.isIntersecting) {
              executeLoad();
              if (once) {
                observer.disconnect();
              }
            }
          });
        },
        {
          threshold: 0.1,
          rootMargin: '50px',
          ...intersectionOptions
        }
      );

      observerRef.current = observer;

      return () => {
        observer.disconnect();
      };
    }
  }, [trigger, enabled, state.hasTriggered, executeLoad, once, intersectionOptions]);

  // è®¾ç½®ç©ºé—²å›è°ƒ
  useEffect(() => {
    if (trigger === 'idle' && enabled && !state.hasTriggered) {
      if ('requestIdleCallback' in window) {
        const idleId = (window as any).requestIdleCallback(
          () => {
            executeLoad();
          },
          {
            timeout: 5000,
            ...idleOptions
          }
        );

        return () => {
          (window as any).cancelIdleCallback(idleId);
        };
      } else {
        // é™çº§åˆ°setTimeout
        timeoutRef.current = setTimeout(() => {
          executeLoad();
        }, 100);

        return () => {
          if (timeoutRef.current) {
            clearTimeout(timeoutRef.current);
          }
        };
      }
    }
  }, [trigger, enabled, state.hasTriggered, executeLoad, idleOptions]);

  // æ¸…ç†
  useEffect(() => {
    return () => {
      if (observerRef.current) {
        observerRef.current.disconnect();
      }
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  return [state, load, data];
}

