import { useState, useEffect } from 'react'
import { 
  ResponsiveState, 
  BreakpointKey, 
  getBreakpoint,
  ResponsiveValue,
  getResponsiveValue
} from '@/lib/responsive/breakpoints'

// é»˜è®¤å“åº”å¼çŠ¶æ€
const getDefaultState = (): ResponsiveState => {
  // æœåŠ¡ç«¯æ¸²æŸ“æ—¶çš„é»˜è®¤å€¼
  if (typeof window === 'undefined') {
    return {
      isMobile: false,
      isTablet: false,
      isDesktop: true,
      isWide: false,
      currentBreakpoint: 'desktop',
      screenWidth: 1024,
      screenHeight: 768
    }
  }

  const width = window.innerWidth
  const height = window.innerHeight
  const breakpoint = getBreakpoint(width)

  return {
    isMobile: breakpoint === 'mobile',
    isTablet: breakpoint === 'tablet',
    isDesktop: breakpoint === 'desktop',
    isWide: breakpoint === 'wide',
    currentBreakpoint: breakpoint,
    screenWidth: width,
    screenHeight: height
  }
}

export const useResponsive = (): ResponsiveState => {
  const [state, setState] = useState<ResponsiveState>(getDefaultState)

  useEffect(() => {
    const handleResize = () => {
      const width = window.innerWidth
      const height = window.innerHeight
      const breakpoint = getBreakpoint(width)

      setState({
        isMobile: breakpoint === 'mobile',
        isTablet: breakpoint === 'tablet',
        isDesktop: breakpoint === 'desktop',
        isWide: breakpoint === 'wide',
        currentBreakpoint: breakpoint,
        screenWidth: width,
        screenHeight: height
      })
    }

    // åˆå§‹åŒ–æ—¶è®¾ç½®æ­£ç¡®çš„çŠ¶æ€
    handleResize()

    // ç›‘å¬çª—å£å¤§å°å˜åŒ–
    window.addEventListener('resize', handleResize)

    // ç›‘å¬è®¾å¤‡æ–¹å‘å˜åŒ–
    window.addEventListener('orientationchange', handleResize)

    return () => {
      window.removeEventListener('resize', handleResize)
      window.removeEventListener('orientationchange', handleResize)
    }
  }, [])

  return state
}

// å“åº”å¼å€¼Hook
export const useResponsiveValue = <T>(value: ResponsiveValue<T>): T => {
  const { currentBreakpoint } = useResponsive()
  return getResponsiveValue(value, currentBreakpoint)
}

// åª’ä½“æŸ¥è¯¢Hook
export const useMediaQuery = (query: string): boolean => {
  const [matches, setMatches] = useState(false)

  useEffect(() => {
    if (typeof window === 'undefined') return

    const mediaQuery = window.matchMedia(query)
    setMatches(mediaQuery.matches)

    const handleChange = (event: MediaQueryListEvent) => {
      setMatches(event.matches)
    }

    mediaQuery.addEventListener('change', handleChange)
    return () => mediaQuery.removeEventListener('change', handleChange)
  }, [query])

  return matches
}

// ç§»åŠ¨ç«¯æ£€æµ‹Hook
export const useIsMobile = (): boolean => {
  const { isMobile } = useResponsive()
  return isMobile
}

// è§¦æ‘¸è®¾å¤‡æ£€æµ‹Hook
export const useIsTouchDevice = (): boolean => {
  const [isTouchDevice, setIsTouchDevice] = useState(false)

  useEffect(() => {
    if (typeof window === 'undefined') return

    const checkTouchDevice = () => {
      return (
        'ontouchstart' in window ||
        navigator.maxTouchPoints > 0 ||
        // @ts-ignore
        navigator.msMaxTouchPoints > 0
      )
    }

    setIsTouchDevice(checkTouchDevice())
  }, [])

  return isTouchDevice
}

