export const useTouchFeedback = () => {
  const addTouchFeedback = useCallback((element: HTMLElement, intensity: 'light' | 'medium' | 'strong' = 'medium') => {
    const feedbackClass = {
      light: 'scale-98',
      medium: 'scale-95', 
      strong: 'scale-90'
    }[intensity]

    element.style.transform = `scale(${intensity === 'light' ? '0.98' : intensity === 'medium' ? '0.95' : '0.90'})`
    element.style.transition = 'transform 0.1s ease'

    setTimeout(() => {
      element.style.transform = 'scale(1)'
    }, 100)
  }, [])

  const addRippleEffect = useCallback((element: HTMLElement, x: number, y: number) => {
    const ripple = document.createElement('div')
    const rect = element.getBoundingClientRect()
    const size = Math.max(rect.width, rect.height)

    ripple.style.width = ripple.style.height = size + 'px'
    ripple.style.left = (x - rect.left - size / 2) + 'px'
    ripple.style.top = (y - rect.top - size / 2) + 'px'
    ripple.style.position = 'absolute'
    ripple.style.borderRadius = '50%'
    ripple.style.background = 'rgba(255, 255, 255, 0.6)'
    ripple.style.transform = 'scale(0)'
    ripple.style.animation = 'ripple 0.6s linear'
    ripple.style.pointerEvents = 'none'

    element.style.position = 'relative'
    element.style.overflow = 'hidden'
    element.appendChild(ripple)

    setTimeout(() => {
      ripple.remove()
    }, 600)
  }, [])

  return {
    addTouchFeedback,
    addRippleEffect
  }
}

// è™šæ‹Ÿé”®ç›˜é€‚é…Hook
export const useVirtualKeyboard = () => {
  const { useState } = require('react')
  const [keyboardHeight, setKeyboardHeight] = useState(0)
  const [isKeyboardOpen, setIsKeyboardOpen] = useState(false)

  useEffect(() => {
    if (typeof window === 'undefined') return

    const handleResize = () => {
      const viewportHeight = window.visualViewport?.height || window.innerHeight
      const windowHeight = window.innerHeight
      const heightDiff = windowHeight - viewportHeight

      setKeyboardHeight(heightDiff)
      setIsKeyboardOpen(heightDiff > 150) // é”®ç›˜é«˜åº¦é€šå¸¸>150px
    }

    // ç›‘å¬è§†å£å˜åŒ–
    if (window.visualViewport) {
      window.visualViewport.addEventListener('resize', handleResize)
    } else {
      window.addEventListener('resize', handleResize)
    }

    return () => {
      if (window.visualViewport) {
        window.visualViewport.removeEventListener('resize', handleResize)
      } else {
        window.removeEventListener('resize', handleResize)
      }
    }
  }, [])

  return {
    keyboardHeight,
    isKeyboardOpen
  }
}

