import { CustomError } from '@/lib/errors/CustomError';
import { ErrorCode } from '@/lib/errors/types';
import { logger } from '@/lib/logging/logger';
import { reportError, monitoringContext } from '@/lib/monitoring';
import { ApiResponse } from './responses';

/**
 * APIå®¢æˆ·ç«¯é…ç½®
 */
export interface ApiClientConfig {
  baseURL?: string;
  timeout?: number;
  retries?: number;
  retryDelay?: number;
  headers?: Record<string, string>;
  onError?: (error: ApiError) => void;
  onRetry?: (attempt: number, error: ApiError) => void;
}

/**
 * APIé”™è¯¯ç±»
 */
export class ApiError extends CustomError {
  public readonly status: number;
  public readonly response?: Response;
  public readonly data?: any;
  public readonly traceId?: string;

  constructor(
    message: string,
    status: number,
    code: string = ErrorCode.API_ERROR,
    response?: Response,
    data?: any,
    traceId?: string
  ) {
    super(message, code);
    this.name = 'ApiError';
    this.status = status;
    this.response = response;
    this.data = data;
    this.traceId = traceId;
  }

  /**
   * åˆ¤æ–­æ˜¯å¦ä¸ºå®¢æˆ·ç«¯é”™è¯¯
   */
  isClientError(): boolean {
    return this.status >= 400 && this.status < 500;
  }

  /**
   * åˆ¤æ–­æ˜¯å¦ä¸ºæœåŠ¡å™¨é”™è¯¯
   */
  isServerError(): boolean {
    return this.status >= 500;
  }

  /**
   * åˆ¤æ–­æ˜¯å¦ä¸ºç½‘ç»œé”™è¯¯
   */
  isNetworkError(): boolean {
    return this.status === 0 || this.code === ErrorCode.NETWORK_ERROR;
  }

  /**
   * åˆ¤æ–­æ˜¯å¦å¯é‡è¯•
   */
  isRetryable(): boolean {
    // ç½‘ç»œé”™è¯¯ã€æœåŠ¡å™¨é”™è¯¯ã€è¶…æ—¶é”™è¯¯å¯é‡è¯•
    return this.isNetworkError() || 
           this.isServerError() || 
           this.status === 408 || // Request Timeout
           this.status === 429;   // Too Many Requests
  }
}

/**
 * è¯·æ±‚é…ç½®æ¥å£
 */
export interface RequestConfig extends RequestInit {
  timeout?: number;
  retries?: number;
  retryDelay?: number;
  skipErrorHandling?: boolean;
}

/**
 * APIå®¢æˆ·ç«¯ç±»
 */
export class ApiClient {
  private config: ApiClientConfig;
  private abortControllers = new Map<string, AbortController>();

  constructor(config: ApiClientConfig = {}) {
    this.config = {
      baseURL: process.env.NEXT_PUBLIC_API_BASE_URL || '',
      timeout: 10000,
      retries: 3,
      retryDelay: 1000,
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      ...config
    };
  }

  /**
   * å‘é€è¯·æ±‚
   */
  async request<T = any>(
    url: string,
    config: RequestConfig = {}
  ): Promise<ApiResponse<T>> {
    const requestId = this.generateRequestId();
    const fullUrl = this.buildUrl(url);
    const requestConfig = this.buildRequestConfig(config, requestId);

    // è®¾ç½®è¯·æ±‚ä¸Šä¸‹æ–‡
    monitoringContext.setRequest({
      id: requestId,
      method: requestConfig.method || 'GET',
      url: fullUrl,
      startTime: Date.now()
    });

    try {
      const response = await this.executeRequest<T>(fullUrl, requestConfig, requestId);

      // æ›´æ–°è¯·æ±‚ä¸Šä¸‹æ–‡
      monitoringContext.setRequest({
        id: requestId,
        endTime: Date.now(),
        duration: Date.now() - (monitoringContext.getCurrentContext().request.startTime || Date.now()),
        statusCode: response.status
      });

      return response;
    } catch (error) {
      // æ›´æ–°è¯·æ±‚ä¸Šä¸‹æ–‡
      monitoringContext.setRequest({
        id: requestId,
        endTime: Date.now(),
        duration: Date.now() - (monitoringContext.getCurrentContext().request.startTime || Date.now()),
        statusCode: error instanceof ApiError ? error.status : 0
      });

      throw error;
    } finally {
      this.abortControllers.delete(requestId);
    }
  }

  /**
   * GETè¯·æ±‚
   */
  async get<T = any>(url: string, config?: RequestConfig): Promise<ApiResponse<T>> {
    return this.request<T>(url, { ...config, method: 'GET' });
  }

  /**
   * POSTè¯·æ±‚
   */
  async post<T = any>(url: string, data?: any, config?: RequestConfig): Promise<ApiResponse<T>> {
    return this.request<T>(url, {
      ...config,
      method: 'POST',
      body: data ? JSON.stringify(data) : undefined
    });
  }

  /**
   * PUTè¯·æ±‚
   */
  async put<T = any>(url: string, data?: any, config?: RequestConfig): Promise<ApiResponse<T>> {
    return this.request<T>(url, {
      ...config,
      method: 'PUT',
      body: data ? JSON.stringify(data) : undefined
    });
  }

  /**
   * DELETEè¯·æ±‚
   */
  async delete<T = any>(url: string, config?: RequestConfig): Promise<ApiResponse<T>> {
    return this.request<T>(url, { ...config, method: 'DELETE' });
  }

  /**
   * PATCHè¯·æ±‚
   */
  async patch<T = any>(url: string, data?: any, config?: RequestConfig): Promise<ApiResponse<T>> {
    return this.request<T>(url, {
      ...config,
      method: 'PATCH',
      body: data ? JSON.stringify(data) : undefined
    });
  }

  /**
   * å–æ¶ˆè¯·æ±‚
   */
  cancelRequest(requestId: string): void {
    const controller = this.abortControllers.get(requestId);
    if (controller) {
      controller.abort();
      this.abortControllers.delete(requestId);
    }
  }

  /**
   * å–æ¶ˆæ‰€æœ‰è¯·æ±‚
   */
  cancelAllRequests(): void {
    this.abortControllers.forEach(controller => controller.abort());
    this.abortControllers.clear();
  }

  /**
   * æ‰§è¡Œè¯·æ±‚ï¼ˆå¸¦é‡è¯•ï¼‰
   */
  private async executeRequest<T>(
    url: string,
    config: RequestConfig & { signal: AbortSignal },
    requestId: string
  ): Promise<ApiResponse<T>> {
    const maxRetries = config.retries ?? this.config.retries ?? 3;
    const retryDelay = config.retryDelay ?? this.config.retryDelay ?? 1000;

    let lastError: ApiError;

    for (let attempt = 0; attempt <= maxRetries; attempt++) {
      try {
        if (attempt > 0) {
          // ç­‰å¾…é‡è¯•å»¶è¿Ÿ
          await this.delay(retryDelay * Math.pow(2, attempt - 1)); // æŒ‡æ•°é€€é¿

          // è°ƒç”¨é‡è¯•å›è°ƒ
          if (this.config.onRetry) {
            this.config.onRetry(attempt, lastError!);
          }

          logger.info('API request retry', {
            metadata: {
              requestId,
              url,
              attempt,
              maxRetries,
              lastError: lastError!.message
            }
          });
        }

        const response = await this.fetchWithTimeout(url, config);
        const data = await this.parseResponse<T>(response);

        // æ£€æŸ¥ä¸šåŠ¡é”™è¯¯
        if (!data.success && data.error) {
          throw new ApiError(
            data.error.message,
            response.status,
            data.error.code,
            response,
            data,
            data.error.traceId
          );
        }

        return data;
      } catch (error) {
        lastError = this.handleRequestError(error, url, requestId);

        // å¦‚æœä¸å¯é‡è¯•æˆ–å·²è¾¾åˆ°æœ€å¤§é‡è¯•æ¬¡æ•°ï¼ŒæŠ›å‡ºé”™è¯¯
        if (!lastError.isRetryable() || attempt === maxRetries) {
          throw lastError;
        }
      }
    }

    throw lastError!;
  }

  /**
   * å¸¦è¶…æ—¶çš„fetch
   */
  private async fetchWithTimeout(url: string, config: RequestConfig & { signal: AbortSignal }): Promise<Response> {
    const timeout = config.timeout ?? this.config.timeout ?? 10000;

    const timeoutId = setTimeout(() => {
      config.signal.abort();
    }, timeout);

    try {
      const response = await fetch(url, config);
      clearTimeout(timeoutId);
      return response;
    } catch (error) {
      clearTimeout(timeoutId);
      throw error;
    }
  }

  /**
   * è§£æå“åº”
   */
  private async parseResponse<T>(response: Response): Promise<ApiResponse<T>> {
    const contentType = response.headers.get('content-type');

    if (contentType?.includes('application/json')) {
      try {
        return await response.json();
      } catch (error) {
        throw new ApiError(
          'å“åº”è§£æå¤±è´¥ï¼šæ— æ•ˆçš„JSONæ ¼å¼',
          response.status,
          ErrorCode.PARSE_ERROR,
          response
        );
      }
    } else {
      // éJSONå“åº”
      const text = await response.text();
      if (!response.ok) {
        throw new ApiError(
          text || `HTTP ${response.status}: ${response.statusText}`,
          response.status,
          ErrorCode.HTTP_ERROR,
          response
        );
      }

      return {
        success: true,
        data: text as any
      };
    }
  }

  /**
   * å¤„ç†è¯·æ±‚é”™è¯¯
   */
  private handleRequestError(error: any, url: string, requestId: string): ApiError {
    let apiError: ApiError;

    if (error instanceof ApiError) {
      apiError = error;
    } else if (error.name === 'AbortError') {
      apiError = new ApiError(
        'è¯·æ±‚å·²å–æ¶ˆ',
        0,
        ErrorCode.REQUEST_CANCELLED
      );
    } else if (error.name === 'TypeError' && error.message.includes('fetch')) {
      apiError = new ApiError(
        'ç½‘ç»œè¿æ¥å¤±è´¥ï¼Œè¯·æ£€æŸ¥ç½‘ç»œè¿æ¥',
        0,
        ErrorCode.NETWORK_ERROR
      );
    } else {
      apiError = new ApiError(
        error.message || 'è¯·æ±‚å¤±è´¥',
        0,
        ErrorCode.UNKNOWN_ERROR
      );
    }

    // è®°å½•é”™è¯¯æ—¥å¿—
    logger.error('API request failed', apiError, {
      metadata: {
        requestId,
        url,
        status: apiError.status,
        code: apiError.code
      }
    });

    // æŠ¥å‘Šé”™è¯¯åˆ°ç›‘æ§ç³»ç»Ÿ
    if (!apiError.isClientError() || apiError.status >= 500) {
      reportError(apiError, {
        tags: {
          api_client: 'true',
          request_id: requestId,
          status: apiError.status.toString()
        },
        extra: {
          url,
          traceId: apiError.traceId
        }
      });
    }

    // è°ƒç”¨é”™è¯¯å›è°ƒ
    if (this.config.onError) {
      this.config.onError(apiError);
    }

    return apiError;
  }

  /**
   * æ„å»ºå®Œæ•´URL
   */
  private buildUrl(url: string): string {
    if (url.startsWith('http://') || url.startsWith('https://')) {
      return url;
    }

    const baseURL = this.config.baseURL || '';
    return `${baseURL}${url.startsWith('/') ? url : `/${url}`}`;
  }

  /**
   * æ„å»ºè¯·æ±‚é…ç½®
   */
  private buildRequestConfig(config: RequestConfig, requestId: string): RequestConfig & { signal: AbortSignal } {
    const controller = new AbortController();
    this.abortControllers.set(requestId, controller);

    return {
      ...config,
      headers: {
        ...this.config.headers,
        ...config.headers,
        'X-Request-ID': requestId
      },
      signal: controller.signal
    };
  }

  /**
   * ç”Ÿæˆè¯·æ±‚ID
   */
  private generateRequestId(): string {
    return `req-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * å»¶è¿Ÿå‡½æ•°
   */
  private delay(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }
}

