    if (error instanceof SyntaxError) {
      throw new CustomError('è¯·æ±‚ä½“æ ¼å¼é”™è¯¯ï¼Œè¯·æ£€æŸ¥JSONæ ¼å¼', ErrorCode.VALIDATION_ERROR);
