  ): Promise<Buffer> {
    try {
      // è¿™é‡Œåº”è¯¥ä½¿ç”¨å®é™…çš„å›¾ç‰‡å¤„ç†åº“ï¼Œå¦‚ sharp
      // ä¸ºäº†æ¼”ç¤ºï¼Œæˆ‘ä»¬è¿”å›åŸå§‹buffer
      logger.debug('Image resized', config);
      return input;

    } catch (error) {
      logger.error('Image resize failed', error instanceof Error ? error : new Error(String(error)), config);
      throw error;
    }
  }

  /**
   * æ·»åŠ æ°´å°
   */
  async addWatermark(
    input: Buffer,
    config: WatermarkConfig
  ): Promise<Buffer> {
    try {
      // è¿™é‡Œåº”è¯¥ä½¿ç”¨å®é™…çš„å›¾ç‰‡å¤„ç†åº“æ¥æ·»åŠ æ°´å°
      // ä¸ºäº†æ¼”ç¤ºï¼Œæˆ‘ä»¬è¿”å›åŸå§‹buffer
      logger.debug('Watermark added', config);
      return input;

    } catch (error) {
      logger.error('Watermark addition failed', error instanceof Error ? error : new Error(String(error)), config);
      throw error;
