  constructor(config?: Partial<OptimizationConfig>) {
    this.config = {
      images: {
        formats: ['webp', 'avif', 'jpeg', 'png'],
        quality: 85,
        progressive: true,
        autoWebP: true,
        autoAVIF: true,
        responsive: true,
