        placeholder: 'blur',
        sizes: [320, 640, 768, 1024, 1280, 1920]
      },
      videos: {
        formats: ['mp4', 'webm'],
        quality: 80,
        compression: true,
        streaming: true,
        thumbnail: true
      },
      scripts: {
        minify: true,
        treeshake: true,
