  /**
   * ä¼˜åŒ–å›¾ç‰‡
   */
  async optimizeImage(
    input: Buffer | string,
    options?: {
      format?: string;
      quality?: number;
      width?: number;
      height?: number;
      progressive?: boolean;
    }
  ): Promise<OptimizationResult> {
    const startTime = Date.now();

    try {
      // è·å–åŸå§‹å¤§å°
      const originalSize = typeof input === 'string' 
        ? Buffer.from(input, 'base64').length 
        : input.length;

      // è¿™é‡Œåº”è¯¥ä½¿ç”¨å®é™…çš„å›¾ç‰‡å¤„ç†åº“ï¼Œå¦‚ sharp
      // ä¸ºäº†æ¼”ç¤ºï¼Œæˆ‘ä»¬æ¨¡æ‹Ÿä¼˜åŒ–è¿‡ç¨‹
      const optimizedSize = Math.floor(originalSize * 0.7); // æ¨¡æ‹Ÿ70%å‹ç¼©
      const format = options?.format || 'webp';
      const quality = options?.quality || this.config.images.quality;

      const result: OptimizationResult = {
        originalSize,
        optimizedSize,
        compressionRatio: (originalSize - optimizedSize) / originalSize,
        format,
        quality,
        dimensions: options?.width && options?.height 
          ? { width: options.width, height: options.height }
          : undefined,
        processingTime: Date.now() - startTime,
        success: true
      };

      logger.debug('Image optimized', result);
      return result;

    } catch (error) {
      logger.error('Image optimization failed', error instanceof Error ? error : new Error(String(error)));

      return {
        originalSize: typeof input === 'string' 
          ? Buffer.from(input, 'base64').length 
          : input.length,
        optimizedSize: 0,
        compressionRatio: 0,
        processingTime: Date.now() - startTime,
        success: false,
        error: error instanceof Error ? error.message : String(error)
      };
    }
  }

  /**
   * ç”Ÿæˆå“åº”å¼å›¾ç‰‡
   */
