'use client';

import React, { createContext, useContext, useReducer, useEffect, ReactNode } from 'react';
import { UserDocument } from '../models/User';

export type AuthUser = Omit<UserDocument, 'password'>;

interface AuthState {
  user: AuthUser | null;
  token: string | null;
  refreshToken: string | null;
  isLoading: boolean;
  isAuthenticated: boolean;
}

type AuthAction =
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'LOGIN_SUCCESS'; payload: { user: AuthUser; token: string; refreshToken: string } }
  | { type: 'LOGOUT' }
  | { type: 'UPDATE_USER'; payload: AuthUser }
  | { type: 'RESTORE_SESSION'; payload: { user: AuthUser; token: string; refreshToken: string } };

const initialState: AuthState = {
  user: null,
  token: null,
  refreshToken: null,
  isLoading: true,
  isAuthenticated: false,
};

function authReducer(state: AuthState, action: AuthAction): AuthState {
  switch (action.type) {
    case 'SET_LOADING':
      return {
        ...state,
        isLoading: action.payload,
      };
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        refreshToken: action.payload.refreshToken,
        isAuthenticated: true,
        isLoading: false,
      };
    case 'LOGOUT':
      return {
        ...initialState,
        isLoading: false,
      };
    case 'UPDATE_USER':
      return {
        ...state,
        user: action.payload,
      };
    case 'RESTORE_SESSION':
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        refreshToken: action.payload.refreshToken,
        isAuthenticated: true,
        isLoading: false,
      };
    default:
      return state;
  }
}

interface AuthContextType extends AuthState {
  login: (email: string, password: string) => Promise<{ success: boolean; error?: string }>;
  register: (email: string, password: string, name: string) => Promise<{ success: boolean; error?: string }>;
  loginWithGoogle: (idToken: string) => Promise<{ success: boolean; error?: string }>;
  logout: () => void;
  updateProfile: (updates: Partial<AuthUser>) => Promise<{ success: boolean; error?: string }>;
  changePassword: (currentPassword: string, newPassword: string) => Promise<{ success: boolean; error?: string }>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

interface AuthProviderProps {
  children: ReactNode;
}

export function AuthProvider({ children }: AuthProviderProps) {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // Restore session on mount
  useEffect(() => {
    const restoreSession = async () => {
      try {
        const token = localStorage.getItem('auth_token');
        const refreshToken = localStorage.getItem('refresh_token');

        if (!token) {
          dispatch({ type: 'SET_LOADING', payload: false });
          return;
        }

        // Verify token and get user profile
        const response = await fetch('/api/auth/profile', {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          dispatch({
            type: 'RESTORE_SESSION',
            payload: {
              user: data.user,
              token,
              refreshToken: refreshToken || '',
            },
          });
        } else {
          // Token is invalid, clear storage
          localStorage.removeItem('auth_token');
          localStorage.removeItem('refresh_token');
          dispatch({ type: 'SET_LOADING', payload: false });
        }
      } catch (error) {
        console.error('Session restore error:', error);
        localStorage.removeItem('auth_token');
        localStorage.removeItem('refresh_token');
        dispatch({ type: 'SET_LOADING', payload: false });
      }
    };

    restoreSession();
  }, []);

  const login = async (email: string, password: string) => {
    try {
      dispatch({ type: 'SET_LOADING', payload: true });

      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Store tokens
        localStorage.setItem('auth_token', data.token);
        localStorage.setItem('refresh_token', data.refreshToken);

        // Set cookie for server-side authentication
        document.cookie = `auth-token=${data.token}; path=/; max-age=${7 * 24 * 60 * 60}; secure; samesite=strict`;

        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: {
            user: data.user,
            token: data.token,
            refreshToken: data.refreshToken,
          },
        });

        return { success: true };
      } else {
        dispatch({ type: 'SET_LOADING', payload: false });
        return { success: false, error: data.error };
      }
    } catch (error) {
      dispatch({ type: 'SET_LOADING', payload: false });
      return { success: false, error: 'Network error' };
    }
  };

  const register = async (email: string, password: string, name: string) => {
    try {
      dispatch({ type: 'SET_LOADING', payload: true });

      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password, name }),
      });

      const data = await response.json();

      if (response.ok) {
        // Store tokens
        localStorage.setItem('auth_token', data.token);
        localStorage.setItem('refresh_token', data.refreshToken);

        // Set cookie for server-side authentication
        document.cookie = `auth-token=${data.token}; path=/; max-age=${7 * 24 * 60 * 60}; secure; samesite=strict`;

        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: {
            user: data.user,
            token: data.token,
            refreshToken: data.refreshToken,
          },
        });

        return { success: true };
      } else {
        dispatch({ type: 'SET_LOADING', payload: false });
        return { success: false, error: data.error };
      }
    } catch (error) {
      dispatch({ type: 'SET_LOADING', payload: false });
      return { success: false, error: 'Network error' };
    }
  };

  const logout = () => {
    localStorage.removeItem('auth_token');
    localStorage.removeItem('refresh_token');

    // Clear cookie
    document.cookie = 'auth-token=; path=/; expires=Thu, 01 Jan 1970 00:00:00 GMT';

    dispatch({ type: 'LOGOUT' });
  };

  const updateProfile = async (updates: Partial<AuthUser>) => {
    try {
      const response = await fetch('/api/auth/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${state.token}`,
        },
        body: JSON.stringify(updates),
      });

      const data = await response.json();

      if (response.ok) {
        dispatch({ type: 'UPDATE_USER', payload: data.user });
        return { success: true };
      } else {
        return { success: false, error: data.error };
      }
    } catch (error) {
      return { success: false, error: 'Network error' };
    }
  };

  const loginWithGoogle = async (idToken: string) => {
    try {
      dispatch({ type: 'SET_LOADING', payload: true });

      const response = await fetch('/api/auth/google/callback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ idToken }),
      });

      const data = await response.json();

      if (response.ok) {
        // Store tokens
        localStorage.setItem('auth_token', data.token);
        localStorage.setItem('refresh_token', data.refreshToken);

        // Set cookie for server-side authentication
        document.cookie = `auth-token=${data.token}; path=/; max-age=${7 * 24 * 60 * 60}; secure; samesite=strict`;

        dispatch({
          type: 'LOGIN_SUCCESS',
          payload: {
            user: data.user,
            token: data.token,
            refreshToken: data.refreshToken,
          },
        });

        return { success: true };
      } else {
        dispatch({ type: 'SET_LOADING', payload: false });
        return { success: false, error: data.error };
      }
    } catch (error) {
      dispatch({ type: 'SET_LOADING', payload: false });
      return { success: false, error: 'Network error' };
    }
  };

  const changePassword = async (currentPassword: string, newPassword: string) => {
    try {
      const response = await fetch('/api/auth/change-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${state.token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });

      const data = await response.json();

      if (response.ok) {
        return { success: true };
      } else {
        return { success: false, error: data.error };
      }
    } catch (error) {
      return { success: false, error: 'Network error' };
    }
  };

  const value: AuthContextType = {
    ...state,
    login,
    register,
    loginWithGoogle,
    logout,
    updateProfile,
    changePassword,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

import { OAuth2Client } from 'google-auth-library';
import User, { UserDocument } from '../models/User';
import { generateToken, generateRefreshToken } from './jwt';
import connectDB from '../mongodb';
import ContributionLog from '../models/ContributionLog';

const client = new OAuth2Client(
  process.env.GOOGLE_CLIENT_ID,
  process.env.GOOGLE_CLIENT_SECRET,
  process.env.GOOGLE_REDIRECT_URI
);

export interface GoogleAuthResponse {
  success: boolean;
  user?: Omit<UserDocument, 'password'>;
  token?: string;
  refreshToken?: string;
  error?: string;
}

/**
 * Verify Google ID token and authenticate user
 */
export async function verifyGoogleToken(idToken: string): Promise<GoogleAuthResponse> {
  try {
    await connectDB();

    // Verify the token with Google
    const ticket = await client.verifyIdToken({
      idToken,
      audience: process.env.GOOGLE_CLIENT_ID,
    });

    const payload = ticket.getPayload();
    if (!payload) {
      return {
        success: false,
        error: 'Invalid Google token',
      };
    }

    const { email, name, picture, sub: googleId } = payload;

    if (!email) {
      return {
        success: false,
        error: 'Email not provided by Google',
      };
    }

    // Check if user already exists
    let user = await User.findOne({ email: email.toLowerCase() });

    if (user) {
      // Update Google ID if not set
      if (!user.googleId) {
        user.googleId = googleId;
        user.avatar = picture || user.avatar;
        await user.save();
      }

      // Reset daily usage if needed
      user.resetDailyUsage();
      await user.save();
    } else {
      // Create new user
      user = new User({
        email: email.toLowerCase(),
        name: name || 'Google User',
        googleId,
        avatar: picture,
        subscription: {
          plan: 'free',
          startDate: new Date(),
          isActive: true,
        },
        usage: {
          dailyGenerations: 0,
          dailyReuses: 0,
          lastResetDate: new Date(),
        },
        settings: {
          emailNotifications: true,
          publicProfile: false,
        },
      });

      const savedUser = await user.save();

      // Log registration contribution
      await ContributionLog.create({
        user: savedUser._id,
        action: 'create',
        target: {
          type: 'user',
          id: savedUser._id.toString(),
          title: 'Google OAuth Registration',
        },
        points: 10, // Welcome bonus
        metadata: {
          description: 'User registration via Google OAuth',
          category: 'account',
        },
      });

      user = savedUser;
    }

    // Generate tokens
    const token = generateToken(user);
    const refreshToken = generateRefreshToken(user);

    // Remove password from response
    const userResponse = user.toObject();
    delete userResponse.password;

    return {
      success: true,
      user: userResponse,
      token,
      refreshToken,
    };
  } catch (error) {
    console.error('Google auth error:', error);
    return {
      success: false,
      error: 'Google authentication failed',
    };
  }
}

/**
 * Get Google OAuth URL for authentication
 */
export function getGoogleAuthUrl(): string {
  const scopes = [
    'https://www.googleapis.com/auth/userinfo.email',
    'https://www.googleapis.com/auth/userinfo.profile',
  ];

  const url = client.generateAuthUrl({
    access_type: 'offline',
    scope: scopes,
    include_granted_scopes: true,
  });

  return url;
}

/**
 * Exchange authorization code for tokens
 */
export async function exchangeCodeForTokens(code: string): Promise<{
  success: boolean;
  idToken?: string;
  error?: string;
}> {
  try {
    const { tokens } = await client.getToken(code);

    if (!tokens.id_token) {
      return {
        success: false,
        error: 'No ID token received from Google',
      };
    }

    return {
      success: true,
      idToken: tokens.id_token,
    };
  } catch (error) {
    console.error('Token exchange error:', error);
    return {
      success: false,
      error: 'Failed to exchange code for tokens',
    };
  }
}

export default {
  verifyGoogleToken,
  getGoogleAuthUrl,
  exchangeCodeForTokens,
};

import jwt, { SignOptions } from 'jsonwebtoken';
import { UserDocument } from '../models/User';

const JWT_SECRET = process.env.JWT_SECRET || process.env.NEXTAUTH_SECRET || 'your-secret-key';
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '7d' as const;

export interface JWTPayload {
  userId: string;
  email: string;
  name: string;
  iat?: number;
  exp?: number;
}

/**
 * Generate JWT token for user
 */
export function generateToken(user: UserDocument): string {
  const payload: JWTPayload = {
    userId: (user._id as any).toString(),
    email: user.email,
    name: user.name,
  };

  const options: SignOptions = {
    expiresIn: '7d',
  };

  return jwt.sign(payload, JWT_SECRET as string, options);
}

/**
 * Verify JWT token and return payload
 */
export function verifyToken(token: string): JWTPayload | null {
  try {
    const decoded = jwt.verify(token, JWT_SECRET as string) as JWTPayload;
    return decoded;
  } catch (error) {
    console.error('JWT verification failed:', error);
    return null;
  }
}

/**
 * Extract token from Authorization header
 */
export function extractTokenFromHeader(authHeader: string | undefined): string | null {
  if (!authHeader) return null;

  const parts = authHeader.split(' ');
  if (parts.length !== 2 || parts[0] !== 'Bearer') {
    return null;
  }

  return parts[1];
}

/**
 * Generate refresh token (longer expiry)
 */
export function generateRefreshToken(user: UserDocument): string {
  const payload: JWTPayload = {
    userId: (user._id as any).toString(),
    email: user.email,
    name: user.name,
  };

  const options: SignOptions = {
    expiresIn: '30d', // Refresh token expires in 30 days
  };

  return jwt.sign(payload, JWT_SECRET as string, options);
}

/**
 * Decode token without verification (for expired token info)
 */
export function decodeToken(token: string): JWTPayload | null {
  try {
    const decoded = jwt.decode(token) as JWTPayload;
    return decoded;
  } catch (error) {
    console.error('JWT decode failed:', error);
    return null;
  }
}

export default {
  generateToken,
  verifyToken,
  extractTokenFromHeader,
  generateRefreshToken,
  decodeToken,
};

import { NextRequest, NextResponse } from 'next/server';
import { verifyToken, extractTokenFromHeader, JWTPayload } from './jwt';
import User from '../models/User';
import connectDB from '../mongodb';

export interface AuthenticatedRequest extends NextRequest {
  user?: JWTPayload & {
    dbUser?: any;
  };
}

/**
 * Authentication middleware for API routes
 */
export async function authenticateToken(request: NextRequest): Promise<{
  success: boolean;
  user?: JWTPayload & { dbUser?: any };
  error?: string;
}> {
  try {
    const authHeader = request.headers.get('authorization');
    const token = extractTokenFromHeader(authHeader || '');

    if (!token) {
      return {
        success: false,
        error: 'No token provided',
      };
    }

    const payload = verifyToken(token);
    if (!payload) {
      return {
        success: false,
        error: 'Invalid or expired token',
      };
    }

    // Optionally fetch user from database for fresh data
    await connectDB();
    const dbUser = await User.findById(payload.userId).select('-password');

    if (!dbUser) {
      return {
        success: false,
        error: 'User not found',
      };
    }

    return {
      success: true,
      user: {
        ...payload,
        dbUser: dbUser.toObject(),
      },
    };
  } catch (error) {
    console.error('Authentication middleware error:', error);
    return {
      success: false,
      error: 'Authentication failed',
    };
  }
}

/**
 * Require authentication middleware
 */
export function requireAuth(handler: (req: AuthenticatedRequest) => Promise<NextResponse>) {
  return async (request: NextRequest) => {
    const authResult = await authenticateToken(request);

    if (!authResult.success) {
      return NextResponse.json(
        { error: authResult.error },
        { status: 401 }
      );
    }

    // Add user to request object
    (request as AuthenticatedRequest).user = authResult.user;

    return handler(request as AuthenticatedRequest);
  };
}

/**
 * Optional authentication middleware (doesn't fail if no token)
 */
export function optionalAuth(handler: (req: AuthenticatedRequest) => Promise<NextResponse>) {
  return async (request: NextRequest) => {
    const authResult = await authenticateToken(request);

    // Add user to request object if authentication succeeded
    if (authResult.success) {
      (request as AuthenticatedRequest).user = authResult.user;
    }

    return handler(request as AuthenticatedRequest);
  };
}

/**
 * Check if user has required subscription level
 */
export function requireSubscription(requiredLevel: 'free' | 'pro' | 'super') {
  return (handler: (req: AuthenticatedRequest) => Promise<NextResponse>) => {
    return requireAuth(async (request: AuthenticatedRequest) => {
      const user = request.user;

      if (!user?.dbUser) {
        return NextResponse.json(
          { error: 'User data not available' },
          { status: 500 }
        );
      }

      const userSubscription = user.dbUser.subscription?.plan || 'free';
      const levels = ['free', 'pro', 'super'];
      const userLevel = levels.indexOf(userSubscription);
      const requiredLevelIndex = levels.indexOf(requiredLevel);

      if (userLevel < requiredLevelIndex) {
        return NextResponse.json(
          { 
            error: 'Insufficient subscription level',
            required: requiredLevel,
            current: userSubscription
          },
          { status: 403 }
        );
      }

      return handler(request);
    });
  };
}

/**
 * Rate limiting middleware (basic implementation)
 */
export function rateLimit(maxRequests: number = 100, windowMs: number = 15 * 60 * 1000) {
  const requests = new Map<string, { count: number; resetTime: number }>();

  return (handler: (req: NextRequest) => Promise<NextResponse>) => {
    return async (request: NextRequest) => {
      const ip = request.headers.get('x-forwarded-for') || request.headers.get('x-real-ip') || 'unknown';
      const now = Date.now();

      const userRequests = requests.get(ip);

      if (!userRequests || now > userRequests.resetTime) {
        requests.set(ip, { count: 1, resetTime: now + windowMs });
      } else {
        userRequests.count++;

        if (userRequests.count > maxRequests) {
          return NextResponse.json(
            { error: 'Too many requests' },
            { status: 429 }
          );
        }
      }

      return handler(request);
    };
  };
}

export default {
  authenticateToken,
  requireAuth,
  optionalAuth,
  requireSubscription,
  rateLimit,
};

