import bcrypt from 'bcryptjs';

const SALT_ROUNDS = 12;

/**
 * Hash password using bcrypt
 */
export async function hashPassword(password: string): Promise<string> {
  try {
    const salt = await bcrypt.genSalt(SALT_ROUNDS);
    const hashedPassword = await bcrypt.hash(password, salt);
    return hashedPassword;
  } catch (error) {
    console.error('Password hashing failed:', error);
    throw new Error('Failed to hash password');
  }
}

/**
 * Compare password with hash
 */
export async function comparePassword(password: string, hash: string): Promise<boolean> {
  try {
    const isMatch = await bcrypt.compare(password, hash);
    return isMatch;
  } catch (error) {
    console.error('Password comparison failed:', error);
    return false;
  }
}

/**
 * Validate password strength
 */
export function validatePassword(password: string): {
  isValid: boolean;
  errors: string[];
} {
  const errors: string[] = [];

  if (password.length < 8) {
    errors.push('Password must be at least 8 characters long');
  }

  if (password.length > 128) {
    errors.push('Password must be less than 128 characters long');
  }

  if (!/[a-z]/.test(password)) {
    errors.push('Password must contain at least one lowercase letter');
  }

  if (!/[A-Z]/.test(password)) {
    errors.push('Password must contain at least one uppercase letter');
  }

  if (!/\d/.test(password)) {
    errors.push('Password must contain at least one number');
  }

  if (!/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/.test(password)) {
    errors.push('Password must contain at least one special character');
  }

  return {
    isValid: errors.length === 0,
    errors,
  };
}

/**
 * Generate random password
 */
export function generateRandomPassword(length: number = 12): string {
  const lowercase = 'abcdefghijklmnopqrstuvwxyz';
  const uppercase = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const numbers = '0123456789';
  const symbols = '!@#$%^&*()_+-=[]{}|;:,.<>?';

  const allChars = lowercase + uppercase + numbers + symbols;

  let password = '';

  // Ensure at least one character from each category
  password += lowercase[Math.floor(Math.random() * lowercase.length)];
  password += uppercase[Math.floor(Math.random() * uppercase.length)];
  password += numbers[Math.floor(Math.random() * numbers.length)];
  password += symbols[Math.floor(Math.random() * symbols.length)];

  // Fill the rest randomly
  for (let i = 4; i < length; i++) {
    password += allChars[Math.floor(Math.random() * allChars.length)];
  }

  // Shuffle the password
  return password.split('').sort(() => Math.random() - 0.5).join('');
}

export default {
  hashPassword,
  comparePassword,
  validatePassword,
  generateRandomPassword,
};

import User, { UserDocument } from '../models/User';
import { hashPassword, comparePassword, validatePassword } from './password';
import { generateToken, generateRefreshToken } from './jwt';
import connectDB from '../mongodb';
import ContributionLog from '../models/ContributionLog';

export interface RegisterData {
  email: string;
  password: string;
  name: string;
}

export interface LoginData {
  email: string;
  password: string;
}

export interface AuthResponse {
  success: boolean;
  user?: Omit<UserDocument, 'password'>;
  token?: string;
  refreshToken?: string;
  error?: string;
}

/**
 * Register new user
 */
export async function registerUser(data: RegisterData): Promise<AuthResponse> {
  try {
    await connectDB();

    // Validate input
    if (!data.email || !data.password || !data.name) {
      return {
        success: false,
        error: 'Email, password, and name are required',
      };
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(data.email)) {
      return {
        success: false,
        error: 'Invalid email format',
      };
    }

    // Validate password strength
    const passwordValidation = validatePassword(data.password);
    if (!passwordValidation.isValid) {
      return {
        success: false,
        error: passwordValidation.errors.join(', '),
      };
    }

    // Check if user already exists
    const existingUser = await User.findOne({ email: data.email.toLowerCase() });
    if (existingUser) {
      return {
        success: false,
        error: 'User with this email already exists',
      };
    }

    // Hash password
    const hashedPassword = await hashPassword(data.password);

    // Create user
    const user = new User({
      email: data.email.toLowerCase(),
      name: data.name.trim(),
      password: hashedPassword,
      subscription: {
        plan: 'free',
        startDate: new Date(),
        isActive: true,
      },
      usage: {
        dailyGenerations: 0,
        dailyReuses: 0,
        lastResetDate: new Date(),
      },
      settings: {
        emailNotifications: true,
        publicProfile: false,
      },
    });

    const savedUser = await user.save();

    // Log registration contribution
    await ContributionLog.create({
      user: savedUser._id,
      action: 'create',
      target: {
        type: 'user',
        id: savedUser._id.toString(),
        title: 'User Registration',
      },
      points: 10, // Welcome bonus
      metadata: {
        description: 'User registration',
        category: 'account',
      },
    });

    // Generate tokens
    const token = generateToken(savedUser);
    const refreshToken = generateRefreshToken(savedUser);

    // Remove password from response
    const userResponse = savedUser.toObject();
    delete userResponse.password;

    return {
      success: true,
      user: userResponse,
      token,
      refreshToken,
    };
  } catch (error) {
    console.error('Registration error:', error);
    return {
      success: false,
      error: 'Registration failed. Please try again.',
    };
  }
}

/**
 * Login user
 */
export async function loginUser(data: LoginData): Promise<AuthResponse> {
  try {
    await connectDB();

    // Validate input
    if (!data.email || !data.password) {
      return {
        success: false,
        error: 'Email and password are required',
      };
    }

    // Find user
    const user = await User.findOne({ email: data.email.toLowerCase() });
    if (!user) {
      return {
        success: false,
        error: 'Invalid email or password',
      };
    }

    // Check password
    const isPasswordValid = await comparePassword(data.password, user.password);
    if (!isPasswordValid) {
      return {
        success: false,
        error: 'Invalid email or password',
      };
    }

    // Reset daily usage if needed
    user.resetDailyUsage();
    await user.save();

    // Generate tokens
    const token = generateToken(user);
    const refreshToken = generateRefreshToken(user);

    // Remove password from response
    const userResponse = user.toObject();
    delete userResponse.password;

    return {
      success: true,
      user: userResponse,
      token,
      refreshToken,
    };
  } catch (error) {
    console.error('Login error:', error);
    return {
      success: false,
      error: 'Login failed. Please try again.',
    };
  }
}

/**
 * Get user profile
 */
export async function getUserProfile(userId: string): Promise<{
  success: boolean;
  user?: Omit<UserDocument, 'password'>;
  error?: string;
}> {
  try {
