  /**
   * å¤„ç†çŸ¥è¯†å›¾è°±æ›´æ–°äº‹ä»¶
   */
  async handleGraphUpdated(graphId: string, graphData?: any): Promise<void> {
    const event: SyncEvent = {
      type: SyncEventType.GRAPH_UPDATED,
