/**
 * æ•°æ®åº“å‘Šè­¦é…ç½®
 */
export interface AlertConfig {
  slowQueryThreshold: number; // ms
  connectionThreshold: number; // percentage
  memoryThreshold: number; // percentage
  diskThreshold: number; // percentage
  errorRateThreshold: number; // percentage
  enabled: boolean;
}

/**
 * é»˜è®¤å‘Šè­¦é…ç½®
 */
export const DEFAULT_ALERT_CONFIG: AlertConfig = {
  slowQueryThreshold: 1000,
  connectionThreshold: 80,
  memoryThreshold: 85,
  diskThreshold: 90,
  errorRateThreshold: 5,
  enabled: true
};

/**
 * æ•°æ®åº“ç›‘æ§å™¨
 */
export class DatabaseMonitor extends EventEmitter {
  private db: any;
  private config: AlertConfig;
  private metrics: DatabaseMetrics[] = [];
  private slowQueries: SlowQueryRecord[] = [];
  private maxHistorySize = 1000;
  private maxSlowQuerySize = 500;
  private monitoringInterval?: NodeJS.Timeout;
  private isMonitoring = false;

  constructor(database: any, config: AlertConfig = DEFAULT_ALERT_CONFIG) {
    super();
    this.db = database;
    this.config = config;
  }

  /**
   * å¼€å§‹ç›‘æ§
   */
  startMonitoring(intervalMs: number = 60000): void {
    if (this.isMonitoring) {
      logger.warn('Database monitoring is already running');
      return;
    }

    this.isMonitoring = true;
    logger.info('Starting database monitoring', { intervalMs });

    // ç«‹å³æ”¶é›†ä¸€æ¬¡æŒ‡æ ‡
    this.collectMetrics();

    // è®¾ç½®å®šæœŸæ”¶é›†
    this.monitoringInterval = setInterval(() => {
      this.collectMetrics();
    }, intervalMs);

    this.emit('monitoringStarted');
  }

  /**
   * åœæ­¢ç›‘æ§
   */
  stopMonitoring(): void {
    if (!this.isMonitoring) {
      return;
    }

    this.isMonitoring = false;

    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = undefined;
    }

    logger.info('Database monitoring stopped');
    this.emit('monitoringStopped');
  }

  /**
   * æ”¶é›†æ•°æ®åº“æŒ‡æ ‡
   */
  private async collectMetrics(): Promise<void> {
    try {
      const metrics = await this.gatherDatabaseMetrics();
      this.recordMetrics(metrics);
      this.checkAlerts(metrics);
      this.emit('metricsCollected', metrics);
    } catch (error) {
      logger.error('Failed to collect database metrics', error instanceof Error ? error : new Error(String(error)));
      this.emit('metricsError', error);
    }
  }

  /**
   * æ”¶é›†æ•°æ®åº“æŒ‡æ ‡æ•°æ®
   */
  private async gatherDatabaseMetrics(): Promise<DatabaseMetrics> {
    // è·å–æœåŠ¡å™¨çŠ¶æ€
    const serverStatus = await this.db.admin().serverStatus();

    // è·å–æ•°æ®åº“ç»Ÿè®¡
    const dbStats = await this.db.stats();

    // è·å–é›†åˆæŒ‡æ ‡
    const collections = await this.gatherCollectionMetrics();

    const metrics: DatabaseMetrics = {
      connections: {
        active: serverStatus.connections?.current || 0,
        available: serverStatus.connections?.available || 0,
        total: serverStatus.connections?.totalCreated || 0,
        created: serverStatus.connections?.totalCreated || 0,
        destroyed: 0 // éœ€è¦è®¡ç®—
      },
      operations: {
        queries: serverStatus.opcounters?.query || 0,
        inserts: serverStatus.opcounters?.insert || 0,
        updates: serverStatus.opcounters?.update || 0,
        deletes: serverStatus.opcounters?.delete || 0,
        commands: serverStatus.opcounters?.command || 0
      },
      performance: {
        avgQueryTime: 0, // éœ€è¦ä»æ…¢æŸ¥è¯¢æ—¥å¿—è®¡ç®—
        slowQueries: this.slowQueries.length,
        failedQueries: 0, // éœ€è¦ä»é”™è¯¯æ—¥å¿—è·å–
        cacheHitRatio: this.calculateCacheHitRatio(serverStatus)
      },
      resources: {
        memoryUsage: serverStatus.mem?.resident || 0,
        diskUsage: dbStats.dataSize || 0,
        cpuUsage: 0, // éœ€è¦ä»ç³»ç»ŸæŒ‡æ ‡è·å–
        networkIO: serverStatus.network?.bytesIn + serverStatus.network?.bytesOut || 0
      },
      collections,
      timestamp: new Date()
    };

    return metrics;
  }

  /**
   * æ”¶é›†é›†åˆæŒ‡æ ‡
   */
  private async gatherCollectionMetrics(): Promise<CollectionMetrics[]> {
    const collections: CollectionMetrics[] = [];

    try {
      const collectionNames = await this.db.listCollections().toArray();

      for (const collInfo of collectionNames) {
        const collName = collInfo.name;

        try {
          const collection = this.db.collection(collName);
          const stats = await collection.stats();

          const metrics: CollectionMetrics = {
            name: collName,
            documentCount: stats.count || 0,
            dataSize: stats.size || 0,
            indexSize: stats.totalIndexSize || 0,
            avgDocumentSize: stats.avgObjSize || 0,
            operations: {
              reads: 0, // éœ€è¦ä»æ“ä½œæ—¥å¿—è·å–
              writes: 0,
              updates: 0,
              deletes: 0
            },
            performance: {
              avgReadTime: 0, // éœ€è¦ä»æ€§èƒ½æ—¥å¿—è®¡ç®—
              avgWriteTime: 0,
              slowOperations: 0
            }
          };

          collections.push(metrics);
        } catch (error) {
          logger.warn(`Failed to get stats for collection: ${collName}`, {
            error: error instanceof Error ? error.message : String(error)
          });
        }
      }
    } catch (error) {
      logger.error('Failed to gather collection metrics', error instanceof Error ? error : new Error(String(error)));
    }

    return collections;
  }

  /**
   * è®¡ç®—ç¼“å­˜å‘½ä¸­ç‡
   */
  private calculateCacheHitRatio(serverStatus: any): number {
    const wiredTiger = serverStatus.wiredTiger;
    if (!wiredTiger || !wiredTiger.cache) {
      return 0;
    }

    const cache = wiredTiger.cache;
    const hits = cache['pages read into cache'] || 0;
    const misses = cache['pages requested from the cache'] || 0;
    const total = hits + misses;

    return total > 0 ? (hits / total) * 100 : 0;
  }

  /**
   * è®°å½•æŒ‡æ ‡
   */
  private recordMetrics(metrics: DatabaseMetrics): void {
    this.metrics.push(metrics);

    // é™åˆ¶å†å²è®°å½•å¤§å°
    if (this.metrics.length > this.maxHistorySize) {
      this.metrics.shift();
    }
  }

  /**
   * æ£€æŸ¥å‘Šè­¦
   */
  private checkAlerts(metrics: DatabaseMetrics): void {
    if (!this.config.enabled) {
      return;
    }

    const alerts: string[] = [];

    // æ£€æŸ¥è¿æ¥æ•°
    const connectionUsage = (metrics.connections.active / metrics.connections.available) * 100;
    if (connectionUsage > this.config.connectionThreshold) {
      alerts.push(`æ•°æ®åº“è¿æ¥ä½¿ç”¨ç‡è¿‡é«˜: ${connectionUsage.toFixed(1)}%`);
    }

    // æ£€æŸ¥å†…å­˜ä½¿ç”¨
    if (metrics.resources.memoryUsage > this.config.memoryThreshold) {
      alerts.push(`æ•°æ®åº“å†…å­˜ä½¿ç”¨ç‡è¿‡é«˜: ${metrics.resources.memoryUsage}MB`);
    }

    // æ£€æŸ¥æ…¢æŸ¥è¯¢
    if (metrics.performance.slowQueries > 10) {
      alerts.push(`æ…¢æŸ¥è¯¢æ•°é‡è¿‡å¤š: ${metrics.performance.slowQueries}`);
    }

    // å‘é€å‘Šè­¦
    if (alerts.length > 0) {
      this.emit('alert', {
        type: 'performance',
        alerts,
        metrics,
        timestamp: new Date()
      });

      logger.warn('Database performance alerts', { alerts });
    }
  }

  /**
   * è®°å½•æ…¢æŸ¥è¯¢
   */
  recordSlowQuery(record: SlowQueryRecord): void {
    this.slowQueries.push(record);

    // é™åˆ¶æ…¢æŸ¥è¯¢è®°å½•å¤§å°
    if (this.slowQueries.length > this.maxSlowQuerySize) {
      this.slowQueries.shift();
    }

    // å‘é€æ…¢æŸ¥è¯¢å‘Šè­¦
    if (record.executionTime > this.config.slowQueryThreshold) {
      this.emit('slowQuery', record);

      logger.warn('Slow query detected', {
        collection: record.collection,
        operation: record.operation,
        executionTime: record.executionTime,
        query: JSON.stringify(record.query)
      });
    }
  }

  /**
   * è·å–å½“å‰æŒ‡æ ‡
   */
  getCurrentMetrics(): DatabaseMetrics | null {
    return this.metrics.length > 0 ? this.metrics[this.metrics.length - 1] : null;
  }

  /**
   * è·å–å†å²æŒ‡æ ‡
   */
  getHistoricalMetrics(limit?: number): DatabaseMetrics[] {
    if (limit) {
      return this.metrics.slice(-limit);
    }
    return [...this.metrics];
  }

  /**
   * è·å–æ…¢æŸ¥è¯¢è®°å½•
   */
  getSlowQueries(limit?: number): SlowQueryRecord[] {
    if (limit) {
      return this.slowQueries.slice(-limit);
    }
    return [...this.slowQueries];
  }

  /**
   * è·å–æ€§èƒ½ç»Ÿè®¡
   */
  getPerformanceStats(timeRange?: { start: Date; end: Date }): {
    avgQueryTime: number;
    totalQueries: number;
    slowQueryRate: number;
    errorRate: number;
    connectionUtilization: number;
    memoryUtilization: number;
  } {
    let relevantMetrics = this.metrics;

    if (timeRange) {
      relevantMetrics = this.metrics.filter(
        m => m.timestamp >= timeRange.start && m.timestamp <= timeRange.end
      );
    }

    if (relevantMetrics.length === 0) {
      return {
        avgQueryTime: 0,
        totalQueries: 0,
        slowQueryRate: 0,
        errorRate: 0,
        connectionUtilization: 0,
        memoryUtilization: 0
      };
    }

    const latest = relevantMetrics[relevantMetrics.length - 1];
    const earliest = relevantMetrics[0];

    const totalQueries = latest.operations.queries - earliest.operations.queries;
    const slowQueries = this.slowQueries.filter(
      q => !timeRange || (q.timestamp >= timeRange.start && q.timestamp <= timeRange.end)
    ).length;

    return {
      avgQueryTime: relevantMetrics.reduce((sum, m) => sum + m.performance.avgQueryTime, 0) / relevantMetrics.length,
      totalQueries,
      slowQueryRate: totalQueries > 0 ? (slowQueries / totalQueries) * 100 : 0,
      errorRate: latest.performance.failedQueries,
      connectionUtilization: (latest.connections.active / latest.connections.available) * 100,
      memoryUtilization: latest.resources.memoryUsage
    };
  }

  /**
   * ç”Ÿæˆæ€§èƒ½æŠ¥å‘Š
   */
  generatePerformanceReport(timeRange?: { start: Date; end: Date }): {
    summary: any;
    slowQueries: SlowQueryRecord[];
    recommendations: string[];
    charts: any[];
  } {
    const stats = this.getPerformanceStats(timeRange);
    const slowQueries = this.getSlowQueries(50);
    const recommendations = this.generateRecommendations(stats, slowQueries);

    return {
      summary: {
        ...stats,
        reportPeriod: timeRange || { start: this.metrics[0]?.timestamp, end: new Date() },
        totalCollections: this.getCurrentMetrics()?.collections.length || 0
      },
      slowQueries,
      recommendations,
      charts: this.generateChartData(timeRange)
    };
  }

  /**
   * ç”Ÿæˆä¼˜åŒ–å»ºè®®
   */
  private generateRecommendations(
    stats: any,
    slowQueries: SlowQueryRecord[]
  ): string[] {
    const recommendations: string[] = [];

    if (stats.slowQueryRate > 5) {
      recommendations.push('æ…¢æŸ¥è¯¢ç‡è¿‡é«˜ï¼Œå»ºè®®ä¼˜åŒ–æŸ¥è¯¢æˆ–æ·»åŠ ç´¢å¼•');
    }

    if (stats.connectionUtilization > 80) {
      recommendations.push('è¿æ¥æ± ä½¿ç”¨ç‡è¿‡é«˜ï¼Œå»ºè®®å¢åŠ è¿æ¥æ± å¤§å°æˆ–ä¼˜åŒ–è¿æ¥ä½¿ç”¨');
    }

    if (stats.memoryUtilization > 1000) {
      recommendations.push('å†…å­˜ä½¿ç”¨é‡è¾ƒé«˜ï¼Œå»ºè®®ä¼˜åŒ–æŸ¥è¯¢æˆ–å¢åŠ æœåŠ¡å™¨å†…å­˜');
    }

    const noIndexQueries = slowQueries.filter(q => !q.indexUsed);
    if (noIndexQueries.length > 0) {
      recommendations.push(`å‘ç° ${noIndexQueries.length} ä¸ªæœªä½¿ç”¨ç´¢å¼•çš„æ…¢æŸ¥è¯¢ï¼Œå»ºè®®æ·»åŠ ç›¸åº”ç´¢å¼•`);
    }

    if (recommendations.length === 0) {
      recommendations.push('æ•°æ®åº“æ€§èƒ½è‰¯å¥½ï¼Œæ— éœ€ç‰¹åˆ«ä¼˜åŒ–');
    }

    return recommendations;
  }

  /**
   * ç”Ÿæˆå›¾è¡¨æ•°æ®
   */
  private generateChartData(timeRange?: { start: Date; end: Date }): any[] {
    let relevantMetrics = this.metrics;

    if (timeRange) {
      relevantMetrics = this.metrics.filter(
        m => m.timestamp >= timeRange.start && m.timestamp <= timeRange.end
      );
    }

    return [
      {
        type: 'line',
        title: 'æŸ¥è¯¢æ€§èƒ½è¶‹åŠ¿',
        data: relevantMetrics.map(m => ({
          timestamp: m.timestamp,
          avgQueryTime: m.performance.avgQueryTime,
          slowQueries: m.performance.slowQueries
        }))
      },
      {
        type: 'line',
        title: 'è¿æ¥ä½¿ç”¨è¶‹åŠ¿',
        data: relevantMetrics.map(m => ({
          timestamp: m.timestamp,
          active: m.connections.active,
          available: m.connections.available
        }))
      },
      {
        type: 'line',
        title: 'èµ„æºä½¿ç”¨è¶‹åŠ¿',
        data: relevantMetrics.map(m => ({
          timestamp: m.timestamp,
          memory: m.resources.memoryUsage,
          disk: m.resources.diskUsage
        }))
      }
    ];
  }

  /**
   * æ¸…ç†å†å²æ•°æ®
   */
  clearHistory(): void {
    this.metrics = [];
    this.slowQueries = [];
    logger.info('Database monitoring history cleared');
  }

  /**
   * æ›´æ–°é…ç½®
   */
  updateConfig(config: Partial<AlertConfig>): void {
    this.config = { ...this.config, ...config };
    logger.info('Database monitoring config updated', { config: this.config });
  }

  /**
   * è·å–ç›‘æ§çŠ¶æ€
   */
  getMonitoringStatus(): {
    isMonitoring: boolean;
    metricsCount: number;
    slowQueriesCount: number;
    lastCollection?: Date;
    config: AlertConfig;
  } {
    return {
      isMonitoring: this.isMonitoring,
      metricsCount: this.metrics.length,
      slowQueriesCount: this.slowQueries.length,
      lastCollection: this.metrics[this.metrics.length - 1]?.timestamp,
      config: this.config
    };
  }
}

/**
 * æ•°æ®åº“ç›‘æ§å·¥å…·å‡½æ•°
 */
export class DatabaseMonitorUtils {
  /**
   * åˆ›å»ºæ…¢æŸ¥è¯¢è®°å½•
   */
  static createSlowQueryRecord(
    collection: string,
    operation: string,
    query: any,
    executionTime: number,
    documentsExamined: number = 0,
    documentsReturned: number = 0,
    indexUsed: boolean = false
  ): SlowQueryRecord {
    return {
      id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      collection,
      operation,
      query,
      executionTime,
      documentsExamined,
      documentsReturned,
      indexUsed,
      timestamp: new Date(),
      stackTrace: new Error().stack
    };
  }

  /**
   * æ ¼å¼åŒ–æ€§èƒ½æŒ‡æ ‡
   */
  static formatMetrics(metrics: DatabaseMetrics): string {
    return `
æ•°æ®åº“æ€§èƒ½æŒ‡æ ‡ (${metrics.timestamp.toISOString()}):
è¿æ¥: ${metrics.connections.active}/${metrics.connections.available}
æ“ä½œ: æŸ¥è¯¢=${metrics.operations.queries}, æ’å…¥=${metrics.operations.inserts}
æ€§èƒ½: å¹³å‡æŸ¥è¯¢æ—¶é—´=${metrics.performance.avgQueryTime}ms, æ…¢æŸ¥è¯¢=${metrics.performance.slowQueries}
èµ„æº: å†…å­˜=${metrics.resources.memoryUsage}MB, ç£ç›˜=${metrics.resources.diskUsage}MB
é›†åˆæ•°: ${metrics.collections.length}
    `.trim();
  }

  /**
   * æ£€æŸ¥æŒ‡æ ‡å¥åº·çŠ¶æ€
   */
  static checkHealthStatus(metrics: DatabaseMetrics, config: AlertConfig): {
    status: 'healthy' | 'warning' | 'critical';
    issues: string[];
  } {
    const issues: string[] = [];
    let status: 'healthy' | 'warning' | 'critical' = 'healthy';

    // æ£€æŸ¥è¿æ¥ä½¿ç”¨ç‡
    const connectionUsage = (metrics.connections.active / metrics.connections.available) * 100;
    if (connectionUsage > config.connectionThreshold) {
      issues.push(`è¿æ¥ä½¿ç”¨ç‡è¿‡é«˜: ${connectionUsage.toFixed(1)}%`);
      status = 'warning';
    }

    // æ£€æŸ¥æ…¢æŸ¥è¯¢
    if (metrics.performance.slowQueries > 20) {
      issues.push(`æ…¢æŸ¥è¯¢è¿‡å¤š: ${metrics.performance.slowQueries}`);
      status = status === 'critical' ? 'critical' : 'warning';
    }

    // æ£€æŸ¥å†…å­˜ä½¿ç”¨
    if (metrics.resources.memoryUsage > 2000) {
      issues.push(`å†…å­˜ä½¿ç”¨è¿‡é«˜: ${metrics.resources.memoryUsage}MB`);
      status = 'critical';
    }

    return { status, issues };
  }
}

export default DatabaseMonitor;

import mongoose from 'mongoose';
import connectDB from '../mongodb';
import { User, Work, KnowledgeGraph as KnowledgeGraphModel, ContributionLog } from '../models';
import type { UserDocument, WorkDocument, ContributionLogDocument, IWork, IContributionLog } from '../models';
import type { KnowledgeGraph } from '@/types/knowledgeGraph';

/**
 * Database operation utilities
 * Provides common database operations with error handling
 */

export class DatabaseOperations {
  /**
   * Ensure database connection before operations
   */
  private static async ensureConnection() {
    await connectDB();
  }

  /**
   * User operations
   */
  static async createUser(userData: Partial<UserDocument>): Promise<UserDocument> {
    await this.ensureConnection();

    try {
      const user = new User(userData);
      return await user.save();
    } catch (error) {
      if (error instanceof mongoose.Error.ValidationError) {
        throw new Error(`Validation error: ${Object.values(error.errors).map(e => e.message).join(', ')}`);
      }
      throw error;
    }
  }

  static async findUserByEmail(email: string): Promise<UserDocument | null> {
    await this.ensureConnection();
    return await User.findOne({ email: email.toLowerCase() });
  }

  static async updateUser(userId: string, updateData: Partial<UserDocument>): Promise<UserDocument | null> {
    await this.ensureConnection();

    try {
      return await User.findByIdAndUpdate(
        userId,
        updateData,
        { new: true, runValidators: true }
      );
    } catch (error) {
      if (error instanceof mongoose.Error.ValidationError) {
        throw new Error(`Validation error: ${Object.values(error.errors).map(e => e.message).join(', ')}`);
      }
      throw error;
    }
  }

  /**
   * Work operations
   */
  static async createWork(workData: Partial<IWork>): Promise<IWork> {
    await this.ensureConnection();

    try {
      const work = new Work(workData);
      return await work.save();
    } catch (error) {
      if (error instanceof mongoose.Error.ValidationError) {
        throw new Error(`Validation error: ${Object.values(error.errors).map(e => e.message).join(', ')}`);
      }
      throw error;
    }
  }

  static async findPublicWorks(limit = 20, skip = 0): Promise<IWork[]> {
    await this.ensureConnection();
    return await Work.find({ isPublic: true, status: 'published' })
      .populate('author', 'name avatar')
      .sort({ createdAt: -1 })
      .limit(limit)
      .skip(skip);
  }

  static async findWorksByAuthor(authorId: string): Promise<IWork[]> {
    await this.ensureConnection();
    return await Work.find({ author: authorId })
      .populate('author', 'name avatar')
      .sort({ createdAt: -1 });
  }

  /**
   * Knowledge Graph operations
   */
  static async createKnowledgeGraph(graphData: Partial<KnowledgeGraph>): Promise<KnowledgeGraph> {
    await this.ensureConnection();

    try {
      const graph = new KnowledgeGraphModel(graphData);
      const savedGraph = await graph.save();
      return savedGraph.toObject();
    } catch (error) {
      if (error instanceof mongoose.Error.ValidationError) {
        throw new Error(`Validation error: ${Object.values(error.errors).map(e => e.message).join(', ')}`);
      }
      throw error;
    }
  }

  static async findPublicKnowledgeGraphs(limit = 20, skip = 0): Promise<KnowledgeGraph[]> {
    await this.ensureConnection();
    return await KnowledgeGraphModel.find({ isPublic: true, status: 'published' })
      .populate('author', 'name avatar')
      .sort({ createdAt: -1 })
      .limit(limit)
      .skip(skip);
  }

  /**
   * Contribution Log operations
   */
  static async logContribution(contributionData: Partial<IContributionLog>): Promise<IContributionLog> {
    await this.ensureConnection();

    try {
      const log = new ContributionLog(contributionData);
      return await log.save();
    } catch (error) {
      if (error instanceof mongoose.Error.ValidationError) {
        throw new Error(`Validation error: ${Object.values(error.errors).map(e => e.message).join(', ')}`);
      }
      throw error;
    }
  }

  static async getUserContributions(userId: string, limit = 50): Promise<IContributionLog[]> {
    await this.ensureConnection();
    return await ContributionLog.find({ user: userId })
      .sort({ createdAt: -1 })
      .limit(limit)
      .populate('user', 'name avatar');
  }

  /**
   * Generic operations
   */
  static async findById<T extends mongoose.Document>(
    model: mongoose.Model<T>,
    id: string
  ): Promise<T | null> {
    await this.ensureConnection();

    if (!mongoose.Types.ObjectId.isValid(id)) {
      throw new Error('Invalid ObjectId format');
    }

    return await model.findById(id);
  }

  static async deleteById<T extends mongoose.Document>(
    model: mongoose.Model<T>,
    id: string
  ): Promise<boolean> {
    await this.ensureConnection();

    if (!mongoose.Types.ObjectId.isValid(id)) {
      throw new Error('Invalid ObjectId format');
    }

    const result = await model.findByIdAndDelete(id);
    return result !== null;
  }

  /**
   * Health check operations
   */
  static async healthCheck(): Promise<{
    mongodb: { connected: boolean; status: string };
    models: { registered: string[] };
  }> {
    try {
      await this.ensureConnection();

      return {
        mongodb: {
          connected: mongoose.connection.readyState === 1,
          status: ['disconnected', 'connected', 'connecting', 'disconnecting'][mongoose.connection.readyState]
        },
        models: {
          registered: Object.keys(mongoose.models)
        }
      };
    } catch (error) {
      return {
        mongodb: {
          connected: false,
          status: 'error'
        },
        models: {
          registered: []
        }
      };
    }
  }
}

export default DatabaseOperations;

/**
 * æ•°æ®åº“æŸ¥è¯¢ä¼˜åŒ–å·¥å…·
 */
import { logger } from '@/lib/logging/logger';

/**
 * æŸ¥è¯¢æ€§èƒ½æŒ‡æ ‡
 */
export interface QueryPerformance {
  query: string;
  collection: string;
  executionTime: number;
  documentsExamined: number;
  documentsReturned: number;
  indexUsed: boolean;
  indexName?: string;
  efficiency: number; // 0-100
  recommendation: string;
  timestamp: Date;
}

/**
 * æŸ¥è¯¢ä¼˜åŒ–å»ºè®®
 */
export interface QueryOptimization {
  type: 'index' | 'query' | 'schema';
  priority: 'high' | 'medium' | 'low';
  description: string;
  impact: string;
  implementation: string;
}

/**
 * æ…¢æŸ¥è¯¢é˜ˆå€¼é…ç½®
 */
export interface SlowQueryConfig {
  thresholdMs: number;
  maxDocumentsExamined: number;
  minEfficiency: number;
  enableProfiling: boolean;
  sampleRate: number; // 0-1
}

/**
 * é»˜è®¤æ…¢æŸ¥è¯¢é…ç½®
 */
export const DEFAULT_SLOW_QUERY_CONFIG: SlowQueryConfig = {
  thresholdMs: 100,
  maxDocumentsExamined: 1000,
  minEfficiency: 50,
  enableProfiling: true,
  sampleRate: 0.1
};

/**
 * æŸ¥è¯¢ä¼˜åŒ–å™¨
 */
export class QueryOptimizer {
  private db: any;
  private config: SlowQueryConfig;
  private performanceHistory: QueryPerformance[] = [];
  private maxHistorySize = 1000;

  constructor(database: any, config: SlowQueryConfig = DEFAULT_SLOW_QUERY_CONFIG) {
    this.db = database;
    this.config = config;
  }

  /**
   * åˆ†ææŸ¥è¯¢æ€§èƒ½
   */
  async analyzeQuery(
    collection: string,
    query: any,
    options: any = {}
  ): Promise<QueryPerformance> {
    const startTime = Date.now();

    try {
      // ä½¿ç”¨explain()åˆ†ææŸ¥è¯¢è®¡åˆ’
      const coll = this.db.collection(collection);
      const explainResult = await coll.find(query, options).explain('executionStats');

      const executionTime = Date.now() - startTime;
      const stats = explainResult.executionStats;

      const performance: QueryPerformance = {
        query: JSON.stringify(query),
        collection,
        executionTime,
        documentsExamined: stats.totalDocsExamined || 0,
        documentsReturned: stats.totalDocsReturned || 0,
        indexUsed: this.isIndexUsed(explainResult),
        indexName: this.getUsedIndexName(explainResult),
        efficiency: this.calculateQueryEfficiency(stats),
        recommendation: this.generateQueryRecommendation(stats, explainResult),
        timestamp: new Date()
      };

      // è®°å½•æ€§èƒ½æ•°æ®
      this.recordPerformance(performance);

      // å¦‚æœæ˜¯æ…¢æŸ¥è¯¢ï¼Œè®°å½•è­¦å‘Š
      if (this.isSlowQuery(performance)) {
        logger.warn('Slow query detected', {
          collection,
          query: performance.query,
          executionTime,
          documentsExamined: performance.documentsExamined,
          indexUsed: performance.indexUsed
        });
      }

      return performance;
    } catch (error) {
      logger.error('Failed to analyze query', error instanceof Error ? error : new Error(String(error)), {
        collection,
        query: JSON.stringify(query)
      });
      throw error;
    }
  }

  /**
   * ä¼˜åŒ–æŸ¥è¯¢æ„å»ºå™¨
   */
  buildOptimizedQuery(
    collection: string,
    filters: Record<string, any>,
    options: {
      sort?: Record<string, 1 | -1>;
      limit?: number;
      skip?: number;
      projection?: Record<string, 1 | 0>;
    } = {}
  ): {
    query: Record<string, any>;
    mongoOptions: Record<string, any>;
    optimizations: string[];
  } {
    const optimizations: string[] = [];
    let optimizedQuery = { ...filters };
    const mongoOptions: Record<string, any> = {};

    // ä¼˜åŒ–æ’åºå­—æ®µ
    if (options.sort) {
      mongoOptions.sort = options.sort;

      // æ£€æŸ¥æ’åºå­—æ®µæ˜¯å¦æœ‰å¯¹åº”ç´¢å¼•
      const sortFields = Object.keys(options.sort);
      if (sortFields.length > 0) {
        optimizations.push(`å»ºè®®ä¸ºæ’åºå­—æ®µ [${sortFields.join(', ')}] åˆ›å»ºç´¢å¼•`);
      }
    }

    // ä¼˜åŒ–æŠ•å½±
    if (options.projection) {
      mongoOptions.projection = options.projection;
      optimizations.push('ä½¿ç”¨å­—æ®µæŠ•å½±å‡å°‘ç½‘ç»œä¼ è¾“');
    }

    // ä¼˜åŒ–åˆ†é¡µ
    if (options.limit) {
      mongoOptions.limit = options.limit;

      if (options.skip && options.skip > 1000) {
        optimizations.push('å¤§åç§»é‡åˆ†é¡µæ€§èƒ½è¾ƒå·®ï¼Œå»ºè®®ä½¿ç”¨åŸºäºæ¸¸æ ‡çš„åˆ†é¡µ');
      }

      if (options.skip) {
        mongoOptions.skip = options.skip;
      }
    }

    // ä¼˜åŒ–æŸ¥è¯¢æ¡ä»¶
    optimizedQuery = this.optimizeQueryConditions(optimizedQuery, optimizations);

    return {
      query: optimizedQuery,
      mongoOptions,
      optimizations
    };
  }

  /**
   * ä¼˜åŒ–æŸ¥è¯¢æ¡ä»¶
   */
  private optimizeQueryConditions(
    query: Record<string, any>,
    optimizations: string[]
  ): Record<string, any> {
    const optimized = { ...query };

    // ä¼˜åŒ–æ­£åˆ™è¡¨è¾¾å¼æŸ¥è¯¢
    for (const [field, value] of Object.entries(optimized)) {
      if (value instanceof RegExp) {
        // å»ºè®®ä½¿ç”¨æ–‡æœ¬ç´¢å¼•æ›¿ä»£æ­£åˆ™è¡¨è¾¾å¼
        optimizations.push(`å­—æ®µ ${field} ä½¿ç”¨æ­£åˆ™è¡¨è¾¾å¼ï¼Œå»ºè®®åˆ›å»ºæ–‡æœ¬ç´¢å¼•æé«˜æ€§èƒ½`);

        // å¦‚æœæ˜¯ç®€å•çš„å‰ç¼€åŒ¹é…ï¼Œè½¬æ¢ä¸ºèŒƒå›´æŸ¥è¯¢
        const regexStr = value.source;
        if (regexStr.startsWith('^') && !regexStr.includes('*') && !regexStr.includes('+')) {
          const prefix = regexStr.substring(1);
          optimized[field] = {
            $gte: prefix,
            $lt: prefix + '\uffff'
          };
          optimizations.push(`å°† ${field} çš„æ­£åˆ™è¡¨è¾¾å¼è½¬æ¢ä¸ºèŒƒå›´æŸ¥è¯¢`);
        }
      }

      // ä¼˜åŒ–æ•°ç»„æŸ¥è¯¢
      if (Array.isArray(value)) {
        if (value.length > 100) {
          optimizations.push(`å­—æ®µ ${field} çš„ $in æŸ¥è¯¢åŒ…å«è¿‡å¤šå€¼ (${value.length})ï¼Œè€ƒè™‘åˆ†æ‰¹æŸ¥è¯¢`);
        }
      }

      // ä¼˜åŒ–æ—¥æœŸèŒƒå›´æŸ¥è¯¢
      if (field.includes('At') || field.includes('Date')) {
        if (typeof value === 'object' && value.$gte && value.$lte) {
          optimizations.push(`æ—¥æœŸèŒƒå›´æŸ¥è¯¢å·²ä¼˜åŒ–ï¼Œå»ºè®®ä¸º ${field} åˆ›å»ºç´¢å¼•`);
        }
      }
    }

    return optimized;
  }

  /**
   * ç”Ÿæˆèšåˆç®¡é“ä¼˜åŒ–å»ºè®®
   */
  optimizeAggregationPipeline(
    collection: string,
    pipeline: any[]
  ): {
    optimizedPipeline: any[];
    optimizations: string[];
  } {
    const optimizations: string[] = [];
    const optimizedPipeline = [...pipeline];

    // å°† $match é˜¶æ®µå°½å¯èƒ½å‰ç§»
    const matchStages = optimizedPipeline.filter(stage => stage.$match);
    const nonMatchStages = optimizedPipeline.filter(stage => !stage.$match);

    if (matchStages.length > 0) {
      optimizedPipeline.splice(0, optimizedPipeline.length, ...matchStages, ...nonMatchStages);
      optimizations.push('å°† $match é˜¶æ®µå‰ç§»ä»¥å‡å°‘å¤„ç†çš„æ–‡æ¡£æ•°é‡');
    }

    // æ£€æŸ¥ $sort å’Œ $limit çš„ç»„åˆ
    const sortIndex = optimizedPipeline.findIndex(stage => stage.$sort);
    const limitIndex = optimizedPipeline.findIndex(stage => stage.$limit);

    if (sortIndex !== -1 && limitIndex !== -1 && limitIndex > sortIndex) {
      // å°† $limit ç§»åˆ° $sort ä¹‹å
      const limitStage = optimizedPipeline.splice(limitIndex, 1)[0];
      optimizedPipeline.splice(sortIndex + 1, 0, limitStage);
      optimizations.push('å°† $limit ç§»åˆ° $sort ä¹‹åä»¥æé«˜æ€§èƒ½');
    }

    // æ£€æŸ¥ä¸å¿…è¦çš„ $project é˜¶æ®µ
    const projectStages = optimizedPipeline.filter(stage => stage.$project);
    if (projectStages.length > 1) {
      optimizations.push('å‘ç°å¤šä¸ª $project é˜¶æ®µï¼Œè€ƒè™‘åˆå¹¶ä»¥å‡å°‘å¤„ç†å¼€é”€');
    }

    // æ£€æŸ¥ $lookup ä¼˜åŒ–
    const lookupStages = optimizedPipeline.filter(stage => stage.$lookup);
    if (lookupStages.length > 0) {
      optimizations.push('$lookup æ“ä½œè¾ƒé‡ï¼Œç¡®ä¿å…³è”å­—æ®µæœ‰ç´¢å¼•ï¼Œè€ƒè™‘ä½¿ç”¨ $match é¢„è¿‡æ»¤');
    }

    return {
      optimizedPipeline,
      optimizations
    };
  }

  /**
   * è·å–æ…¢æŸ¥è¯¢æŠ¥å‘Š
   */
  getSlowQueryReport(timeRange: { start: Date; end: Date }): {
    totalQueries: number;
    slowQueries: QueryPerformance[];
    topSlowQueries: QueryPerformance[];
    recommendations: QueryOptimization[];
  } {
    const queriesInRange = this.performanceHistory.filter(
      perf => perf.timestamp >= timeRange.start && perf.timestamp <= timeRange.end
    );

    const slowQueries = queriesInRange.filter(perf => this.isSlowQuery(perf));

    // æŒ‰æ‰§è¡Œæ—¶é—´æ’åºï¼Œå–å‰10ä¸ªæœ€æ…¢çš„æŸ¥è¯¢
    const topSlowQueries = [...slowQueries]
      .sort((a, b) => b.executionTime - a.executionTime)
      .slice(0, 10);

    const recommendations = this.generateOptimizationRecommendations(slowQueries);

    return {
      totalQueries: queriesInRange.length,
      slowQueries,
      topSlowQueries,
      recommendations
    };
  }

  /**
   * ç”Ÿæˆä¼˜åŒ–å»ºè®®
   */
  private generateOptimizationRecommendations(slowQueries: QueryPerformance[]): QueryOptimization[] {
    const recommendations: QueryOptimization[] = [];
    const queryPatterns = new Map<string, QueryPerformance[]>();

    // æŒ‰æŸ¥è¯¢æ¨¡å¼åˆ†ç»„
    slowQueries.forEach(query => {
      const pattern = this.extractQueryPattern(query.query);
      if (!queryPatterns.has(pattern)) {
        queryPatterns.set(pattern, []);
      }
      queryPatterns.get(pattern)!.push(query);
    });

    // ä¸ºæ¯ä¸ªæ¨¡å¼ç”Ÿæˆå»ºè®®
    queryPatterns.forEach((queries, pattern) => {
      const avgExecutionTime = queries.reduce((sum, q) => sum + q.executionTime, 0) / queries.length;
      const avgEfficiency = queries.reduce((sum, q) => sum + q.efficiency, 0) / queries.length;

      if (avgEfficiency < 30) {
        recommendations.push({
          type: 'index',
          priority: 'high',
          description: `æŸ¥è¯¢æ¨¡å¼ "${pattern}" æ•ˆç‡è¿‡ä½ (${avgEfficiency.toFixed(1)}%)`,
          impact: `å½±å“ ${queries.length} ä¸ªæŸ¥è¯¢ï¼Œå¹³å‡æ‰§è¡Œæ—¶é—´ ${avgExecutionTime.toFixed(1)}ms`,
          implementation: 'ä¸ºæŸ¥è¯¢æ¡ä»¶å­—æ®µåˆ›å»ºå¤åˆç´¢å¼•'
        });
      }

      if (avgExecutionTime > 1000) {
        recommendations.push({
          type: 'query',
          priority: 'high',
          description: `æŸ¥è¯¢æ¨¡å¼ "${pattern}" æ‰§è¡Œæ—¶é—´è¿‡é•¿`,
          impact: `å¹³å‡æ‰§è¡Œæ—¶é—´ ${avgExecutionTime.toFixed(1)}ms`,
          implementation: 'ä¼˜åŒ–æŸ¥è¯¢æ¡ä»¶ï¼Œå‡å°‘æ‰«æçš„æ–‡æ¡£æ•°é‡'
        });
      }

      const noIndexQueries = queries.filter(q => !q.indexUsed);
      if (noIndexQueries.length > 0) {
        recommendations.push({
          type: 'index',
          priority: 'medium',
          description: `æŸ¥è¯¢æ¨¡å¼ "${pattern}" æœªä½¿ç”¨ç´¢å¼•`,
          impact: `${noIndexQueries.length} ä¸ªæŸ¥è¯¢è¿›è¡Œå…¨è¡¨æ‰«æ`,
          implementation: 'ä¸ºæŸ¥è¯¢å­—æ®µåˆ›å»ºé€‚å½“çš„ç´¢å¼•'
        });
      }
    });

    return recommendations;
  }

  /**
   * æå–æŸ¥è¯¢æ¨¡å¼
   */
  private extractQueryPattern(queryStr: string): string {
    try {
      const query = JSON.parse(queryStr);
      const pattern: Record<string, string> = {};

      for (const [key, value] of Object.entries(query)) {
        if (typeof value === 'object' && value !== null) {
          pattern[key] = 'object';
        } else {
          pattern[key] = typeof value;
        }
      }

      return JSON.stringify(pattern);
    } catch {
      return 'invalid_query';
    }
  }

  /**
   * æ£€æŸ¥æ˜¯å¦ä½¿ç”¨äº†ç´¢å¼•
   */
  private isIndexUsed(explainResult: any): boolean {
    const winningPlan = explainResult.queryPlanner?.winningPlan;
    return winningPlan?.stage !== 'COLLSCAN';
  }

  /**
   * è·å–ä½¿ç”¨çš„ç´¢å¼•åç§°
   */
  private getUsedIndexName(explainResult: any): string | undefined {
    const winningPlan = explainResult.queryPlanner?.winningPlan;

    if (winningPlan?.stage === 'IXSCAN') {
      return winningPlan.indexName;
    }

    if (winningPlan?.inputStage?.stage === 'IXSCAN') {
      return winningPlan.inputStage.indexName;
    }

    return undefined;
  }

  /**
   * è®¡ç®—æŸ¥è¯¢æ•ˆç‡
   */
  private calculateQueryEfficiency(stats: any): number {
    const examined = stats.totalDocsExamined || 0;
    const returned = stats.totalDocsReturned || 0;

    if (examined === 0) return 100;
    if (returned === 0) return 0;

    return Math.round((returned / examined) * 100);
  }

  /**
   * ç”ŸæˆæŸ¥è¯¢å»ºè®®
   */
  private generateQueryRecommendation(stats: any, explainResult: any): string {
    const efficiency = this.calculateQueryEfficiency(stats);
    const examined = stats.totalDocsExamined || 0;
    const returned = stats.totalDocsReturned || 0;
    const indexUsed = this.isIndexUsed(explainResult);

    if (!indexUsed) {
      return 'æŸ¥è¯¢æœªä½¿ç”¨ç´¢å¼•ï¼Œå»ºè®®ä¸ºæŸ¥è¯¢å­—æ®µåˆ›å»ºç´¢å¼•';
    }

    if (efficiency < 10) {
      return `æŸ¥è¯¢æ•ˆç‡è¿‡ä½ (${efficiency}%)ï¼Œå»ºè®®ä¼˜åŒ–æŸ¥è¯¢æ¡ä»¶æˆ–ç´¢å¼•`;
    }

    if (examined > 10000) {
      return `æ‰«ææ–‡æ¡£è¿‡å¤š (${examined})ï¼Œå»ºè®®æ·»åŠ æ›´ç²¾ç¡®çš„æŸ¥è¯¢æ¡ä»¶`;
    }

    if (efficiency < 50) {
      return `æŸ¥è¯¢æ•ˆç‡åä½ (${efficiency}%)ï¼Œè€ƒè™‘ä¼˜åŒ–ç´¢å¼•æˆ–æŸ¥è¯¢æ¡ä»¶`;
    }

    return 'æŸ¥è¯¢æ€§èƒ½è‰¯å¥½';
  }

  /**
   * åˆ¤æ–­æ˜¯å¦ä¸ºæ…¢æŸ¥è¯¢
   */
  private isSlowQuery(performance: QueryPerformance): boolean {
    return performance.executionTime > this.config.thresholdMs ||
           performance.documentsExamined > this.config.maxDocumentsExamined ||
           performance.efficiency < this.config.minEfficiency;
  }

  /**
   * è®°å½•æ€§èƒ½æ•°æ®
   */
  private recordPerformance(performance: QueryPerformance): void {
    this.performanceHistory.push(performance);

    // é™åˆ¶å†å²è®°å½•å¤§å°
    if (this.performanceHistory.length > this.maxHistorySize) {
      this.performanceHistory.shift();
    }
  }

  /**
   * æ¸…ç†æ€§èƒ½å†å²
   */
  clearPerformanceHistory(): void {
    this.performanceHistory = [];
  }

  /**
   * è·å–æ€§èƒ½ç»Ÿè®¡
   */
  getPerformanceStats(): {
    totalQueries: number;
    averageExecutionTime: number;
    slowQueryRate: number;
    indexUsageRate: number;
    averageEfficiency: number;
  } {
    if (this.performanceHistory.length === 0) {
      return {
        totalQueries: 0,
        averageExecutionTime: 0,
        slowQueryRate: 0,
        indexUsageRate: 0,
        averageEfficiency: 0
      };
    }

    const totalQueries = this.performanceHistory.length;
    const totalExecutionTime = this.performanceHistory.reduce((sum, p) => sum + p.executionTime, 0);
    const slowQueries = this.performanceHistory.filter(p => this.isSlowQuery(p)).length;
    const indexedQueries = this.performanceHistory.filter(p => p.indexUsed).length;
    const totalEfficiency = this.performanceHistory.reduce((sum, p) => sum + p.efficiency, 0);

    return {
      totalQueries,
      averageExecutionTime: totalExecutionTime / totalQueries,
      slowQueryRate: (slowQueries / totalQueries) * 100,
      indexUsageRate: (indexedQueries / totalQueries) * 100,
      averageEfficiency: totalEfficiency / totalQueries
    };
  }
}

/**
 * æŸ¥è¯¢æ„å»ºå™¨è¾…åŠ©ç±»
 */
export class QueryBuilder {
  private query: Record<string, any> = {};
  private options: Record<string, any> = {};

  /**
   * æ·»åŠ ç­‰å€¼æ¡ä»¶
   */
  equals(field: string, value: any): QueryBuilder {
    this.query[field] = value;
    return this;
  }

  /**
   * æ·»åŠ èŒƒå›´æ¡ä»¶
   */
  range(field: string, min?: any, max?: any): QueryBuilder {
    const condition: Record<string, any> = {};
    if (min !== undefined) condition.$gte = min;
    if (max !== undefined) condition.$lte = max;
    this.query[field] = condition;
    return this;
  }

  /**
   * æ·»åŠ åŒ…å«æ¡ä»¶
   */
  in(field: string, values: any[]): QueryBuilder {
    this.query[field] = { $in: values };
    return this;
  }

  /**
   * æ·»åŠ æ–‡æœ¬æœç´¢
   */
  text(searchText: string): QueryBuilder {
    this.query.$text = { $search: searchText };
    return this;
  }

  /**
   * æ·»åŠ æ’åº
   */
  sort(field: string, direction: 1 | -1 = 1): QueryBuilder {
    if (!this.options.sort) this.options.sort = {};
    this.options.sort[field] = direction;
    return this;
  }

  /**
   * æ·»åŠ é™åˆ¶
   */
  limit(count: number): QueryBuilder {
    this.options.limit = count;
    return this;
  }

  /**
   * æ·»åŠ è·³è¿‡
   */
  skip(count: number): QueryBuilder {
    this.options.skip = count;
    return this;
  }

  /**
   * æ·»åŠ å­—æ®µæŠ•å½±
   */
  project(fields: Record<string, 1 | 0>): QueryBuilder {
    this.options.projection = fields;
    return this;
  }

  /**
   * æ„å»ºæŸ¥è¯¢
   */
  build(): { query: Record<string, any>; options: Record<string, any> } {
    return {
      query: this.query,
      options: this.options
    };
  }
}

export default QueryOptimizer;

/**
 * æ•°æ®åº“åˆ†é¡µä¼˜åŒ–
 */
import { logger } from '@/lib/logging/logger';

/**
 * åˆ†é¡µå‚æ•°
 */
export interface PaginationParams {
