    };
  }

  /**
   * éªŒè¯å…³è”é…ç½®
   */
  static validateRelationConfig(config: RelationConfig): {
    valid: boolean;
    errors: string[];
  } {
    const errors: string[] = [];

    if (!config.from) {
      errors.push('å…³è”é›†åˆåç§°ä¸èƒ½ä¸ºç©º');
    }

    if (!config.localField) {
      errors.push('æœ¬åœ°å­—æ®µä¸èƒ½ä¸ºç©º');
    }

    if (!config.foreignField) {
      errors.push('å¤–é”®å­—æ®µä¸èƒ½ä¸ºç©º');
    }

    if (!config.as) {
      errors.push('ç»“æœå­—æ®µåä¸èƒ½ä¸ºç©º');
    }

    if (config.cacheTTL && config.cacheTTL < 0) {
      errors.push('ç¼“å­˜TTLå¿…é¡»å¤§äº0');
    }

    return {
      valid: errors.length === 0,
      errors
    };
  }
}

