import { ErrorCode, ErrorType, ErrorSeverity, ErrorDetails, ErrorContext, getErrorMetadata } from './types';

/**
 * è‡ªå®šä¹‰é”™è¯¯åŸºç±»
 */
export class CustomError extends Error {
  public readonly code: ErrorCode;
  public readonly type: ErrorType;
  public readonly severity: ErrorSeverity;
  public readonly httpStatus: number;
  public readonly retryable: boolean;
  public readonly userMessage: string;
  public readonly developerMessage: string;
  public readonly details?: ErrorDetails[];
  public readonly context?: ErrorContext;
  public readonly timestamp: string;
  public readonly traceId?: string;

  constructor(
    code: ErrorCode,
    message?: string,
    details?: ErrorDetails[],
    context?: ErrorContext,
    cause?: Error
  ) {
    const metadata = getErrorMetadata(code);
    const finalMessage = message || metadata.developerMessage;

    super(finalMessage);

    // è®¾ç½®é”™è¯¯åç§°
    this.name = this.constructor.name;

    // è®¾ç½®é”™è¯¯å±æ€§
    this.code = code;
    this.type = metadata.type;
    this.severity = metadata.severity;
    this.httpStatus = metadata.httpStatus;
    this.retryable = metadata.retryable;
    this.userMessage = metadata.userMessage;
    this.developerMessage = finalMessage;
    this.details = details;
    this.context = context;
    this.timestamp = new Date().toISOString();
    this.traceId = context?.traceId;

    // ä¿æŒå †æ ˆè·Ÿè¸ª
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, this.constructor);
    }

    // è®¾ç½®åŸå› 
    if (cause) {
      this.cause = cause;
    }
  }

  /**
   * è½¬æ¢ä¸ºJSONå¯¹è±¡
   */
  toJSON(): Record<string, any> {
    return {
      name: this.name,
      code: this.code,
      type: this.type,
      severity: this.severity,
      httpStatus: this.httpStatus,
      retryable: this.retryable,
      message: this.message,
      userMessage: this.userMessage,
      developerMessage: this.developerMessage,
      details: this.details,
      context: this.context,
      timestamp: this.timestamp,
      traceId: this.traceId,
      stack: this.stack
    };
  }

  /**
   * è½¬æ¢ä¸ºAPIå“åº”æ ¼å¼
   */
  toApiResponse() {
    return {
      success: false as const,
      error: {
        code: this.code,
        type: this.type,
        message: this.userMessage,
        details: this.details,
        context: this.context,
        timestamp: this.timestamp,
        traceId: this.traceId
      }
    };
  }

  /**
   * åˆ›å»ºå¸¦ä¸Šä¸‹æ–‡çš„æ–°é”™è¯¯
   */
  withContext(context: ErrorContext): CustomError {
    return new CustomError(
      this.code,
      this.message,
      this.details,
      { ...this.context, ...context },
      this
    );
  }

  /**
   * åˆ›å»ºå¸¦è¯¦æƒ…çš„æ–°é”™è¯¯
   */
  withDetails(details: ErrorDetails[]): CustomError {
    return new CustomError(
      this.code,
      this.message,
      [...(this.details || []), ...details],
      this.context,
      this
    );
  }

  /**
   * æ£€æŸ¥æ˜¯å¦ä¸ºç‰¹å®šç±»å‹çš„é”™è¯¯
   */
  isType(type: ErrorType): boolean {
    return this.type === type;
  }

  /**
   * æ£€æŸ¥æ˜¯å¦ä¸ºç‰¹å®šä¸¥é‡ç¨‹åº¦çš„é”™è¯¯
   */
  isSeverity(severity: ErrorSeverity): boolean {
    return this.severity === severity;
  }

  /**
   * æ£€æŸ¥æ˜¯å¦å¯é‡è¯•
   */
  isRetryable(): boolean {
    return this.retryable;
  }

  /**
   * æ£€æŸ¥æ˜¯å¦ä¸ºå®¢æˆ·ç«¯é”™è¯¯ï¼ˆ4xxï¼‰
   */
  isClientError(): boolean {
    return this.httpStatus >= 400 && this.httpStatus < 500;
  }

  /**
   * æ£€æŸ¥æ˜¯å¦ä¸ºæœåŠ¡å™¨é”™è¯¯ï¼ˆ5xxï¼‰
   */
  isServerError(): boolean {
    return this.httpStatus >= 500;
  }

  /**
   * è·å–æ—¥å¿—çº§åˆ«
   */
  getLogLevel(): 'error' | 'warn' | 'info' {
    switch (this.severity) {
      case ErrorSeverity.CRITICAL:
      case ErrorSeverity.HIGH:
        return 'error';
      case ErrorSeverity.MEDIUM:
        return 'warn';
      case ErrorSeverity.LOW:
      default:
        return 'info';
    }
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºéªŒè¯é”™è¯¯
   */
  static validation(
    message?: string,
    details?: ErrorDetails[],
    context?: ErrorContext
  ): CustomError {
    return new CustomError(ErrorCode.VALIDATION_FAILED, message, details, context);
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºè®¤è¯é”™è¯¯
   */
  static unauthorized(
    message?: string,
    context?: ErrorContext
  ): CustomError {
    return new CustomError(ErrorCode.UNAUTHORIZED, message, undefined, context);
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºæƒé™é”™è¯¯
   */
  static forbidden(
    message?: string,
    context?: ErrorContext
  ): CustomError {
    return new CustomError(ErrorCode.FORBIDDEN, message, undefined, context);
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºèµ„æºä¸å­˜åœ¨é”™è¯¯
   */
  static notFound(
    resource?: string,
    context?: ErrorContext
  ): CustomError {
    const message = resource ? `${resource} not found` : undefined;
    return new CustomError(ErrorCode.RESOURCE_NOT_FOUND, message, undefined, context);
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºå†²çªé”™è¯¯
   */
  static conflict(
    message?: string,
    context?: ErrorContext
  ): CustomError {
    return new CustomError(ErrorCode.RESOURCE_CONFLICT, message, undefined, context);
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºé™æµé”™è¯¯
   */
  static rateLimit(
    message?: string,
    context?: ErrorContext
  ): CustomError {
    return new CustomError(ErrorCode.RATE_LIMIT_EXCEEDED, message, undefined, context);
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºå†…éƒ¨æœåŠ¡å™¨é”™è¯¯
   */
  static internal(
    message?: string,
    cause?: Error,
    context?: ErrorContext
  ): CustomError {
    return new CustomError(ErrorCode.INTERNAL_SERVER_ERROR, message, undefined, context, cause);
  }

  /**
   * é™æ€å·¥å‚æ–¹æ³•ï¼šåˆ›å»ºå¤–éƒ¨æœåŠ¡é”™è¯¯
   */
  static externalService(
    service: string,
    message?: string,
    context?: ErrorContext
  ): CustomError {
    const finalMessage = message || `External service ${service} error`;
    return new CustomError(ErrorCode.EXTERNAL_SERVICE_ERROR, finalMessage, undefined, context);
  }

  /**
   * ä»æ ‡å‡†Erroråˆ›å»ºCustomError
   */
  static fromError(
    error: Error,
    code: ErrorCode = ErrorCode.UNKNOWN_ERROR,
    context?: ErrorContext
  ): CustomError {
    if (error instanceof CustomError) {
      return context ? error.withContext(context) : error;
    }

    return new CustomError(
      code,
      error.message,
      undefined,
      context,
      error
    );
  }

  /**
   * ä»HTTPçŠ¶æ€ç åˆ›å»ºé”™è¯¯
   */
  static fromHttpStatus(
    status: number,
    message?: string,
    context?: ErrorContext
  ): CustomError {
    let code: ErrorCode;

    switch (status) {
      case 400:
        code = ErrorCode.INVALID_INPUT;
        break;
      case 401:
        code = ErrorCode.UNAUTHORIZED;
        break;
      case 403:
        code = ErrorCode.FORBIDDEN;
        break;
      case 404:
        code = ErrorCode.RESOURCE_NOT_FOUND;
        break;
      case 409:
        code = ErrorCode.RESOURCE_CONFLICT;
        break;
      case 422:
        code = ErrorCode.VALIDATION_FAILED;
        break;
      case 429:
        code = ErrorCode.RATE_LIMIT_EXCEEDED;
        break;
      case 500:
        code = ErrorCode.INTERNAL_SERVER_ERROR;
        break;
      case 502:
        code = ErrorCode.EXTERNAL_SERVICE_ERROR;
        break;
      case 503:
        code = ErrorCode.SERVICE_UNAVAILABLE;
        break;
      default:
        code = ErrorCode.UNKNOWN_ERROR;
    }

    return new CustomError(code, message, undefined, context);
  }
}

