/**
 * å›¾è°±æ¸²æŸ“å™¨
 * æ ¸å¿ƒçš„D3.jså›¾è°±æ¸²æŸ“å¼•æ“
 */
import * as d3 from 'd3'
import {
  D3Node,
  D3Edge,
  GraphVisualizationData,
  GraphRenderer,
  GraphRendererConfig,
  LayoutConfig,
  VisualConfig,
  InteractionConfig,
  GraphEvent,
  GraphEventType,
  GraphEventHandler,
  DEFAULT_LAYOUT_CONFIG,
  DEFAULT_VISUAL_CONFIG,
  DEFAULT_INTERACTION_CONFIG
} from './types'
import {
  createZoomBehavior,
  createDragBehavior,
  createTooltip,
  showTooltip,
  hideTooltip,
  generateNodeTooltip,
  generateEdgeTooltip
} from './d3-utils'
import { layoutManager } from './layout-algorithms'

export class D3GraphRenderer implements GraphRenderer {
  private container: HTMLElement
  private svg: d3.Selection<SVGSVGElement, unknown, null, undefined>
  private g: d3.Selection<SVGGElement, unknown, null, undefined>
  private tooltip: d3.Selection<HTMLDivElement, unknown, HTMLElement, any>

  private layoutConfig: LayoutConfig
  private visualConfig: VisualConfig
  private interactionConfig: InteractionConfig

  private nodes: D3Node[] = []
  private links: D3Edge[] = []
  private selectedNodes: Set<string> = new Set()
  private selectedEdges: Set<string> = new Set()

  private eventHandlers: Map<GraphEventType, GraphEventHandler[]> = new Map()
  private zoom: d3.ZoomBehavior<SVGSVGElement, unknown> | null = null

  constructor(config: GraphRendererConfig) {
    this.container = config.container
    this.layoutConfig = config.layout || DEFAULT_LAYOUT_CONFIG
    this.visualConfig = config.visual || DEFAULT_VISUAL_CONFIG
    this.interactionConfig = config.interaction || DEFAULT_INTERACTION_CONFIG

    this.initializeSVG()
    this.initializeTooltip()

    if (config.data) {
      this.render(config.data)
    }
  }

  private initializeSVG(): void {
    // æ¸…ç†ç°æœ‰å†…å®¹
    d3.select(this.container).selectAll('*').remove()

    // åˆ›å»ºSVG
    this.svg = d3.select(this.container)
      .append('svg')
      .attr('width', '100%')
      .attr('height', '100%')
      .style('background', this.visualConfig.colors.background)

    // åˆ›å»ºä¸»å®¹å™¨ç»„
    this.g = this.svg.append('g')
      .attr('class', 'graph-container')

    // åˆ›å»ºå›¾å±‚
    this.g.append('g').attr('class', 'edges')
    this.g.append('g').attr('class', 'nodes')
  }

  private initializeTooltip(): void {
    this.tooltip = createTooltip()
  }

  render(data: GraphVisualizationData): void {
    this.nodes = [...data.nodes]
    this.links = [...data.links]

    this.renderEdges()
    this.renderNodes()
    this.applyLayout()
    this.enableInteraction(this.interactionConfig)
  }

  update(data: GraphVisualizationData): void {
    this.nodes = [...data.nodes]
    this.links = [...data.links]

    this.updateEdges()
    this.updateNodes()
    this.applyLayout()
  }

  clear(): void {
    this.g.selectAll('.nodes').selectAll('*').remove()
    this.g.selectAll('.edges').selectAll('*').remove()
    this.nodes = []
    this.links = []
    this.selectedNodes.clear()
    this.selectedEdges.clear()
  }

  private renderNodes(): void {
    const nodeGroup = this.g.select('.nodes')

    const nodeSelection = nodeGroup
      .selectAll<SVGGElement, D3Node>('.node')
      .data(this.nodes, d => d.id)

    // ç§»é™¤æ—§èŠ‚ç‚¹
    nodeSelection.exit().remove()

    // åˆ›å»ºæ–°èŠ‚ç‚¹ç»„
    const nodeEnter = nodeSelection.enter()
      .append('g')
      .attr('class', 'node')
      .style('cursor', 'pointer')

    // æ·»åŠ èŠ‚ç‚¹åœ†åœˆ
    nodeEnter.append('circle')
      .attr('r', d => d.radius)
      .attr('fill', d => d.color)
      .attr('stroke', d => d.strokeColor)
      .attr('stroke-width', d => d.strokeWidth)
      .attr('opacity', d => d.opacity)

    // æ·»åŠ èŠ‚ç‚¹æ ‡ç­¾
    nodeEnter.append('text')
      .attr('class', 'node-label')
      .attr('text-anchor', 'middle')
      .attr('dy', '.35em')
      .style('font-size', '12px')
      .style('font-weight', 'bold')
      .style('fill', '#333')
      .style('pointer-events', 'none')
      .text(d => d.label)

    // åˆå¹¶é€‰æ‹©é›†
    const nodeUpdate = nodeEnter.merge(nodeSelection)

    // ç»‘å®šäº‹ä»¶
    this.bindNodeEvents(nodeUpdate)
  }

  private updateNodes(): void {
    const nodeGroup = this.g.select('.nodes')
    const nodeSelection = nodeGroup.selectAll<SVGGElement, D3Node>('.node')
      .data(this.nodes, d => d.id)

    // æ›´æ–°ç°æœ‰èŠ‚ç‚¹
    nodeSelection.select('circle')
      .attr('r', d => d.radius)
      .attr('fill', d => d.color)
      .attr('stroke', d => d.strokeColor)
      .attr('stroke-width', d => d.strokeWidth)
      .attr('opacity', d => d.opacity)

    nodeSelection.select('.node-label')
      .text(d => d.label)
  }

  private renderEdges(): void {
    const edgeGroup = this.g.select('.edges')

    const edgeSelection = edgeGroup
      .selectAll<SVGLineElement, D3Edge>('.edge')
      .data(this.links, d => d.id)

    // ç§»é™¤æ—§è¾¹
    edgeSelection.exit().remove()

    // åˆ›å»ºæ–°è¾¹
    const edgeEnter = edgeSelection.enter()
      .append('line')
      .attr('class', 'edge')
      .attr('stroke', d => d.color)
      .attr('stroke-width', d => d.strokeWidth)
      .attr('stroke-dasharray', d => d.strokeDasharray || null)
      .attr('opacity', d => d.opacity)
      .style('cursor', 'pointer')

    // åˆå¹¶é€‰æ‹©é›†
    const edgeUpdate = edgeEnter.merge(edgeSelection)

    // ç»‘å®šäº‹ä»¶
    this.bindEdgeEvents(edgeUpdate)
  }

  private updateEdges(): void {
    const edgeGroup = this.g.select('.edges')
    const edgeSelection = edgeGroup.selectAll<SVGLineElement, D3Edge>('.edge')
      .data(this.links, d => d.id)

    // æ›´æ–°ç°æœ‰è¾¹
    edgeSelection
      .attr('stroke', d => d.color)
      .attr('stroke-width', d => d.strokeWidth)
      .attr('stroke-dasharray', d => d.strokeDasharray || null)
      .attr('opacity', d => d.opacity)
  }

  private bindNodeEvents(selection: d3.Selection<SVGGElement, D3Node, SVGGElement, unknown>): void {
    selection
      .on('click', (event, d) => {
        event.stopPropagation()
        this.handleNodeClick(d, event)
      })
      .on('dblclick', (event, d) => {
        event.stopPropagation()
        this.handleNodeDoubleClick(d, event)
      })
      .on('mouseenter', (event, d) => {
        this.handleNodeMouseEnter(d, event)
      })
      .on('mouseleave', (event, d) => {
        this.handleNodeMouseLeave(d, event)
      })
  }

  private bindEdgeEvents(selection: d3.Selection<SVGLineElement, D3Edge, SVGGElement, unknown>): void {
    selection
      .on('click', (event, d) => {
        event.stopPropagation()
        this.handleEdgeClick(d, event)
      })
      .on('mouseenter', (event, d) => {
        this.handleEdgeMouseEnter(d, event)
      })
      .on('mouseleave', (event, d) => {
        this.handleEdgeMouseLeave(d, event)
      })
  }

  private handleNodeClick(node: D3Node, event: Event): void {
    if (this.interactionConfig.selection.enabled) {
      if (this.interactionConfig.selection.multiSelect && (event as MouseEvent).ctrlKey) {
        this.toggleNodeSelection(node.id)
      } else {
        this.selectNodes([node.id])
      }
    }

    this.emit({
      type: 'node:click',
      target: node,
      originalEvent: event,
      position: { x: (event as MouseEvent).clientX, y: (event as MouseEvent).clientY }
    })
  }

  private handleNodeDoubleClick(node: D3Node, event: Event): void {
    this.emit({
      type: 'node:dblclick',
      target: node,
      originalEvent: event,
      position: { x: (event as MouseEvent).clientX, y: (event as MouseEvent).clientY }
    })
  }

  private handleNodeMouseEnter(node: D3Node, event: Event): void {
    if (this.interactionConfig.tooltip.enabled) {
      const tooltip = generateNodeTooltip(node)
      showTooltip(this.tooltip, tooltip, event as MouseEvent, this.interactionConfig.tooltip.offset)
    }

    this.emit({
      type: 'node:mouseenter',
      target: node,
      originalEvent: event,
      position: { x: (event as MouseEvent).clientX, y: (event as MouseEvent).clientY }
    })
  }

  private handleNodeMouseLeave(node: D3Node, event: Event): void {
    if (this.interactionConfig.tooltip.enabled) {
      hideTooltip(this.tooltip)
    }

    this.emit({
      type: 'node:mouseleave',
      target: node,
      originalEvent: event,
      position: { x: (event as MouseEvent).clientX, y: (event as MouseEvent).clientY }
    })
  }

  private handleEdgeClick(edge: D3Edge, event: Event): void {
    if (this.interactionConfig.selection.enabled) {
      if (this.interactionConfig.selection.multiSelect && (event as MouseEvent).ctrlKey) {
        this.toggleEdgeSelection(edge.id)
      } else {
        this.selectEdges([edge.id])
      }
    }

    this.emit({
      type: 'edge:click',
      target: edge,
      originalEvent: event,
      position: { x: (event as MouseEvent).clientX, y: (event as MouseEvent).clientY }
    })
  }

  private handleEdgeMouseEnter(edge: D3Edge, event: Event): void {
    if (this.interactionConfig.tooltip.enabled) {
      const tooltip = generateEdgeTooltip(edge)
      showTooltip(this.tooltip, tooltip, event as MouseEvent, this.interactionConfig.tooltip.offset)
    }

    this.emit({
      type: 'edge:mouseenter',
      target: edge,
      originalEvent: event,
      position: { x: (event as MouseEvent).clientX, y: (event as MouseEvent).clientY }
    })
  }

  private handleEdgeMouseLeave(edge: D3Edge, event: Event): void {
    if (this.interactionConfig.tooltip.enabled) {
      hideTooltip(this.tooltip)
    }

    this.emit({
      type: 'edge:mouseleave',
      target: edge,
      originalEvent: event,
      position: { x: (event as MouseEvent).clientX, y: (event as MouseEvent).clientY }
    })
  }

  private toggleNodeSelection(nodeId: string): void {
    if (this.selectedNodes.has(nodeId)) {
      this.selectedNodes.delete(nodeId)
    } else {
      this.selectedNodes.add(nodeId)
    }
    this.updateSelectionStyles()
    this.emitSelectionChange()
  }

  private toggleEdgeSelection(edgeId: string): void {
    if (this.selectedEdges.has(edgeId)) {
      this.selectedEdges.delete(edgeId)
    } else {
      this.selectedEdges.add(edgeId)
    }
    this.updateSelectionStyles()
    this.emitSelectionChange()
  }

  private updateSelectionStyles(): void {
    // æ›´æ–°èŠ‚ç‚¹é€‰æ‹©æ ·å¼
    this.g.selectAll<SVGGElement, D3Node>('.node')
      .select('circle')
      .attr('stroke', d => 
        this.selectedNodes.has(d.id) 
          ? this.visualConfig.colors.selection 
          : d.strokeColor
      )
      .attr('stroke-width', d => 
        this.selectedNodes.has(d.id) 
          ? d.strokeWidth * 2 
          : d.strokeWidth
      )

    // æ›´æ–°è¾¹é€‰æ‹©æ ·å¼
    this.g.selectAll<SVGLineElement, D3Edge>('.edge')
      .attr('stroke', d => 
        this.selectedEdges.has(d.id) 
          ? this.visualConfig.colors.selection 
          : d.color
      )
      .attr('stroke-width', d => 
        this.selectedEdges.has(d.id) 
          ? d.strokeWidth * 2 
          : d.strokeWidth
      )
  }

  private emitSelectionChange(): void {
    this.emit({
      type: 'selection:change',
      target: null,
      originalEvent: new Event('selection:change'),
      position: { x: 0, y: 0 },
      data: {
        nodes: Array.from(this.selectedNodes),
        edges: Array.from(this.selectedEdges)
      }
    })
  }

  // å…¬å…±æ–¹æ³•å®ç°
  setLayout(config: LayoutConfig): void {
    this.layoutConfig = { ...this.layoutConfig, ...config }
  }

  applyLayout(): void {
    layoutManager.applyLayout(
      this.layoutConfig.type,
      this.nodes,
      this.links,
      this.layoutConfig
    )

    // å¦‚æœæ˜¯åŠ›å¯¼å‘å¸ƒå±€ï¼Œç»‘å®štickäº‹ä»¶
    const simulation = layoutManager.getCurrentSimulation()
    if (simulation) {
      simulation.on('tick', () => {
        this.updatePositions()
      })
    }
  }

  private updatePositions(): void {
    // æ›´æ–°èŠ‚ç‚¹ä½ç½®
    this.g.selectAll<SVGGElement, D3Node>('.node')
      .attr('transform', d => `translate(${d.x || 0}, ${d.y || 0})`)

    // æ›´æ–°è¾¹ä½ç½®
    this.g.selectAll<SVGLineElement, D3Edge>('.edge')
      .attr('x1', d => (d.source as D3Node).x || 0)
      .attr('y1', d => (d.source as D3Node).y || 0)
      .attr('x2', d => (d.target as D3Node).x || 0)
      .attr('y2', d => (d.target as D3Node).y || 0)
  }

  setVisualConfig(config: VisualConfig): void {
    this.visualConfig = { ...this.visualConfig, ...config }
    this.updateNodes()
    this.updateEdges()
  }

  updateNodeStyles(nodeIds: string[]): void {
    this.updateNodes()
  }

  updateEdgeStyles(edgeIds: string[]): void {
    this.updateEdges()
  }

  enableInteraction(config: InteractionConfig): void {
    this.interactionConfig = { ...this.interactionConfig, ...config }

    if (config.zoom.enabled) {
      this.zoom = createZoomBehavior(this.svg, this.g, config.zoom)
    }

    if (config.drag.enabled) {
      const dragBehavior = createDragBehavior(this.nodes, this.links, config.drag)
      this.g.selectAll('.node').call(dragBehavior)
    }

    // ç»‘å®šç”»å¸ƒç‚¹å‡»äº‹ä»¶
    this.svg.on('click', (event) => {
      if (event.target === this.svg.node()) {
        this.clearSelection()
        this.emit({
          type: 'canvas:click',
          target: null,
          originalEvent: event,
          position: { x: event.clientX, y: event.clientY }
        })
      }
    })
  }

  disableInteraction(): void {
    if (this.zoom) {
      this.svg.on('.zoom', null)
      this.zoom = null
    }

    this.g.selectAll('.node').on('.drag', null)
    this.svg.on('click', null)
  }

  selectNodes(nodeIds: string[]): void {
    this.selectedNodes.clear()
    nodeIds.forEach(id => this.selectedNodes.add(id))
    this.updateSelectionStyles()
    this.emitSelectionChange()
  }

  selectEdges(edgeIds: string[]): void {
    this.selectedEdges.clear()
    edgeIds.forEach(id => this.selectedEdges.add(id))
    this.updateSelectionStyles()
    this.emitSelectionChange()
  }

  clearSelection(): void {
    this.selectedNodes.clear()
    this.selectedEdges.clear()
    this.updateSelectionStyles()
    this.emitSelectionChange()
  }

  getSelection(): { nodes: string[]; edges: string[] } {
    return {
      nodes: Array.from(this.selectedNodes),
      edges: Array.from(this.selectedEdges)
    }
  }

  zoomToFit(padding = 50): void {
    if (!this.zoom) return

    const bounds = this.calculateBounds()
    if (!bounds) return

    const { width, height } = this.layoutConfig
    const scale = Math.min(
      (width - padding * 2) / bounds.width,
      (height - padding * 2) / bounds.height,
      2
    )

    const translate = [
      width / 2 - scale * bounds.centerX,
      height / 2 - scale * bounds.centerY
    ]

    this.svg.transition()
      .duration(750)
      .call(
        this.zoom.transform,
        d3.zoomIdentity.translate(translate[0], translate[1]).scale(scale)
      )
  }

  zoomToNodes(nodeIds: string[], padding = 50): void {
    if (!this.zoom) return

    const targetNodes = this.nodes.filter(n => nodeIds.includes(n.id))
    if (targetNodes.length === 0) return

    const bounds = this.calculateNodesBounds(targetNodes)
    if (!bounds) return

    const { width, height } = this.layoutConfig
    const scale = Math.min(
      (width - padding * 2) / bounds.width,
      (height - padding * 2) / bounds.height,
      2
    )

    const translate = [
      width / 2 - scale * bounds.centerX,
      height / 2 - scale * bounds.centerY
    ]

    this.svg.transition()
      .duration(750)
      .call(
        this.zoom.transform,
        d3.zoomIdentity.translate(translate[0], translate[1]).scale(scale)
      )
  }

  centerView(): void {
    if (!this.zoom) return

    this.svg.transition()
      .duration(750)
      .call(this.zoom.transform, d3.zoomIdentity)
  }

  private calculateBounds() {
    if (this.nodes.length === 0) return null

    const xs = this.nodes.map(d => d.x || 0)
    const ys = this.nodes.map(d => d.y || 0)
    const minX = Math.min(...xs)
    const maxX = Math.max(...xs)
    const minY = Math.min(...ys)
    const maxY = Math.max(...ys)

    return {
      minX,
      maxX,
      minY,
      maxY,
      width: maxX - minX || 1,
      height: maxY - minY || 1,
      centerX: (minX + maxX) / 2,
      centerY: (minY + maxY) / 2
    }
  }

  private calculateNodesBounds(nodes: D3Node[]) {
    if (nodes.length === 0) return null

    const xs = nodes.map(d => d.x || 0)
    const ys = nodes.map(d => d.y || 0)
    const minX = Math.min(...xs)
    const maxX = Math.max(...xs)
    const minY = Math.min(...ys)
    const maxY = Math.max(...ys)

    return {
      minX,
      maxX,
      minY,
      maxY,
      width: maxX - minX || 1,
      height: maxY - minY || 1,
      centerX: (minX + maxX) / 2,
      centerY: (minY + maxY) / 2
    }
  }

  on(event: GraphEventType, handler: GraphEventHandler): void {
    if (!this.eventHandlers.has(event)) {
      this.eventHandlers.set(event, [])
    }
    this.eventHandlers.get(event)!.push(handler)
  }

  off(event: GraphEventType, handler?: GraphEventHandler): void {
    if (!this.eventHandlers.has(event)) return

    if (handler) {
      const handlers = this.eventHandlers.get(event)!
      const index = handlers.indexOf(handler)
      if (index > -1) {
        handlers.splice(index, 1)
      }
    } else {
      this.eventHandlers.set(event, [])
    }
  }

  emit(event: GraphEvent): void {
    const handlers = this.eventHandlers.get(event.type)
    if (handlers) {
      handlers.forEach(handler => handler(event))
    }
  }

  destroy(): void {
    // åœæ­¢å¸ƒå±€
    layoutManager.stopCurrentLayout()

    // æ¸…ç†äº‹ä»¶ç›‘å¬å™¨
    this.eventHandlers.clear()

    // æ¸…ç†DOM
    if (this.tooltip) {
      this.tooltip.remove()
    }

    d3.select(this.container).selectAll('*').remove()
  }
}

/**
 * å›¾è°±å¸ƒå±€ç®—æ³•
 * å®ç°å¤šç§å¸ƒå±€ç®—æ³•ç”¨äºçŸ¥è¯†å›¾è°±å¯è§†åŒ–
 */
import * as d3 from 'd3'
import { D3Node, D3Edge, LayoutConfig, LayoutAlgorithm } from './types'

/**
 * åŠ›å¯¼å‘å¸ƒå±€ç®—æ³•
 */
export class ForceLayoutAlgorithm implements LayoutAlgorithm {
  name = 'force'
  private simulation: d3.Simulation<D3Node, D3Edge> | null = null

  apply(nodes: D3Node[], links: D3Edge[], config: LayoutConfig): void {
    const { width, height, options } = config

    // åœæ­¢ä¹‹å‰çš„ä»¿çœŸ
    this.stop()

    // åˆ›å»ºåŠ›ä»¿çœŸ
    this.simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink<D3Node, D3Edge>(links)
        .id(d => d.id)
        .distance(options.linkDistance || 80)
        .strength(options.linkStrength || 0.5)
      )
      .force('charge', d3.forceManyBody()
        .strength(options.chargeStrength || -300)
      )
      .force('center', d3.forceCenter(width / 2, height / 2)
        .strength(options.centerStrength || 0.1)
      )
      .force('collision', d3.forceCollide()
        .radius(d => d.radius + (options.collisionRadius || 5))
        .strength(0.7)
      )
      .alpha(options.alpha || 0.3)
      .alphaDecay(options.alphaDecay || 0.02)
      .velocityDecay(options.velocityDecay || 0.4)
  }

  stop(): void {
    if (this.simulation) {
      this.simulation.stop()
      this.simulation = null
    }
  }

  getSimulation(): d3.Simulation<D3Node, D3Edge> | null {
    return this.simulation
  }
}

/**
 * å±‚æ¬¡å¸ƒå±€ç®—æ³•
 */
export class HierarchicalLayoutAlgorithm implements LayoutAlgorithm {
  name = 'hierarchical'

  apply(nodes: D3Node[], links: D3Edge[], config: LayoutConfig): void {
    const { width, height, options } = config
    const levelSpacing = options.levelSpacing || 150
    const nodeSpacing = options.nodeSpacing || 100

    // æŒ‰å±‚çº§åˆ†ç»„èŠ‚ç‚¹
    const nodesByLevel = new Map<number, D3Node[]>()
    nodes.forEach(node => {
      const level = node.level
      if (!nodesByLevel.has(level)) {
        nodesByLevel.set(level, [])
      }
      nodesByLevel.get(level)!.push(node)
    })

    // è®¡ç®—æ¯å±‚çš„ä½ç½®
    const levels = Array.from(nodesByLevel.keys()).sort((a, b) => a - b)
    const maxLevel = Math.max(...levels)

    levels.forEach(level => {
      const levelNodes = nodesByLevel.get(level)!
      const y = (height / (maxLevel + 1)) * (level + 1)

      // åœ¨æ°´å¹³æ–¹å‘å‡åŒ€åˆ†å¸ƒèŠ‚ç‚¹
      levelNodes.forEach((node, index) => {
        const totalWidth = (levelNodes.length - 1) * nodeSpacing
        const startX = (width - totalWidth) / 2
        node.x = startX + index * nodeSpacing
        node.y = y
        node.fx = node.x
        node.fy = node.y
      })
    })
  }
}

/**
 * ç¯å½¢å¸ƒå±€ç®—æ³•
 */
export class CircularLayoutAlgorithm implements LayoutAlgorithm {
  name = 'circular'

  apply(nodes: D3Node[], links: D3Edge[], config: LayoutConfig): void {
    const { width, height, options } = config
    const centerX = width / 2
    const centerY = height / 2
    const radius = options.radius || Math.min(width, height) / 3
    const startAngle = options.startAngle || 0
    const endAngle = options.endAngle || 2 * Math.PI

    const angleStep = (endAngle - startAngle) / nodes.length

    nodes.forEach((node, index) => {
      const angle = startAngle + index * angleStep
      node.x = centerX + radius * Math.cos(angle)
      node.y = centerY + radius * Math.sin(angle)
      node.fx = node.x
      node.fy = node.y
    })
  }
}

/**
 * æ ‘å½¢å¸ƒå±€ç®—æ³•
 */
export class TreeLayoutAlgorithm implements LayoutAlgorithm {
  name = 'tree'

  apply(nodes: D3Node[], links: D3Edge[], config: LayoutConfig): void {
    const { width, height } = config

    // æ„å»ºå±‚æ¬¡ç»“æ„
    const hierarchy = this.buildHierarchy(nodes, links)

    if (!hierarchy) return

    // åˆ›å»ºæ ‘å¸ƒå±€
    const treeLayout = d3.tree<D3Node>()
      .size([width - 100, height - 100])

    const root = treeLayout(hierarchy)

    // åº”ç”¨ä½ç½®
    root.descendants().forEach(d => {
      if (d.data) {
        d.data.x = d.x + 50
        d.data.y = d.y + 50
        d.data.fx = d.data.x
        d.data.fy = d.data.y
      }
    })
  }

  private buildHierarchy(nodes: D3Node[], links: D3Edge[]): d3.HierarchyNode<D3Node> | null {
    // æ‰¾åˆ°æ ¹èŠ‚ç‚¹ï¼ˆæ²¡æœ‰çˆ¶èŠ‚ç‚¹çš„èŠ‚ç‚¹ï¼‰
    const childrenMap = new Map<string, string[]>()
    const hasParent = new Set<string>()

    links.forEach(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source
      const targetId = typeof link.target === 'object' ? link.target.id : link.target

      if (!childrenMap.has(sourceId)) {
        childrenMap.set(sourceId, [])
      }
      childrenMap.get(sourceId)!.push(targetId)
      hasParent.add(targetId)
    })

    // æ‰¾åˆ°æ ¹èŠ‚ç‚¹
    const rootNodes = nodes.filter(node => !hasParent.has(node.id))
    if (rootNodes.length === 0) return null

    const root = rootNodes[0]

    // é€’å½’æ„å»ºå±‚æ¬¡ç»“æ„
    const buildNode = (nodeId: string): d3.HierarchyNode<D3Node> => {
      const node = nodes.find(n => n.id === nodeId)!
      const children = childrenMap.get(nodeId) || []

      return {
        data: node,
        children: children.map(childId => buildNode(childId)),
        depth: 0,
        height: 0,
        parent: null
      } as d3.HierarchyNode<D3Node>
    }

    return d3.hierarchy(root, (d: D3Node) => {
      const children = childrenMap.get(d.id) || []
      return children.map(childId => nodes.find(n => n.id === childId)!).filter(Boolean)
    })
  }
}

/**
 * ç½‘æ ¼å¸ƒå±€ç®—æ³•
 */
export class GridLayoutAlgorithm implements LayoutAlgorithm {
  name = 'grid'

  apply(nodes: D3Node[], links: D3Edge[], config: LayoutConfig): void {
    const { width, height, options } = config
    const columns = options.columns || Math.ceil(Math.sqrt(nodes.length))
    const rows = Math.ceil(nodes.length / columns)
    const cellWidth = options.cellWidth || width / columns
    const cellHeight = options.cellHeight || height / rows

    nodes.forEach((node, index) => {
      const col = index % columns
      const row = Math.floor(index / columns)

      node.x = (col + 0.5) * cellWidth
      node.y = (row + 0.5) * cellHeight
      node.fx = node.x
      node.fy = node.y
    })
  }
}

/**
 * å¸ƒå±€ç®¡ç†å™¨
 */
export class LayoutManager {
  private algorithms: Map<string, LayoutAlgorithm> = new Map()
  private currentAlgorithm: LayoutAlgorithm | null = null

  constructor() {
    // æ³¨å†Œé»˜è®¤å¸ƒå±€ç®—æ³•
    this.registerAlgorithm(new ForceLayoutAlgorithm())
    this.registerAlgorithm(new HierarchicalLayoutAlgorithm())
    this.registerAlgorithm(new CircularLayoutAlgorithm())
    this.registerAlgorithm(new TreeLayoutAlgorithm())
    this.registerAlgorithm(new GridLayoutAlgorithm())
  }

  registerAlgorithm(algorithm: LayoutAlgorithm): void {
    this.algorithms.set(algorithm.name, algorithm)
  }

  getAlgorithm(name: string): LayoutAlgorithm | undefined {
    return this.algorithms.get(name)
  }

  applyLayout(
    layoutType: string,
    nodes: D3Node[],
    links: D3Edge[],
    config: LayoutConfig
  ): void {
    // åœæ­¢å½“å‰å¸ƒå±€
    if (this.currentAlgorithm?.stop) {
      this.currentAlgorithm.stop()
    }

    // åº”ç”¨æ–°å¸ƒå±€
    const algorithm = this.getAlgorithm(layoutType)
    if (algorithm) {
      this.currentAlgorithm = algorithm
      algorithm.apply(nodes, links, config)
    } else {
      console.warn(`Unknown layout algorithm: ${layoutType}`)
    }
  }

  stopCurrentLayout(): void {
    if (this.currentAlgorithm?.stop) {
      this.currentAlgorithm.stop()
      this.currentAlgorithm = null
    }
  }

  getCurrentSimulation(): d3.Simulation<D3Node, D3Edge> | null {
    if (this.currentAlgorithm instanceof ForceLayoutAlgorithm) {
      return this.currentAlgorithm.getSimulation()
    }
    return null
  }

  getAvailableLayouts(): string[] {
    return Array.from(this.algorithms.keys())
  }
}

// å¯¼å‡ºå•ä¾‹å®ä¾‹
export const layoutManager = new LayoutManager()

