 */

export type LogLevel = 'debug' | 'info' | 'warn' | 'error'

interface LogEntry {
  level: LogLevel
  message: string
  data?: any
  timestamp: string
  url?: string
  userAgent?: string
}

class ClientLogger {
  private isDevelopment = process.env.NODE_ENV === 'development'
  private logs: LogEntry[] = []
  private maxLogs = 100

  private createLogEntry(level: LogLevel, message: string, data?: any): LogEntry {
    return {
      level,
      message,
      data,
      timestamp: new Date().toISOString(),
      url: typeof window !== 'undefined' ? window.location.href : undefined,
      userAgent: typeof window !== 'undefined' ? window.navigator.userAgent : undefined
    }
  }

  private addLog(entry: LogEntry) {
    this.logs.push(entry)
    if (this.logs.length > this.maxLogs) {
      this.logs.shift()
    }
  }

  debug(message: string, data?: any) {
    const entry = this.createLogEntry('debug', message, data)
    this.addLog(entry)

    if (this.isDevelopment) {
      console.debug(`[DEBUG] ${message}`, data)
    }
  }

  info(message: string, data?: any) {
    const entry = this.createLogEntry('info', message, data)
    this.addLog(entry)

    console.info(`[INFO] ${message}`, data)
  }

  warn(message: string, data?: any) {
    const entry = this.createLogEntry('warn', message, data)
    this.addLog(entry)

    console.warn(`[WARN] ${message}`, data)
  }

  error(message: string, error?: Error | any, data?: any) {
    const entry = this.createLogEntry('error', message, { error: error?.message || error, data })
    this.addLog(entry)

    console.error(`[ERROR] ${message}`, error, data)

    // åœ¨ç”Ÿäº§ç¯å¢ƒä¸­ï¼Œå¯ä»¥å‘é€åˆ°é”™è¯¯ç›‘æ§æœåŠ¡
    if (!this.isDevelopment && typeof window !== 'undefined') {
      this.reportError(entry)
    }
  }

  private reportError(entry: LogEntry) {
    // è¿™é‡Œå¯ä»¥é›†æˆé”™è¯¯ç›‘æ§æœåŠ¡ï¼Œå¦‚ Sentry
    // ç›®å‰åªæ˜¯å­˜å‚¨åˆ°æœ¬åœ°
    try {
      const errorReports = JSON.parse(localStorage.getItem('error_reports') || '[]')
      errorReports.push(entry)

      // åªä¿ç•™æœ€è¿‘çš„50ä¸ªé”™è¯¯æŠ¥å‘Š
      if (errorReports.length > 50) {
        errorReports.splice(0, errorReports.length - 50)
      }

      localStorage.setItem('error_reports', JSON.stringify(errorReports))
    } catch (e) {
      // å¿½ç•¥localStorageé”™è¯¯
    }
  }

  getLogs(level?: LogLevel): LogEntry[] {
    if (level) {
      return this.logs.filter(log => log.level === level)
    }
    return [...this.logs]
  }

  clearLogs() {
    this.logs = []
  }

  exportLogs(): string {
    return JSON.stringify(this.logs, null, 2)
  }
}

