    if (!config.level || !config.format) {
      return false;
    }

    // éªŒè¯æ—¥å¿—çº§åˆ«
    if (!Object.values(LogLevel).includes(config.level as LogLevel)) {
      return false;
    }

    // éªŒè¯ä¼ è¾“å™¨é…ç½®
    if (!config.transports) {
      return false;
    }

    return true;
  } catch (error) {
    return false;
  }
};

