 * æ—¥å¿—æ ¼å¼åŒ–å™¨
 */
import winston from 'winston';
import { SENSITIVE_FIELDS } from './config';

/**
 * æ—¥å¿—æ¡ç›®æ¥å£
 */
export interface LogEntry {
  timestamp: string;
  level: string;
  message: string;
  traceId?: string;
  userId?: string;
  requestId?: string;
  tag?: string;
  metadata: {
    service: string;
    version: string;
    environment: string;
    [key: string]: any;
  };
  error?: {
    name: string;
    message: string;
    stack?: string;
    code?: string;
  };
  performance?: {
    duration: number;
    memory: number;
    cpu: number;
  };
  context?: {
    url?: string;
    method?: string;
    userAgent?: string;
    ip?: string;
    [key: string]: any;
  };
}

/**
 * è„±æ•å¤„ç†å‡½æ•°
 */
const sanitizeData = (data: any): any => {
  if (data === null || data === undefined) {
    return data;
  }

  if (typeof data === 'string') {
    return data;
  }

  if (Array.isArray(data)) {
    return data.map(sanitizeData);
  }

  if (typeof data === 'object') {
    const sanitized: any = {};

    for (const [key, value] of Object.entries(data)) {
      const lowerKey = key.toLowerCase();

      // æ£€æŸ¥æ˜¯å¦ä¸ºæ•æ„Ÿå­—æ®µ
      const isSensitive = SENSITIVE_FIELDS.some(field => 
        lowerKey.includes(field.toLowerCase())
      );

      if (isSensitive) {
        sanitized[key] = '[REDACTED]';
      } else {
        sanitized[key] = sanitizeData(value);
      }
    }

    return sanitized;
  }

  return data;
};

/**
 * é”™è¯¯å¯¹è±¡æ ¼å¼åŒ–
 */
const formatError = (error: Error): LogEntry['error'] => {
  return {
    name: error.name,
    message: error.message,
    stack: error.stack,
    code: (error as any).code
  };
};

/**
 * æ€§èƒ½ä¿¡æ¯æ ¼å¼åŒ–
 */
const formatPerformance = (performance?: any): LogEntry['performance'] | undefined => {
  if (!performance) return undefined;

  return {
    duration: performance.duration || 0,
    memory: process.memoryUsage().heapUsed,
    cpu: process.cpuUsage().user
  };
};

/**
 * JSONæ ¼å¼åŒ–å™¨
 */
export const jsonFormatter = winston.format.combine(
  winston.format.timestamp({
    format: 'YYYY-MM-DD HH:mm:ss.SSS'
  }),
  winston.format.errors({ stack: true }),
  winston.format.printf((info) => {
    const logEntry: LogEntry = {
      timestamp: info.timestamp,
      level: info.level,
      message: info.message,
      traceId: info.traceId,
      userId: info.userId,
      requestId: info.requestId,
      tag: info.tag,
      metadata: {
        service: info.service || 'inspi-ai-platform',
        version: info.version || '0.1.0',
        environment: info.environment || process.env.NODE_ENV || 'development',
        ...sanitizeData(info.metadata || {})
      }
    };

    // æ·»åŠ é”™è¯¯ä¿¡æ¯
    if (info.error || info instanceof Error) {
      logEntry.error = formatError(info.error || info);
    }

    // æ·»åŠ æ€§èƒ½ä¿¡æ¯
    if (info.performance) {
      logEntry.performance = formatPerformance(info.performance);
    }

    // æ·»åŠ ä¸Šä¸‹æ–‡ä¿¡æ¯
    if (info.context) {
      logEntry.context = sanitizeData(info.context);
    }

    // æ·»åŠ å…¶ä»–è‡ªå®šä¹‰å­—æ®µ
    const customFields = { ...info };
    delete customFields.timestamp;
    delete customFields.level;
    delete customFields.message;
    delete customFields.traceId;
    delete customFields.userId;
    delete customFields.requestId;
    delete customFields.tag;
    delete customFields.metadata;
    delete customFields.error;
    delete customFields.performance;
    delete customFields.context;
    delete customFields.service;
    delete customFields.version;
    delete customFields.environment;
    delete customFields[Symbol.for('level')];
    delete customFields[Symbol.for('message')];
    delete customFields[Symbol.for('splat')];

    if (Object.keys(customFields).length > 0) {
      logEntry.metadata = {
        ...logEntry.metadata,
        ...sanitizeData(customFields)
      };
    }

    return JSON.stringify(logEntry);
  })
);

/**
 * æ§åˆ¶å°æ ¼å¼åŒ–å™¨ï¼ˆå¼€å‘ç¯å¢ƒï¼‰
 */
export const consoleFormatter = winston.format.combine(
  winston.format.timestamp({
    format: 'HH:mm:ss.SSS'
  }),
  winston.format.colorize(),
  winston.format.errors({ stack: true }),
  winston.format.printf((info) => {
    let output = `${info.timestamp} [${info.level}]`;

    // æ·»åŠ æ ‡ç­¾
    if (info.tag) {
      output += ` [${info.tag}]`;
    }

    // æ·»åŠ è¿½è¸ªID
    if (info.traceId) {
      output += ` [${info.traceId.substring(0, 8)}]`;
    }

    // æ·»åŠ ç”¨æˆ·ID
    if (info.userId) {
      output += ` [user:${info.userId}]`;
    }

    output += `: ${info.message}`;

    // æ·»åŠ é”™è¯¯å †æ ˆ
    if (info.stack) {
      output += `\n${info.stack}`;
    }

    // æ·»åŠ å…ƒæ•°æ®ï¼ˆå¼€å‘ç¯å¢ƒæ˜¾ç¤ºï¼‰
    if (info.metadata && Object.keys(info.metadata).length > 0) {
      output += `\n  Metadata: ${JSON.stringify(sanitizeData(info.metadata), null, 2)}`;
    }

    // æ·»åŠ ä¸Šä¸‹æ–‡ä¿¡æ¯
    if (info.context && Object.keys(info.context).length > 0) {
      output += `\n  Context: ${JSON.stringify(sanitizeData(info.context), null, 2)}`;
    }

    return output;
  })
);

/**
 * ç®€å•æ ¼å¼åŒ–å™¨ï¼ˆç”¨äºæ–‡ä»¶æ—¥å¿—ï¼‰
 */
export const simpleFormatter = winston.format.combine(
  winston.format.timestamp({
    format: 'YYYY-MM-DD HH:mm:ss'
  }),
  winston.format.errors({ stack: true }),
  winston.format.printf((info) => {
    let output = `${info.timestamp} [${info.level.toUpperCase()}]`;

    if (info.tag) {
      output += ` [${info.tag}]`;
    }

    if (info.traceId) {
      output += ` [${info.traceId}]`;
    }

    output += `: ${info.message}`;

    if (info.stack) {
      output += `\n${info.stack}`;
    }

    return output;
  })
);

/**
 * åˆ›å»ºè‡ªå®šä¹‰æ ¼å¼åŒ–å™¨
 */
export const createCustomFormatter = (options: {
  includeMetadata?: boolean;
  includeContext?: boolean;
  includePerformance?: boolean;
  colorize?: boolean;
  format?: 'json' | 'simple' | 'detailed';
}) => {
  const {
    includeMetadata = true,
    includeContext = true,
    includePerformance = false,
    colorize = false,
    format = 'json'
  } = options;

  const formats = [
    winston.format.timestamp({
      format: 'YYYY-MM-DD HH:mm:ss.SSS'
    }),
    winston.format.errors({ stack: true })
  ];

  if (colorize) {
    formats.push(winston.format.colorize());
  }

  formats.push(
    winston.format.printf((info) => {
      if (format === 'json') {
        const logEntry: Partial<LogEntry> = {
          timestamp: info.timestamp,
          level: info.level,
          message: info.message,
          traceId: info.traceId,
          userId: info.userId,
          requestId: info.requestId,
          tag: info.tag
        };

        if (includeMetadata && info.metadata) {
          logEntry.metadata = sanitizeData(info.metadata);
        }

        if (includeContext && info.context) {
          logEntry.context = sanitizeData(info.context);
        }

        if (includePerformance && info.performance) {
          logEntry.performance = formatPerformance(info.performance);
        }

        if (info.error || info instanceof Error) {
          logEntry.error = formatError(info.error || info);
        }

        return JSON.stringify(logEntry);
      }

      // ç®€å•æ ¼å¼
      let output = `${info.timestamp} [${info.level.toUpperCase()}]`;

      if (info.tag) {
        output += ` [${info.tag}]`;
      }

      if (info.traceId) {
        output += ` [${info.traceId.substring(0, 8)}]`;
      }

      output += `: ${info.message}`;

      if (info.stack) {
        output += `\n${info.stack}`;
      }

      return output;
    })
  );

  return winston.format.combine(...formats);
};

/**
 * Winstonæ—¥å¿—å™¨å®ä¾‹
 */
import winston from 'winston';
import { v4 as uuidv4 } from 'uuid';
import { logConfig, LOG_TAGS, LogLevel } from './config';
import { 
  getDefaultTransports, 
  setupTransportEvents,
  createAccessLogTransport,
  createPerformanceLogTransport,
  createSecurityLogTransport
} from './transports';
import { LogEntry } from './formatters';

/**
 * æ—¥å¿—ä¸Šä¸‹æ–‡æ¥å£
 */
export interface LogContext {
  traceId?: string;
  userId?: string;
  requestId?: string;
  tag?: string;
  metadata?: Record<string, any>;
  context?: Record<string, any>;
  performance?: {
    duration?: number;
    memory?: number;
    cpu?: number;
  };
}

/**
 * åˆ›å»ºä¸»æ—¥å¿—å™¨
 */
const createMainLogger = (): winston.Logger => {
  const transports = getDefaultTransports();

  // è®¾ç½®ä¼ è¾“å™¨äº‹ä»¶å¤„ç†
  transports.forEach(setupTransportEvents);

  const logger = winston.createLogger({
    level: logConfig.level,
    defaultMeta: {
      service: logConfig.metadata.service,
      version: logConfig.metadata.version,
      environment: logConfig.metadata.environment
    },
    transports,
    exitOnError: false,
    // å¼‚å¸¸å¤„ç†
    exceptionHandlers: [
      new winston.transports.File({ 
        filename: 'logs/exceptions.log',
        format: winston.format.combine(
          winston.format.timestamp(),
          winston.format.json()
        )
      })
    ],
    // æ‹’ç»å¤„ç†
    rejectionHandlers: [
      new winston.transports.File({ 
        filename: 'logs/rejections.log',
        format: winston.format.combine(
          winston.format.timestamp(),
          winston.format.json()
        )
      })
    ]
  });

  return logger;
};

/**
 * åˆ›å»ºä¸“ç”¨æ—¥å¿—å™¨
 */
const createAccessLogger = (): winston.Logger => {
  return winston.createLogger({
    level: LogLevel.INFO,
    transports: [createAccessLogTransport()],
    exitOnError: false
  });
};

const createPerformanceLogger = (): winston.Logger => {
  return winston.createLogger({
    level: LogLevel.INFO,
    transports: [createPerformanceLogTransport()],
    exitOnError: false
  });
};

const createSecurityLogger = (): winston.Logger => {
  return winston.createLogger({
    level: LogLevel.WARN,
    transports: [createSecurityLogTransport()],
    exitOnError: false
  });
};

// åˆ›å»ºæ—¥å¿—å™¨å®ä¾‹
export const mainLogger = createMainLogger();
export const accessLogger = createAccessLogger();
export const performanceLogger = createPerformanceLogger();
export const securityLogger = createSecurityLogger();

/**
 * å¢å¼ºçš„æ—¥å¿—å™¨ç±»
 */
export class Logger {
  private logger: winston.Logger;
  private defaultContext: LogContext;

  constructor(logger: winston.Logger = mainLogger, defaultContext: LogContext = {}) {
    this.logger = logger;
    this.defaultContext = defaultContext;
  }

  /**
   * åˆ›å»ºå­æ—¥å¿—å™¨
   */
  child(context: LogContext): Logger {
    return new Logger(this.logger, {
      ...this.defaultContext,
      ...context,
      metadata: {
        ...this.defaultContext.metadata,
        ...context.metadata
      }
    });
  }

  /**
   * ç”Ÿæˆè¿½è¸ªID
   */
  generateTraceId(): string {
    return uuidv4();
  }

  /**
   * åˆå¹¶ä¸Šä¸‹æ–‡
   */
  private mergeContext(context?: LogContext): LogContext {
    return {
      ...this.defaultContext,
      ...context,
      metadata: {
        ...this.defaultContext.metadata,
        ...context?.metadata
      }
    };
  }

  /**
   * è®°å½•æ—¥å¿—çš„é€šç”¨æ–¹æ³•
   */
  private log(level: string, message: string, context?: LogContext): void {
    const mergedContext = this.mergeContext(context);

    this.logger.log(level, message, {
      ...mergedContext,
      timestamp: new Date().toISOString()
    });
  }

  /**
   * é”™è¯¯æ—¥å¿—
   */
  error(message: string, error?: Error, context?: LogContext): void {
    const errorContext = error ? {
      ...context,
      error: {
        name: error.name,
        message: error.message,
        stack: error.stack,
        code: (error as any).code
      }
    } : context;

    this.log(LogLevel.ERROR, message, errorContext);
  }

  /**
   * è­¦å‘Šæ—¥å¿—
   */
  warn(message: string, context?: LogContext): void {
    this.log(LogLevel.WARN, message, context);
  }

  /**
   * ä¿¡æ¯æ—¥å¿—
   */
  info(message: string, context?: LogContext): void {
    this.log(LogLevel.INFO, message, context);
  }

  /**
   * HTTPæ—¥å¿—
   */
  http(message: string, context?: LogContext): void {
    this.log(LogLevel.HTTP, message, context);
  }

  /**
   * è¯¦ç»†æ—¥å¿—
   */
  verbose(message: string, context?: LogContext): void {
    this.log(LogLevel.VERBOSE, message, context);
  }

  /**
   * è°ƒè¯•æ—¥å¿—
   */
  debug(message: string, context?: LogContext): void {
    this.log(LogLevel.DEBUG, message, context);
  }

  /**
   * æœ€è¯¦ç»†æ—¥å¿—
   */
  silly(message: string, context?: LogContext): void {
    this.log(LogLevel.SILLY, message, context);
  }

  /**
   * æ€§èƒ½æ—¥å¿—
   */
  performance(message: string, duration: number, context?: LogContext): void {
    const perfContext = {
      ...context,
      performance: {
        duration,
        memory: process.memoryUsage().heapUsed,
        cpu: process.cpuUsage().user,
        ...context?.performance
      },
      tag: LOG_TAGS.PERFORMANCE
    };

    performanceLogger.info(message, perfContext);
  }

  /**
   * å®‰å…¨æ—¥å¿—
   */
  security(message: string, context?: LogContext): void {
    const securityContext = {
      ...context,
      tag: LOG_TAGS.SECURITY
    };

    securityLogger.warn(message, securityContext);
  }

  /**
   * è®¿é—®æ—¥å¿—
   */
  access(method: string, url: string, status: number, responseTime: number, context?: LogContext): void {
    const accessContext = {
      ...context,
      method,
      url,
      status,
      responseTime,
      tag: LOG_TAGS.API
    };

    accessLogger.info(`${method} ${url} ${status} ${responseTime}ms`, accessContext);
  }

  /**
   * æ•°æ®åº“æ—¥å¿—
   */
  database(message: string, context?: LogContext): void {
    this.log(LogLevel.DEBUG, message, {
      ...context,
      tag: LOG_TAGS.DATABASE
    });
  }

  /**
   * ç¼“å­˜æ—¥å¿—
   */
  cache(message: string, context?: LogContext): void {
    this.log(LogLevel.DEBUG, message, {
      ...context,
      tag: LOG_TAGS.CACHE
    });
  }

  /**
   * é‚®ä»¶æ—¥å¿—
   */
  email(message: string, context?: LogContext): void {
    this.log(LogLevel.INFO, message, {
      ...context,
      tag: LOG_TAGS.EMAIL
    });
  }

  /**
   * AIæœåŠ¡æ—¥å¿—
   */
  ai(message: string, context?: LogContext): void {
    this.log(LogLevel.INFO, message, {
      ...context,
      tag: LOG_TAGS.AI
    });
  }

  /**
   * ç”¨æˆ·æ“ä½œæ—¥å¿—
   */
  user(message: string, userId: string, context?: LogContext): void {
    this.log(LogLevel.INFO, message, {
      ...context,
      userId,
      tag: LOG_TAGS.USER
    });
  }

  /**
   * ç³»ç»Ÿæ—¥å¿—
   */
  system(message: string, context?: LogContext): void {
    this.log(LogLevel.INFO, message, {
      ...context,
      tag: LOG_TAGS.SYSTEM
    });
  }

  /**
   * è®¤è¯æ—¥å¿—
   */
  auth(message: string, context?: LogContext): void {
    this.log(LogLevel.INFO, message, {
      ...context,
      tag: LOG_TAGS.AUTH
    });
  }
}

