/**
 * æ—¥å¿—é‡‡æ ·å™¨ï¼ˆç”¨äºé«˜é¢‘æ—¥å¿—ï¼‰
 */
export class LogSampler {
  private counters = new Map<string, number>();
  private sampleRate: number;

  constructor(sampleRate: number = 0.1) {
    this.sampleRate = sampleRate;
  }

  /**
   * åˆ¤æ–­æ˜¯å¦åº”è¯¥è®°å½•æ—¥å¿—
   */
  shouldLog(key: string): boolean {
    const count = this.counters.get(key) || 0;
    this.counters.set(key, count + 1);

    return Math.random() < this.sampleRate || count % Math.ceil(1 / this.sampleRate) === 0;
  }

  /**
   * é‡ç½®è®¡æ•°å™¨
   */
  reset(): void {
    this.counters.clear();
  }
}

/**
 * åˆ›å»ºæ—¥å¿—é‡‡æ ·å™¨
 */
export const createLogSampler = (sampleRate: number = 0.1): LogSampler => {
  return new LogSampler(sampleRate);
};

import { NextRequest, NextResponse } from 'next/server';
import jwt from 'jsonwebtoken';
import connectDB from '@/lib/mongodb';
import User from '@/lib/models/User';

export interface AuthenticatedRequest extends NextRequest {
  user?: any;
}

export async function authenticateToken(request: NextRequest) {
  const authHeader = request.headers.get('authorization');
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return null;
  }

  try {
    const decoded = jwt.verify(token, process.env.NEXTAUTH_SECRET!) as any;

    await connectDB();
    const user = await User.findById(decoded.userId);

    if (!user) {
      return null;
    }

    return user;
  } catch (error) {
    console.error('Token verification failed:', error);
    return null;
  }
}

export function requireAuth(handler: Function) {
  return async (request: NextRequest) => {
    const user = await authenticateToken(request);

    if (!user) {
      return NextResponse.json(
        { code: 'UNAUTHORIZED', message: 'è¯·å…ˆç™»å½•' },
        { status: 401 }
      );
    }

    // Add user to request object
    (request as AuthenticatedRequest).user = user;

    return handler(request);
  };
}

export function optionalAuth(handler: Function) {
  return async (request: NextRequest) => {
    const user = await authenticateToken(request);

    // Add user to request object (can be null)
    (request as AuthenticatedRequest).user = user;

    return handler(request);
  };
}

