      return NextResponse.json({
        code: 'RATE_LIMIT_EXCEEDED',
        message: `ä»Šæ—¥${limitType}æ¬¡æ•°å·²ç”¨å®Œï¼ˆ${limits[userPlan]}æ¬¡ï¼‰ï¼Œè¯·å‡çº§è®¢é˜…æˆ–æ˜å¤©å†è¯•`,
        currentPlan: user.subscription.plan,
        usageLimit: limits[userPlan],
        resetTime: new Date(Date.now() + 24 * 60 * 60 * 1000), // Tomorrow
      }, { status: 429 });
    }

    return null; // Continue to handler
  };
}

export async function incrementUsage(userId: string, type: 'generation' | 'reuse') {
  try {
    const redis = await getRedisClient();
    const today = new Date().toISOString().split('T')[0];
    const key = `usage:${userId}:${type}:${today}`;

    await redis.incr(key);
    await redis.expire(key, 24 * 60 * 60); // Expire after 24 hours
  } catch (error) {
    console.error('Failed to increment usage:', error);
  }
}

/**
 * ä½¿ç”¨é™åˆ¶ä¸­é—´ä»¶
 * æ£€æŸ¥ç”¨æˆ·çš„ä½¿ç”¨é™åˆ¶å¹¶è®°å½•ä½¿ç”¨æƒ…å†µ
 */

import { NextRequest, NextResponse } from 'next/server';
import { verifyToken } from '../auth/jwt';
import { SubscriptionService } from '../services/subscriptionService';
import { UsageLimitError } from '../../types/subscription';

/**
 * ä½¿ç”¨é™åˆ¶ä¸­é—´ä»¶
 */
export async function usageLimitMiddleware(
  request: NextRequest,
  type: 'generation' | 'reuse'
): Promise<NextResponse | null> {
  try {
    // 1. éªŒè¯ç”¨æˆ·èº«ä»½
    const token = request.cookies.get('token')?.value || 
                  request.headers.get('authorization')?.replace('Bearer ', '');

    if (!token) {
      return NextResponse.json(
        { error: 'Authentication required' },
        { status: 401 }
      );
    }

    const payload = verifyToken(token) as any;
    if (!payload || !payload.userId) {
      return NextResponse.json(
        { error: 'Invalid token' },
        { status: 401 }
      );
    }

    const userId = payload.userId;

    // 2. æ£€æŸ¥ä½¿ç”¨é™åˆ¶
    const limitCheck = await SubscriptionService.checkUsageLimit(userId, type);

    if (!limitCheck.allowed) {
      return NextResponse.json({
        error: 'Usage limit exceeded',
        message: `æ‚¨ä»Šæ—¥çš„${type === 'generation' ? 'ç”Ÿæˆ' : 'å¤ç”¨'}æ¬¡æ•°å·²ç”¨å®Œ`,
        current: limitCheck.current,
        limit: limitCheck.limit,
        plan: limitCheck.plan,
        resetTime: getNextResetTime()
      }, { status: 429 });
    }

    return null; // é€šè¿‡æ£€æŸ¥
  } catch (error) {
    console.error('Usage limit middleware error:', error);

    if (error instanceof UsageLimitError) {
      return NextResponse.json({
        error: 'Usage limit exceeded',
        message: error.message,
        current: error.current,
        limit: error.limit,
        plan: error.plan,
        resetTime: getNextResetTime()
      }, { status: 429 });
    }

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

/**
 * è®°å½•ä½¿ç”¨çš„è¾…åŠ©å‡½æ•°
 */
export async function recordUsage(
  request: NextRequest,
  type: 'generation' | 'reuse',
  count: number = 1
): Promise<void> {
  try {
    const token = request.cookies.get('token')?.value || 
                  request.headers.get('authorization')?.replace('Bearer ', '');

    if (!token) return;

    const payload = verifyToken(token) as any;
    if (!payload || !payload.userId) return;

    await SubscriptionService.recordUsage(payload.userId, type, count);
  } catch (error) {
    console.error('Record usage error:', error);
    // ä¸æŠ›å‡ºé”™è¯¯ï¼Œé¿å…å½±å“ä¸»è¦åŠŸèƒ½
  }
}

/**
 * è·å–ä¸‹æ¬¡é‡ç½®æ—¶é—´
 */
function getNextResetTime(): string {
  const tomorrow = new Date();
  tomorrow.setDate(tomorrow.getDate() + 1);
  tomorrow.setHours(0, 0, 0, 0);
  return tomorrow.toISOString();
}

/**
 * è·å–ç”¨æˆ·IDçš„è¾…åŠ©å‡½æ•°
 */
export function getUserIdFromRequest(request: NextRequest): string | null {
  try {
    const token = request.cookies.get('token')?.value || 
                  request.headers.get('authorization')?.replace('Bearer ', '');

    if (!token) return null;

    const payload = verifyToken(token) as any;
    return payload?.userId || null;
  } catch {
    return null;
  }
}

