        subjects: {
          $push: {
            subject: '$_id.subject',
            count: '$count',
            avgRating: '$avgRating',
            totalUsage: '$totalUsage'
          }
        },
        totalCount: { $sum: '$count' },
        avgRating: { $avg: '$avgRating' },
