// æ ¹æ®ç”¨æˆ·IDè·å–å›¾è°±åˆ—è¡¨
KnowledgeGraphSchema.statics.findByUserId = function(userId: string, options: any = {}) {
  const query = { userId: new mongoose.Types.ObjectId(userId) };

  if (options.type) {
    query.type = options.type;
  }

  if (options.subject) {
    query.subject = options.subject;
  }

  if (options.isPublic !== undefined) {
    query.isPublic = options.isPublic;
  }

  return this.find(query)
    .sort({ updatedAt: -1 })
    .limit(options.limit || 50)
    .skip(options.offset || 0);
};

// æœç´¢å…¬å¼€å›¾è°±
KnowledgeGraphSchema.statics.searchPublicGraphs = function(searchOptions: any = {}) {
  const query: any = { isPublic: true };

  if (searchOptions.subject) {
    query.subject = searchOptions.subject;
  }

  if (searchOptions.gradeLevel) {
    query.gradeLevel = searchOptions.gradeLevel;
  }

  if (searchOptions.search) {
    query.$or = [
      { name: { $regex: searchOptions.search, $options: 'i' } },
      { description: { $regex: searchOptions.search, $options: 'i' } }
    ];
  }

  return this.find(query)
    .populate('userId', 'name avatar')
    .sort({ updatedAt: -1 })
    .limit(searchOptions.limit || 20)
    .skip(searchOptions.offset || 0);
};

// è·å–çƒ­é—¨å›¾è°±
KnowledgeGraphSchema.statics.getPopularGraphs = function(limit: number = 10) {
  return this.aggregate([
    { $match: { isPublic: true } },
    {
      $addFields: {
        totalWorkCount: {
          $sum: '$nodes.metadata.workCount'
        }
      }
    },
    { $sort: { totalWorkCount: -1, updatedAt: -1 } },
    { $limit: limit },
    {
      $lookup: {
        from: 'users',
        localField: 'userId',
        foreignField: '_id',
        as: 'author'
      }
    },
    { $unwind: '$author' },
    {
      $project: {
        name: 1,
        description: 1,
        subject: 1,
        gradeLevel: 1,
        nodeCount: { $size: '$nodes' },
        edgeCount: { $size: '$edges' },
        totalWorkCount: 1,
        'author.name': 1,
        'author.avatar': 1,
        createdAt: 1,
        updatedAt: 1
      }
    }
  ]);
};

