  isActive(): boolean;
  getDaysRemaining(): number;
}

/**
 * è®¢é˜…Schema
 */
const SubscriptionSchema = new Schema<SubscriptionDocument>({
  userId: {
    type: Schema.Types.ObjectId,
    ref: 'User',
    required: true,
    index: true
  },
  plan: {
    type: String,
    enum: ['free', 'pro', 'super'] as SubscriptionPlan[],
    required: true,
    default: 'free'
  },
  status: {
    type: String,
    enum: ['active', 'cancelled', 'expired', 'pending'] as SubscriptionStatus[],
    required: true,
    default: 'active'
  },
  startDate: {
    type: Date,
    required: true,
    default: Date.now
  },
  endDate: {
    type: Date,
    required: true,
    default: () => {
      // å…è´¹ç‰ˆæ°¸ä¸è¿‡æœŸï¼Œè®¾ç½®ä¸º100å¹´å
      const date = new Date();
      date.setFullYear(date.getFullYear() + 100);
      return date;
    }
  },
  autoRenew: {
    type: Boolean,
    required: true,
    default: false
  },
  paymentMethod: {
