import { logger } from '@/lib/logging/logger';

/**
 * å¥åº·æ£€æŸ¥ç»“æœæ¥å£
 */
export interface HealthCheckResult {
  name: string;
  status: 'healthy' | 'unhealthy' | 'degraded';
  message?: string;
  duration: number;
  timestamp: number;
  metadata?: Record<string, any>;
}

/**
 * ç³»ç»Ÿå¥åº·çŠ¶æ€æ¥å£
 */
export interface SystemHealth {
  status: 'healthy' | 'unhealthy' | 'degraded';
  timestamp: number;
  uptime: number;
  version: string;
  environment: string;
  checks: HealthCheckResult[];
  summary: {
    total: number;
    healthy: number;
    unhealthy: number;
    degraded: number;
  };
}

/**
 * å¥åº·æ£€æŸ¥å‡½æ•°ç±»å‹
 */
export type HealthCheckFunction = () => Promise<Omit<HealthCheckResult, 'name' | 'duration' | 'timestamp'>>;

/**
 * å¥åº·æ£€æŸ¥ç®¡ç†å™¨
 */
class HealthCheckManager {
  private checks = new Map<string, HealthCheckFunction>();
  private lastResults = new Map<string, HealthCheckResult>();
  private startTime = Date.now();

  /**
   * æ³¨å†Œå¥åº·æ£€æŸ¥
   */
  register(name: string, checkFn: HealthCheckFunction) {
    this.checks.set(name, checkFn);
  }

  /**
   * æ³¨é”€å¥åº·æ£€æŸ¥
   */
  unregister(name: string) {
    this.checks.delete(name);
    this.lastResults.delete(name);
  }

  /**
   * æ‰§è¡Œå•ä¸ªå¥åº·æ£€æŸ¥
   */
  async runCheck(name: string): Promise<HealthCheckResult> {
    const checkFn = this.checks.get(name);
    if (!checkFn) {
      throw new Error(`Health check '${name}' not found`);
    }

    const startTime = Date.now();

    try {
      const result = await Promise.race([
        checkFn(),
        this.timeout(5000) // 5ç§’è¶…æ—¶
      ]);

      const healthResult: HealthCheckResult = {
        name,
        status: result.status,
        message: result.message,
        duration: Date.now() - startTime,
        timestamp: Date.now(),
        metadata: result.metadata
      };

      this.lastResults.set(name, healthResult);
      return healthResult;
    } catch (error) {
      const healthResult: HealthCheckResult = {
        name,
        status: 'unhealthy',
        message: error instanceof Error ? error.message : 'Unknown error',
        duration: Date.now() - startTime,
        timestamp: Date.now()
      };

      this.lastResults.set(name, healthResult);
      return healthResult;
    }
  }

  /**
   * æ‰§è¡Œæ‰€æœ‰å¥åº·æ£€æŸ¥
   */
  async runAllChecks(): Promise<HealthCheckResult[]> {
    const checkNames = Array.from(this.checks.keys());
    const results = await Promise.allSettled(
      checkNames.map(name => this.runCheck(name))
    );

    return results.map((result, index) => {
      if (result.status === 'fulfilled') {
        return result.value;
      } else {
        return {
          name: checkNames[index],
          status: 'unhealthy' as const,
          message: result.reason?.message || 'Check failed',
          duration: 0,
          timestamp: Date.now()
        };
      }
    });
  }

  /**
   * è·å–ç³»ç»Ÿå¥åº·çŠ¶æ€
   */
  async getSystemHealth(): Promise<SystemHealth> {
    const checks = await this.runAllChecks();

    const summary = {
      total: checks.length,
      healthy: checks.filter(c => c.status === 'healthy').length,
      unhealthy: checks.filter(c => c.status === 'unhealthy').length,
      degraded: checks.filter(c => c.status === 'degraded').length
    };

    let overallStatus: 'healthy' | 'unhealthy' | 'degraded' = 'healthy';
    if (summary.unhealthy > 0) {
      overallStatus = 'unhealthy';
    } else if (summary.degraded > 0) {
      overallStatus = 'degraded';
    }

    return {
      status: overallStatus,
      timestamp: Date.now(),
      uptime: Date.now() - this.startTime,
      version: process.env.NEXT_PUBLIC_APP_VERSION || '1.0.0',
      environment: process.env.NODE_ENV || 'development',
      checks,
      summary
    };
  }

  /**
   * è·å–æœ€åçš„æ£€æŸ¥ç»“æœ
   */
  getLastResults(): HealthCheckResult[] {
    return Array.from(this.lastResults.values());
  }

  /**
   * è¶…æ—¶Promise
   */
  private timeout(ms: number): Promise<never> {
    return new Promise((_, reject) => {
      setTimeout(() => reject(new Error('Health check timeout')), ms);
    });
  }
}

/**
 * æ•°æ®åº“å¥åº·æ£€æŸ¥
 */
export const databaseHealthCheck: HealthCheckFunction = async () => {
  try {
    // è¿™é‡Œåº”è¯¥å®é™…è¿æ¥æ•°æ®åº“è¿›è¡Œæ£€æŸ¥
    // ä¾‹å¦‚ï¼šawait db.ping() æˆ–æ‰§è¡Œç®€å•æŸ¥è¯¢

    // æ¨¡æ‹Ÿæ•°æ®åº“æ£€æŸ¥
    const startTime = Date.now();
    await new Promise(resolve => setTimeout(resolve, Math.random() * 100));
    const responseTime = Date.now() - startTime;

    if (responseTime > 1000) {
      return {
        status: 'degraded',
        message: `Database response time is slow: ${responseTime}ms`,
        metadata: { responseTime }
      };
    }

    return {
      status: 'healthy',
      message: 'Database is responding normally',
      metadata: { responseTime }
    };
  } catch (error) {
    return {
      status: 'unhealthy',
      message: `Database connection failed: ${error instanceof Error ? error.message : 'Unknown error'}`
    };
  }
};

/**
 * Rediså¥åº·æ£€æŸ¥
 */
export const redisHealthCheck: HealthCheckFunction = async () => {
  try {
    // è¿™é‡Œåº”è¯¥å®é™…è¿æ¥Redisè¿›è¡Œæ£€æŸ¥
    // ä¾‹å¦‚ï¼šawait redis.ping()

    // æ¨¡æ‹ŸRedisæ£€æŸ¥
