/**
 * æ€§èƒ½æŒ‡æ ‡æ¥å£
 */
export interface PerformanceMetric {
  name: string;
  value: number;
  unit: string;
  timestamp: number;
  url?: string;
  component?: string;
  tags?: Record<string, string>;
}

/**
 * Web VitalsæŒ‡æ ‡
 */
export interface WebVitalsMetric {
  name: 'FCP' | 'LCP' | 'FID' | 'CLS' | 'TTFB' | 'INP';
  value: number;
  delta: number;
  id: string;
  navigationType: string;
}

/**
 * èµ„æºæ€§èƒ½æŒ‡æ ‡
 */
export interface ResourceMetric {
  name: string;
  type: string;
  size: number;
  duration: number;
  startTime: number;
  endTime: number;
  url: string;
}

/**
 * å†…å­˜ä½¿ç”¨æŒ‡æ ‡
 */
export interface MemoryMetric {
  used: number;
  total: number;
  limit: number;
  percentage: number;
}

/**
 * æ€§èƒ½ç›‘æ§ç®¡ç†å™¨
 */
class PerformanceMonitor {
  private config = getMonitoringConfig();
  private metrics: PerformanceMetric[] = [];
  private observer: PerformanceObserver | null = null;
  private memoryInterval: NodeJS.Timeout | null = null;

  /**
   * åˆå§‹åŒ–æ€§èƒ½ç›‘æ§
   */
  init() {
    if (!this.config.performance.enabled) {
      return;
    }

    if (typeof window === 'undefined') {
      return;
    }

    this.initWebVitals();
    this.initResourceMonitoring();
    this.initMemoryMonitoring();
    this.initNavigationMonitoring();
    this.initLongTaskMonitoring();
  }

  /**
   * åˆå§‹åŒ–Web Vitalsç›‘æ§
   */
  private initWebVitals() {
    // æ¨¡æ‹Ÿweb-vitalsåº“çš„åŠŸèƒ½
    // å®é™…ä½¿ç”¨æ—¶éœ€è¦å®‰è£…: npm install web-vitals

    // import { getCLS, getFID, getFCP, getLCP, getTTFB } from 'web-vitals';

    const reportWebVital = (metric: WebVitalsMetric) => {
      const performanceMetric: PerformanceMetric = {
        name: metric.name,
        value: metric.value,
        unit: metric.name === 'CLS' ? 'score' : 'ms',
        timestamp: Date.now(),
        url: window.location.href,
        tags: {
          navigationType: metric.navigationType,
          metricId: metric.id
        }
      };

      this.recordMetric(performanceMetric);
    };

    // æ¨¡æ‹ŸWeb Vitalsæ”¶é›†
    this.collectWebVitals(reportWebVital);
  }

  /**
   * æ¨¡æ‹ŸWeb Vitalsæ”¶é›†
   */
  private collectWebVitals(callback: (metric: WebVitalsMetric) => void) {
    // FCP (First Contentful Paint)
    if ('PerformanceObserver' in window) {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        entries.forEach((entry) => {
          if (entry.name === 'first-contentful-paint') {
            callback({
              name: 'FCP',
              value: entry.startTime,
              delta: entry.startTime,
              id: 'fcp-' + Date.now(),
              navigationType: 'navigate'
            });
          }
        });
      });
      observer.observe({ entryTypes: ['paint'] });
    }

    // LCP (Largest Contentful Paint)
    if ('PerformanceObserver' in window) {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        const lastEntry = entries[entries.length - 1];
        if (lastEntry) {
          callback({
            name: 'LCP',
            value: lastEntry.startTime,
            delta: lastEntry.startTime,
            id: 'lcp-' + Date.now(),
            navigationType: 'navigate'
          });
        }
      });
      observer.observe({ entryTypes: ['largest-contentful-paint'] });
    }

    // CLS (Cumulative Layout Shift)
    let clsValue = 0;
    if ('PerformanceObserver' in window) {
      const observer = new PerformanceObserver((list) => {
        const entries = list.getEntries();
        entries.forEach((entry: any) => {
          if (!entry.hadRecentInput) {
            clsValue += entry.value;
          }
        });

        callback({
          name: 'CLS',
          value: clsValue,
          delta: clsValue,
          id: 'cls-' + Date.now(),
          navigationType: 'navigate'
        });
      });
      observer.observe({ entryTypes: ['layout-shift'] });
    }
  }

  /**
   * åˆå§‹åŒ–èµ„æºç›‘æ§
   */
  private initResourceMonitoring() {
    if (!('PerformanceObserver' in window)) {
      return;
    }

    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries();
      entries.forEach((entry) => {
        if (entry.entryType === 'resource') {
          const resourceEntry = entry as PerformanceResourceTiming;
          this.recordResourceMetric(resourceEntry);
        }
      });
    });

    observer.observe({ entryTypes: ['resource'] });
    this.observer = observer;
  }

  /**
   * è®°å½•èµ„æºæ€§èƒ½æŒ‡æ ‡
   */
  private recordResourceMetric(entry: PerformanceResourceTiming) {
    const metric: ResourceMetric = {
      name: this.getResourceName(entry.name),
      type: this.getResourceType(entry.name),
      size: entry.transferSize || 0,
      duration: entry.duration,
      startTime: entry.startTime,
      endTime: entry.responseEnd,
      url: entry.name
    };

    // åªè®°å½•æ…¢èµ„æºæˆ–å¤§èµ„æº
    if (metric.duration > 1000 || metric.size > 100000) {
      const performanceMetric: PerformanceMetric = {
        name: 'resource_load',
        value: metric.duration,
        unit: 'ms',
        timestamp: Date.now(),
        url: window.location.href,
        tags: {
          resource_name: metric.name,
          resource_type: metric.type,
          resource_size: metric.size.toString()
        }
      };

      this.recordMetric(performanceMetric);
    }
  }

  /**
   * åˆå§‹åŒ–å†…å­˜ç›‘æ§
   */
  private initMemoryMonitoring() {
    if (!('memory' in performance)) {
      return;
    }

    const collectMemoryMetrics = () => {
      const memory = (performance as any).memory;
      const memoryMetric: MemoryMetric = {
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        limit: memory.jsHeapSizeLimit,
        percentage: (memory.usedJSHeapSize / memory.jsHeapSizeLimit) * 100
      };

      // åªåœ¨å†…å­˜ä½¿ç”¨ç‡è¾ƒé«˜æ—¶è®°å½•
      if (memoryMetric.percentage > 70) {
        const performanceMetric: PerformanceMetric = {
          name: 'memory_usage',
          value: memoryMetric.percentage,
          unit: 'percent',
          timestamp: Date.now(),
          url: window.location.href,
          tags: {
            used_mb: Math.round(memoryMetric.used / 1024 / 1024).toString(),
            total_mb: Math.round(memoryMetric.total / 1024 / 1024).toString()
          }
        };

        this.recordMetric(performanceMetric);
      }
    };

    // æ¯30ç§’æ£€æŸ¥ä¸€æ¬¡å†…å­˜ä½¿ç”¨æƒ…å†µ
    this.memoryInterval = setInterval(collectMemoryMetrics, 30000);
  }

  /**
   * åˆå§‹åŒ–å¯¼èˆªç›‘æ§
   */
  private initNavigationMonitoring() {
    if (!('PerformanceObserver' in window)) {
      return;
    }

    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries();
      entries.forEach((entry) => {
        if (entry.entryType === 'navigation') {
          const navEntry = entry as PerformanceNavigationTiming;
          this.recordNavigationMetrics(navEntry);
        }
      });
    });

    observer.observe({ entryTypes: ['navigation'] });
  }

  /**
   * è®°å½•å¯¼èˆªæ€§èƒ½æŒ‡æ ‡
   */
  private recordNavigationMetrics(entry: PerformanceNavigationTiming) {
    const metrics = [
      {
        name: 'dns_lookup',
        value: entry.domainLookupEnd - entry.domainLookupStart,
        unit: 'ms'
      },
      {
        name: 'tcp_connect',
        value: entry.connectEnd - entry.connectStart,
        unit: 'ms'
      },
      {
        name: 'request_response',
        value: entry.responseEnd - entry.requestStart,
        unit: 'ms'
      },
      {
        name: 'dom_processing',
        value: entry.domComplete - entry.domLoading,
        unit: 'ms'
      },
      {
        name: 'page_load',
        value: entry.loadEventEnd - entry.navigationStart,
        unit: 'ms'
      }
    ];

    metrics.forEach(metric => {
      if (metric.value > 0) {
        const performanceMetric: PerformanceMetric = {
          name: metric.name,
          value: metric.value,
          unit: metric.unit,
          timestamp: Date.now(),
          url: window.location.href,
          tags: {
            navigation_type: entry.type.toString()
          }
        };

        this.recordMetric(performanceMetric);
      }
    });
  }

  /**
   * åˆå§‹åŒ–é•¿ä»»åŠ¡ç›‘æ§
   */
  private initLongTaskMonitoring() {
    if (!('PerformanceObserver' in window)) {
      return;
    }

    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries();
      entries.forEach((entry) => {
        if (entry.entryType === 'longtask') {
          const performanceMetric: PerformanceMetric = {
            name: 'long_task',
            value: entry.duration,
            unit: 'ms',
            timestamp: Date.now(),
            url: window.location.href,
            tags: {
              task_name: entry.name
            }
          };

          this.recordMetric(performanceMetric);
        }
      });
    });

    observer.observe({ entryTypes: ['longtask'] });
  }

  /**
   * è®°å½•æ€§èƒ½æŒ‡æ ‡
   */
  recordMetric(metric: PerformanceMetric) {
    // åº”ç”¨è¿‡æ»¤å™¨
    const filterResult = performanceFilter.filterPerformance(metric);
    if (!filterResult.shouldReport) {
      return;
    }

    // æ·»åŠ è¿‡æ»¤å™¨æ ‡ç­¾
    metric.tags = { ...metric.tags, ...filterResult.tags };

    // å­˜å‚¨æŒ‡æ ‡
    this.metrics.push(metric);

    // å‘é€åˆ°Sentry
    sentry.addBreadcrumb({
      message: `Performance metric: ${metric.name}`,
      category: 'performance',
      level: 'info',
      data: {
        value: metric.value,
        unit: metric.unit,
        tags: metric.tags
      }
    });

    // å¦‚æœæ˜¯å…³é”®æ€§èƒ½é—®é¢˜ï¼Œå‘é€äº‹ä»¶
    if (this.isCriticalPerformanceIssue(metric)) {
      sentry.captureMessage(
        `Critical performance issue: ${metric.name} = ${metric.value}${metric.unit}`,
        'warning'
      );
    }

    // é™åˆ¶å†…å­˜ä¸­çš„æŒ‡æ ‡æ•°é‡
    if (this.metrics.length > 1000) {
      this.metrics = this.metrics.slice(-500);
    }
  }

  /**
   * åˆ¤æ–­æ˜¯å¦ä¸ºå…³é”®æ€§èƒ½é—®é¢˜
   */
  private isCriticalPerformanceIssue(metric: PerformanceMetric): boolean {
    switch (metric.name) {
      case 'LCP':
        return metric.value > 4000; // LCP > 4s
      case 'FID':
        return metric.value > 300; // FID > 300ms
      case 'CLS':
        return metric.value > 0.25; // CLS > 0.25
      case 'long_task':
        return metric.value > 500; // é•¿ä»»åŠ¡ > 500ms
      case 'memory_usage':
        return metric.value > 90; // å†…å­˜ä½¿ç”¨ç‡ > 90%
      default:
        return false;
    }
  }

  /**
   * è·å–èµ„æºåç§°
   */
  private getResourceName(url: string): string {
    try {
      const urlObj = new URL(url);
      const pathname = urlObj.pathname;
      const segments = pathname.split('/');
      return segments[segments.length - 1] || 'unknown';
    } catch {
      return 'unknown';
    }
  }

  /**
   * è·å–èµ„æºç±»å‹
   */
  private getResourceType(url: string): string {
    if (url.includes('.js')) return 'script';
    if (url.includes('.css')) return 'stylesheet';
    if (url.match(/\.(png|jpg|jpeg|gif|svg|webp)$/)) return 'image';
    if (url.match(/\.(woff|woff2|ttf|eot)$/)) return 'font';
    if (url.includes('/api/')) return 'api';
    return 'other';
  }

  /**
   * è·å–æ€§èƒ½æŒ‡æ ‡
   */
  getMetrics(): PerformanceMetric[] {
    return [...this.metrics];
  }

  /**
   * æ¸…é™¤æŒ‡æ ‡
   */
  clearMetrics() {
    this.metrics = [];
  }

  /**
   * é”€æ¯ç›‘æ§å™¨
   */
  destroy() {
    if (this.observer) {
      this.observer.disconnect();
      this.observer = null;
    }

    if (this.memoryInterval) {
      clearInterval(this.memoryInterval);
      this.memoryInterval = null;
    }

    this.clearMetrics();
  }
}

/**
 * æ‰‹åŠ¨è®°å½•æ€§èƒ½æŒ‡æ ‡
 */
export function recordPerformanceMetric(
  name: string,
  value: number,
  unit: string = 'ms',
  tags?: Record<string, string>
) {
  const metric: PerformanceMetric = {
    name,
    value,
    unit,
    timestamp: Date.now(),
    url: typeof window !== 'undefined' ? window.location.href : undefined,
    tags
  };

  performanceMonitor.recordMetric(metric);
}

/**
 * æ€§èƒ½è®¡æ—¶å™¨
 */
export class PerformanceTimer {
  private startTime: number;
  private name: string;
  private tags: Record<string, string>;

  constructor(name: string, tags: Record<string, string> = {}) {
    this.name = name;
    this.tags = tags;
    this.startTime = performance.now();
  }

  /**
   * ç»“æŸè®¡æ—¶å¹¶è®°å½•
   */
  end(): number {
    const duration = performance.now() - this.startTime;
    recordPerformanceMetric(this.name, duration, 'ms', this.tags);
    return duration;
  }
}

/**
 * æ€§èƒ½è£…é¥°å™¨
 */
export function withPerformanceMonitoring<T extends (...args: any[]) => any>(
  fn: T,
  name: string,
  tags?: Record<string, string>
): T {
  return ((...args: any[]) => {
    const timer = new PerformanceTimer(name, tags);

    try {
      const result = fn(...args);

      if (result instanceof Promise) {
        return result
          .then((res) => {
            timer.end();
            return res;
          })
          .catch((error) => {
            timer.end();
            throw error;
          });
      }

      timer.end();
      return result;
    } catch (error) {
      timer.end();
      throw error;
    }
  }) as T;
}

