/**
 * è®¾ç½®è¯·æ±‚ä¸Šä¸‹æ–‡
 */
export function setSentryRequestContext(req: {
  url?: string;
  method?: string;
  headers?: Record<string, string>;
  userAgent?: string;
}) {
  sentry.setContext('request', {
    url: req.url,
    method: req.method,
    headers: {
      'user-agent': req.userAgent,
      'accept-language': req.headers?.['accept-language'],
      'referer': req.headers?.['referer']
    }
  });
}

/**
 * æŠ¥å‘Šé”™è¯¯åˆ°Sentry
 */
export function reportError(error: Error, context?: {
  user?: any;
  extra?: Record<string, any>;
  tags?: Record<string, string>;
  level?: 'debug' | 'info' | 'warning' | 'error' | 'fatal';
}) {
  if (!shouldReportError(error)) {
    return;
  }

  sentry.configureScope((scope) => {
    if (context?.user) {
      scope.setUser(context.user);
    }

    if (context?.tags) {
      Object.entries(context.tags).forEach(([key, value]) => {
        scope.setTag(key, value);
      });
    }

    if (context?.extra) {
      scope.setContext('extra', context.extra);
    }
  });

  sentry.captureError(error);
}

/**
 * æ€§èƒ½ç›‘æ§è£…é¥°å™¨
 */
export function withSentryPerformance<T extends (...args: any[]) => any>(
  fn: T,
  transactionName: string,
  op: string = 'function'
): T {
  return ((...args: any[]) => {
    const transaction = sentry.startTransaction(transactionName, op);

    try {
      const result = fn(...args);

      if (result instanceof Promise) {
        return result
          .then((res) => {
            transaction?.finish();
            return res;
          })
          .catch((error) => {
            sentry.captureError(error);
            transaction?.finish();
            throw error;
          });
      }

      transaction?.finish();
      return result;
    } catch (error) {
      sentry.captureError(error instanceof Error ? error : new Error(String(error)));
      transaction?.finish();
      throw error;
    }
  }) as T;
}

export default sentry;

