import { logger } from '@/lib/logging/logger';

/**
 * é¦–å±æ€§èƒ½æŒ‡æ ‡
 */
export interface FirstPaintMetrics {
  // é¦–æ¬¡ç»˜åˆ¶æ—¶é—´
  FP: number | null;
  // é¦–æ¬¡å†…å®¹ç»˜åˆ¶æ—¶é—´
  FCP: number | null;
  // æœ€å¤§å†…å®¹ç»˜åˆ¶æ—¶é—´
  LCP: number | null;
  // DOMå†…å®¹åŠ è½½å®Œæˆæ—¶é—´
  DCL: number | null;
  // é¡µé¢å®Œå…¨åŠ è½½æ—¶é—´
  Load: number | null;
  // é¦–æ¬¡æœ‰æ„ä¹‰ç»˜åˆ¶æ—¶é—´ï¼ˆè‡ªå®šä¹‰ï¼‰
  FMP: number | null;
  // å¯äº¤äº’æ—¶é—´
