/**
 * å…³é”®èµ„æºä¿¡æ¯
 */
export interface CriticalResource {
  url: string;
  type: 'script' | 'style' | 'font' | 'image';
  size: number;
  loadTime: number;
  blocking: boolean;
  critical: boolean;
}

/**
 * é¦–å±ä¼˜åŒ–é…ç½®
 */
export interface FirstPaintConfig {
  // å…³é”®CSSå†…è”é˜ˆå€¼
  inlineCSSThreshold: number;
  // å…³é”®JSå†…è”é˜ˆå€¼
  inlineJSThreshold: number;
  // å›¾ç‰‡æ‡’åŠ è½½é˜ˆå€¼
  imageLazyThreshold: number;
  // å­—ä½“é¢„åŠ è½½
