  imageSizes?: string;
}

/**
 * é¢„åŠ è½½ç­–ç•¥
 */
export interface PreloadStrategy {
  critical: PreloadConfig[]; // å…³é”®èµ„æº
  important: PreloadConfig[]; // é‡è¦èµ„æº
  optional: PreloadConfig[]; // å¯é€‰èµ„æº
  lazy: PreloadConfig[]; // æ‡’åŠ è½½èµ„æº
}

/**
 * é¢„åŠ è½½ç»Ÿè®¡
 */
export interface PreloadStats {
  totalResources: number;
  loadedResources: number;
  failedResources: number;
  averageLoadTime: number;
