    try {
      // åˆ é™¤å¤‡ä»½æ–‡ä»¶
      const backupPath = join(this.backupDir, `${backupId}.json`);
      const compressedPath = backupPath + '.gz';

      if (existsSync(backupPath)) {
        require('fs').unlinkSync(backupPath);
