  /**
   * è‡ªåŠ¨å¤‡ä»½è°ƒåº¦
   */
  async scheduleAutoBackup(): Promise<void> {
    const now = new Date();
    const hour = now.getHours();

    // æ¯å¤©å‡Œæ™¨2ç‚¹æ‰§è¡Œå…¨é‡å¤‡ä»½
    if (hour === 2) {
      await this.createBackup({
