  // ç§æœ‰æ–¹æ³•

  private async getCollectionsToBackup(options: BackupOptions): Promise<string[]> {
    if (options.type === 'user' && options.userId) {
      return ['users', 'works', 'knowledge_graphs', 'contribution_logs'];
    }

    if (options.type === 'full') {
      const collections = await this.db.listCollections().toArray();
      return collections.map(col => col.name).filter(name => !name.startsWith('system.'));
    }

    // å¢é‡å¤‡ä»½ï¼šåªå¤‡ä»½æœ€è¿‘ä¿®æ”¹çš„æ•°æ®
    return ['works', 'contribution_logs', 'user_sessions'];
  }

  private async collectBackupData(collections: string[], options: BackupOptions): Promise<any> {
    const backupData: any = {};

    for (const collectionName of collections) {
      const collection = this.db.collection(collectionName);
      let query = {};

      // ç”¨æˆ·ç‰¹å®šå¤‡ä»½
      if (options.type === 'user' && options.userId) {
        query = { userId: options.userId };
      }

      // å¢é‡å¤‡ä»½ï¼šåªè·å–æœ€è¿‘24å°æ—¶çš„æ•°æ®
      if (options.type === 'incremental') {
        const yesterday = new Date();
        yesterday.setDate(yesterday.getDate() - 1);
        query = { ...query, updatedAt: { $gte: yesterday } };
      }

      const documents = await collection.find(query).toArray();
      backupData[collectionName] = documents;
    }

    return backupData;
  }

  private async writeBackup(path: string, data: string): Promise<void> {
    const fs = require('fs').promises;
    await fs.writeFile(path, data, 'utf8');
  }

  private async writeCompressedBackup(path: string, data: string): Promise<void> {
    const writeStream = createWriteStream(path);
    const gzipStream = createGzip();

    await pipeline(
      require('stream').Readable.from([data]),
      gzipStream,
      writeStream
    );
  }

  private async readBackup(path: string, metadata: BackupMetadata): Promise<string> {
    const compressedPath = path + '.gz';

    if (existsSync(compressedPath)) {
      return this.readCompressedBackup(compressedPath);
    } else if (existsSync(path)) {
      const fs = require('fs').promises;
      return await fs.readFile(path, 'utf8');
    } else {
      throw new Error(`Backup file not found: ${path}`);
    }
  }

  private async readCompressedBackup(path: string): Promise<string> {
    const readStream = createReadStream(path);
    const gunzipStream = createGunzip();

    let data = '';
    const chunks: Buffer[] = [];

    await pipeline(
      readStream,
      gunzipStream,
      require('stream').Writable({
        write(chunk, encoding, callback) {
          chunks.push(chunk);
          callback();
        }
      })
    );

    return Buffer.concat(chunks).toString('utf8');
  }

  private async saveBackupMetadata(metadata: BackupMetadata): Promise<void> {
    const collection = this.db.collection('backup_metadata');
    await collection.insertOne(metadata);
  }

  private async getBackupMetadata(backupId: string): Promise<BackupMetadata | null> {
    const collection = this.db.collection('backup_metadata');
    return await collection.findOne({ id: backupId });
  }

  private async restoreToDatabase(data: any, options: RestoreOptions): Promise<void> {
    const collections = options.collections || Object.keys(data);

    for (const collectionName of collections) {
      if (!data[collectionName]) continue;

      const collection = this.db.collection(collectionName);
      const documents = data[collectionName];

      if (options.overwrite) {
        // æ¸…ç©ºé›†åˆåæ’å…¥
        await collection.deleteMany({});
        if (documents.length > 0) {
          await collection.insertMany(documents);
        }
      } else {
        // é€ä¸ªæ£€æŸ¥å¹¶æ’å…¥ä¸å­˜åœ¨çš„æ–‡æ¡£
        for (const doc of documents) {
          const existing = await collection.findOne({ _id: doc._id });
          if (!existing) {
            await collection.insertOne(doc);
          }
        }
      }
    }
  }
}

/**
 * æ•°æ®åŠ å¯†å’Œè§£å¯†å·¥å…·
 * æä¾›ç”¨æˆ·æ•æ„Ÿæ•°æ®çš„å®‰å…¨å­˜å‚¨åŠŸèƒ½
 */

import crypto from 'crypto';

// åŠ å¯†é…ç½®
const ALGORITHM = 'aes-256-gcm';
const KEY_LENGTH = 32;
const IV_LENGTH = 16;
const TAG_LENGTH = 16;

// è·å–åŠ å¯†å¯†é’¥
function getEncryptionKey(): Buffer {
  const key = process.env.ENCRYPTION_KEY;
  if (!key) {
    throw new Error('ENCRYPTION_KEY environment variable is required');
  }

  // å¦‚æœå¯†é’¥é•¿åº¦ä¸è¶³ï¼Œä½¿ç”¨PBKDF2æ´¾ç”Ÿ
  if (key.length < KEY_LENGTH) {
    const salt = process.env.ENCRYPTION_SALT || 'inspi-ai-default-salt';
    return crypto.pbkdf2Sync(key, salt, 100000, KEY_LENGTH, 'sha256');
  }

  return Buffer.from(key.slice(0, KEY_LENGTH));
}

/**
 * åŠ å¯†æ•æ„Ÿæ•°æ®
 */
export function encryptSensitiveData(data: string): string {
  try {
    const key = getEncryptionKey();
    const iv = crypto.randomBytes(IV_LENGTH);
    const cipher = crypto.createCipherGCM(ALGORITHM, key, iv);

    let encrypted = cipher.update(data, 'utf8', 'hex');
    encrypted += cipher.final('hex');

    const tag = cipher.getAuthTag();

    // ç»„åˆIVã€tagå’ŒåŠ å¯†æ•°æ®
    const result = iv.toString('hex') + ':' + tag.toString('hex') + ':' + encrypted;
    return result;
  } catch (error) {
    console.error('Encryption failed:', error);
    throw new Error('Failed to encrypt sensitive data');
  }
}

/**
 * è§£å¯†æ•æ„Ÿæ•°æ®
 */
export function decryptSensitiveData(encryptedData: string): string {
  try {
    const key = getEncryptionKey();
    const parts = encryptedData.split(':');

    if (parts.length !== 3) {
      throw new Error('Invalid encrypted data format');
    }

    const iv = Buffer.from(parts[0], 'hex');
    const tag = Buffer.from(parts[1], 'hex');
    const encrypted = parts[2];

    const decipher = crypto.createDecipherGCM(ALGORITHM, key, iv);
    decipher.setAuthTag(tag);

    let decrypted = decipher.update(encrypted, 'hex', 'utf8');
    decrypted += decipher.final('utf8');

    return decrypted;
  } catch (error) {
    console.error('Decryption failed:', error);
    throw new Error('Failed to decrypt sensitive data');
  }
}

/**
 * ç”Ÿæˆå®‰å…¨çš„éšæœºå¯†ç 
 */
export function generateSecurePassword(length: number = 16): string {
  const charset = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';
  let password = '';

  for (let i = 0; i < length; i++) {
    const randomIndex = crypto.randomInt(0, charset.length);
    password += charset[randomIndex];
  }

  return password;
}

/**
 * å“ˆå¸Œå¯†ç ï¼ˆç”¨äºå­˜å‚¨ï¼‰
 */
export async function hashPassword(password: string): Promise<string> {
  const salt = crypto.randomBytes(16).toString('hex');
  const hash = crypto.pbkdf2Sync(password, salt, 100000, 64, 'sha256').toString('hex');
  return `${salt}:${hash}`;
}

/**
 * éªŒè¯å¯†ç 
 */
export async function verifyPassword(password: string, hashedPassword: string): Promise<boolean> {
  try {
    const [salt, hash] = hashedPassword.split(':');
    const verifyHash = crypto.pbkdf2Sync(password, salt, 100000, 64, 'sha256').toString('hex');
    return hash === verifyHash;
  } catch (error) {
    console.error('Password verification failed:', error);
    return false;
  }
}

/**
 * ç”Ÿæˆå®‰å…¨çš„ä»¤ç‰Œ
 */
export function generateSecureToken(length: number = 32): string {
  return crypto.randomBytes(length).toString('hex');
}

/**
 * åˆ›å»ºæ•°æ®ç­¾å
 */
export function createDataSignature(data: string): string {
  const key = getEncryptionKey();
  const hmac = crypto.createHmac('sha256', key);
  hmac.update(data);
  return hmac.digest('hex');
}

/**
 * éªŒè¯æ•°æ®ç­¾å
 */
export function verifyDataSignature(data: string, signature: string): boolean {
  try {
    const expectedSignature = createDataSignature(data);
    return crypto.timingSafeEqual(
      Buffer.from(signature, 'hex'),
      Buffer.from(expectedSignature, 'hex')
    );
  } catch (error) {
    console.error('Signature verification failed:', error);
    return false;
  }
}

/**
 * åŠ å¯†ç”¨æˆ·ä¸ªäººä¿¡æ¯
 */
export function encryptPersonalInfo(info: {
  email?: string;
  phone?: string;
  realName?: string;
  idCard?: string;
}): Record<string, string> {
  const encrypted: Record<string, string> = {};

  Object.entries(info).forEach(([key, value]) => {
    if (value && typeof value === 'string') {
      encrypted[key] = encryptSensitiveData(value);
    }
  });

  return encrypted;
}

/**
 * è§£å¯†ç”¨æˆ·ä¸ªäººä¿¡æ¯
 */
export function decryptPersonalInfo(encryptedInfo: Record<string, string>): Record<string, string> {
  const decrypted: Record<string, string> = {};

  Object.entries(encryptedInfo).forEach(([key, value]) => {
    if (value && typeof value === 'string') {
      try {
        decrypted[key] = decryptSensitiveData(value);
      } catch (error) {
        console.error(`Failed to decrypt ${key}:`, error);
        decrypted[key] = '[ENCRYPTED]';
      }
    }
  });

  return decrypted;
}

/**
 * æ•°æ®è„±æ•
 */
export function maskSensitiveData(data: string, type: 'email' | 'phone' | 'idCard' | 'name'): string {
  if (!data) return '';

  switch (type) {
    case 'email':
      const [username, domain] = data.split('@');
      if (!username || !domain) return data;
      const maskedUsername = username.length > 2 
        ? username[0] + '*'.repeat(username.length - 2) + username[username.length - 1]
        : username;
      return `${maskedUsername}@${domain}`;

    case 'phone':
      if (data.length < 7) return data;
      return data.slice(0, 3) + '****' + data.slice(-3);

    case 'idCard':
      if (data.length < 8) return data;
      return data.slice(0, 4) + '*'.repeat(data.length - 8) + data.slice(-4);

    case 'name':
      if (data.length <= 1) return data;
      if (data.length === 2) return data[0] + '*';
      return data[0] + '*'.repeat(data.length - 2) + data[data.length - 1];

    default:
      return data;
  }
}

