export function applySecurityHeaders(response: NextResponse): NextResponse {
  const headers = process.env.NODE_ENV === 'production' 
    ? SECURITY_HEADERS 
    : DEV_SECURITY_HEADERS;

  Object.entries(headers).forEach(([key, value]) => {
    response.headers.set(key, value);
  });

  return response;
}

/**
 * HTTPSé‡å®šå‘ä¸­é—´ä»¶
 */
export function enforceHTTPS(request: NextRequest): NextResponse | null {
  // åœ¨ç”Ÿäº§ç¯å¢ƒå¼ºåˆ¶HTTPS
  if (process.env.NODE_ENV === 'production') {
    const proto = request.headers.get('x-forwarded-proto');
    const host = request.headers.get('host');

    if (proto !== 'https' && host) {
      const httpsUrl = `https://${host}${request.nextUrl.pathname}${request.nextUrl.search}`;
      return NextResponse.redirect(httpsUrl, 301);
    }
  }

  return null;
}

/**
 * æ£€æŸ¥è¯·æ±‚æ¥æº
 */
export function validateOrigin(request: NextRequest): boolean {
  const origin = request.headers.get('origin');
  const referer = request.headers.get('referer');
  const host = request.headers.get('host');

  // å…è®¸çš„åŸŸååˆ—è¡¨
  const allowedOrigins = [
    process.env.NEXT_PUBLIC_APP_URL,
    `https://${host}`,
    `http://${host}`, // å¼€å‘ç¯å¢ƒHTTP
    `http://localhost:3000`, // å¼€å‘ç¯å¢ƒ
    `http://localhost:3003`, // æµ‹è¯•ç¯å¢ƒ
    `http://127.0.0.1:3000`,  // å¼€å‘ç¯å¢ƒ
    `http://127.0.0.1:3003`   // æµ‹è¯•ç¯å¢ƒ
  ].filter(Boolean);

  // æ£€æŸ¥Originå¤´
  if (origin && !allowedOrigins.includes(origin)) {
    return false;
  }

  // æ£€æŸ¥Refererå¤´
  if (referer) {
    const refererUrl = new URL(referer);
    const refererOrigin = `${refererUrl.protocol}//${refererUrl.host}`;
    if (!allowedOrigins.includes(refererOrigin)) {
      return false;
    }
  }

  return true;
}

