  /**
   * è·å–ç”¨æˆ·éšç§è®¾ç½®
   */
  async getPrivacySettings(userId: string): Promise<PrivacySettings | null> {
    try {
      const collection = this.db.collection('privacy_settings');
      return await collection.findOne({ userId });
    } catch (error) {
